// map from to, clamped
float map(float value, float min1, float max1, float min2, float max2) {
  return clamp(min2 + (value - min1) * (max2 - min2) / (max1 - min1), min2, max2);
}


// polynomial smooth min (k = 0.1);
float sminCubic( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}


// Return rotation matrix
mat3 rotate3dx(float _angle){
    float c = cos(_angle);
    float s = sin(_angle);
    return mat3(
        1, 0,  0, 
        0, c, -s,
        0, s,  c
    );
}
mat3 rotate3dy(float _angle){
	float c = cos(_angle);
    float s = sin(_angle);
    return mat3(
         c, 0, s,
         0, 1, 0,  
        -s, 0, c
    );
}
mat3 rotate3dz(float _angle){
	float c = cos(_angle);
    float s = sin(_angle);
    return mat3(
        c, -s, 0,
        s,  c, 0,
        0,  0, 1
    );
}


// Signed, negative inside object
float SDF_box(vec3 p, vec3 size, float rounding) {
	p = abs(p)-size;
    return length(max(p, 0.0)) + min(max(p.x, max(p.y, p.z)), 0.0) - rounding;
}


// Signed, negative inside object
float SDF_capsule(vec3 p, vec3 A, vec3 B, float r) {
    vec3 AB = B-A;
    vec3 AP = p-A;

    float t = dot(AB, AP) / dot(AB, AB);
    t = clamp(t, 0.0, 1.0);

    //return length(p - (A + t*AB)) - r;
    float tt = 2.0*abs(0.5 - t);	// 1 .. 0 .. 1
    tt = t;
    return length(p - (A + t*AB)) -r + r*smoothstep(1.0, 0.0, pow(tt, 0.9));

    float s1 = length(p - (A + t*AB)) - r*smoothstep(1.0, 0.0, tt);
    float s2 =  length(p - (A + t*AB)) - r*(1.0+1.0*pow((t-0.5), 2.0));
    float s = 0.5*(1.0+sin(iTime));
    s = 0.0;
    return s*s1+(1.0-s)*s2;
}


//Signed, negative inside object.
float SDF_sphere(vec3 p, vec3 c, float r) {
    return length(p - c) - r;
}


// Get the distance from point p to the closest object of all objects in the scene.
// Returns: distance, object index
// Distance can be constrained to selected object.
float GetDist(vec3 p, out int objIndex, in int objConstraint) {
    // Reusable translation variable.
    vec3 trans;

    // Sphere /////////////////////////////////////////
    //vec4 sphere = vec4(2.*sin(iTime), 0, 10.+6.*cos(iTime), 2);
    vec4 sphere;
    sphere = vec4(0, 3, 4, 2.1);
    float sdf_sphere = SDF_sphere(p, sphere.xyz, sphere.w);
    //sdf_sphere = abs(sdf_sphere) - 0.05;	// Shell
	///////////////////////////////////////////////////

    // A sphere above the light to make a lamp
    float sdf_sphereLight = SDF_sphere(p, vec3(12.*sin(iTime/4.), 13, 12.*cos(iTime/4.) + 6.), 0.8);
	sdf_sphereLight = SDF_sphere(p, vec3(0, 15, 0), 0.8);

    // Capsule ///////////////////////////////////////
	vec3 cpA, cpB;
    float cr;
    cr = 0.85;

    // Position object before rotating around world origo
	// Rotate around origo
    float xang = iTime*0.6+pi/4.0;
    float yang = iTime*0.3;
    float zang = iTime*0.3+pi/3.0;
    mat3 rot = rotate3dy(yang) * rotate3dx(xang) * rotate3dz(zang);

    float a = 1.0;
    float b = 4.0;
    vec3 cpA1 = vec3(a, 0, 0);
    vec3 cpB1 = vec3(b, 0, 0);
    vec3 cpA2 = vec3(-a, 0, 0);
    vec3 cpB2 = vec3(-b, 0, 0);
    vec3 cpA3 = vec3(0, a, 0);
    vec3 cpB3 = vec3(0, b, 0);
    vec3 cpA4 = vec3(0, -a, 0);
    vec3 cpB4 = vec3(0, -b, 0);
    vec3 cpA5 = vec3(0, 0, a);
    vec3 cpB5 = vec3(0, 0, b);
    vec3 cpA6 = vec3(0, 0, -a);		
    vec3 cpB6 = vec3(0, 0, -b);
    vec3 baseT = vec3(0, 3, 4);
    cpA1 *= rot;
    cpB1 *= rot;
    cpA2 *= rot;
    cpB2 *= rot;
    cpA3 *= rot;
    cpB3 *= rot;
    cpA4 *= rot;
    cpB4 *= rot;
    cpA5 *= rot;
    cpB5 *= rot;
    cpA6 *= rot;
    cpB6 *= rot;
    cpA1 += baseT;
    cpB1 += baseT;
    cpA2 += baseT;
    cpB2 += baseT;
    cpA3 += baseT;
    cpB3 += baseT;
    cpA4 += baseT;
    cpB4 += baseT;
    cpA5 += baseT;
    cpB5 += baseT;
    cpA6 += baseT;
    cpB6 += baseT;


    ///////////////////////////////////////////////////
    float sdf_capsule1 = SDF_capsule(p, cpA1, cpB1, cr);
    float sdf_capsule2 = SDF_capsule(p, cpA2, cpB2, cr);
    float sdf_capsule3 = SDF_capsule(p, cpA3, cpB3, cr);
    float sdf_capsule4 = SDF_capsule(p, cpA4, cpB4, cr);
    float sdf_capsule5 = SDF_capsule(p, cpA5, cpB5, cr);
    float sdf_capsule6 = SDF_capsule(p, cpA6, cpB6, cr);

    // Box ////////////////////////////////////////////
    vec3 boxSize = vec3(0.7);
    vec3 boxCenter = vec3(-6, 3., 6);
    float boxRounding = 1.5;
    float boxThickness = 0.05;
    vec3 boxPos = p-boxCenter;

    // Mirror in x. Works because position contains marching point and box sdf function
    //  is made for box at origo.
    //boxPos.x = abs(boxPos.x); 
    //boxPos.x -= 4.;				// Space mirrored out from original pos.

    float sdf_box = SDF_box(boxPos, boxSize, boxRounding);
    //sdf_box = abs(sdf_box) - boxThickness;	// Makes shell with thickness.
    ///////////////////////////////////////////////////

    // Floor plane ////////////////////////////////////
    float sdf_plane = p.y;	//  flat and at y = 0.
    sdf_plane = dot(p, normalize(vec3(0, 1, 0 )));	// Tilt plane
    sdf_plane += sin(p.x/4. +iTime/4.) /5.;
    sdf_plane += sin(p.x/2. +iTime/2.) /7.;
    sdf_plane += sin(p.x    +iTime/1.) /9.;

    sdf_plane += sin(p.z/2. +iTime)    /3.;
    sdf_plane += sin(p.z    +iTime*3.) /7.;
    ///////////////////////////////////////////////////

    // Info about hit object.
    int objIndexDist = 0;
    float objLimit = SURF_DIST;

    // Capsules + sphere
    if      (sdf_capsule1   < objLimit) objIndexDist = 1;
    else if (sdf_capsule2   < objLimit) objIndexDist = 2;
    else if (sdf_capsule3   < objLimit) objIndexDist = 3;
    else if (sdf_capsule4   < objLimit) objIndexDist = 4;
    else if (sdf_sphere     < objLimit) objIndexDist = 5;
    else if (sdf_capsule5   < objLimit) objIndexDist = 6;
    else if (sdf_capsule6   < objLimit) objIndexDist = 7;

    ///////////////////////////////////////////////////    
    // Copy all distances to a global array
    objectDistances[0] = sdf_capsule1;
    objectDistances[1] = sdf_capsule2;
    objectDistances[2] = sdf_capsule3;
    objectDistances[3] = sdf_capsule4;
    objectDistances[4] = sdf_sphere;
    objectDistances[5] = sdf_capsule5;
    objectDistances[6] = sdf_capsule6;

    // Return the distance to any object for this position p.
    // The ray marcher will march until the distance is really small.
    // Distance functions must be signed, and return negative value inside object.
    //
    // abs(object distance) - Creates shell. Distance will increase from edges.
    //
    // Using: 
    //  min(d1, d2)  - Ray will stop at closest to either one. Typical usage.
    //  max(d1, d2)  - Ray point requires both d1 and d2 small. Will get the intersection.
    //  max(-d1, d2) - Object 1 will cut out object 2.
    float d = MAX_DIST;



    // Capsules + sphere
    d = MAX_DIST;
    d = min(d, sdf_capsule1);
    d = min(d, sdf_capsule2);
    d = min(d, sdf_capsule3);
    d = min(d, sdf_capsule4);
    d = min(d, sdf_capsule5);
    d = min(d, sdf_capsule6);
    d = min(d, sdf_sphere);


    // With object constraint, only give the distance to the selected object.
    if (objConstraint > -1) {
        d = MAX_DIST;
        if      (objConstraint == 1) d = min(d, sdf_capsule1);
        else if (objConstraint == 2) d = min(d, sdf_capsule2);
        else if (objConstraint == 3) d = min(d, sdf_capsule3);
        else if (objConstraint == 4) d = min(d, sdf_capsule4);
        else if (objConstraint == 5) d = min(d, sdf_sphere);
        else if (objConstraint == 6) d = min(d, sdf_capsule5);
        else if (objConstraint == 7) d = min(d, sdf_capsule6);
    }

    objIndex = objIndexDist;
    return d;
    //return vec2(d, float(objIndexDist));
}


// Ray march / sphere trace. Walk in ray direction, but the distance of the closest object.
//  Guarantees to not oveshoot the second closest object.
// Returns: distance to first hit object, hit object index, iterations
void RayMarch(vec3 ro, vec3 rd, 
              out float dO, out int objIndex, out int iterations) {

    // Distance from origin start 0 each loop
    dO = 0.0;
    float dS;
    vec2 distResult;
    int i;
    for ( i=0; i<MAX_STEPS; i++ ) {
        // Point marches along ray direction.
        vec3 p = ro + dO*rd;

        // Check if the point is so close to a surface that it's a hit.
        // GetDist returns (distance, object index)
        dS = GetDist(p, objIndex, -1);
        //dS = distResult.x;

        // March the length of closest distance, but in ray direction.
        dO += dS; 

        // Exit loop if hit object or too long distance.
        if (abs(dS) < SURF_DIST || dO > MAX_DIST) {
        	break;
        } 
    }

    //Outputs
    //dO
    //objIndex
    iterations = i;
    return;
    //return; vec3(dO, float(objIndex), float(i));
    //return vec3(dO, float(distResult.y), float(i));	// Always return distance for when loop was exited.
    // Return nbr of iterations for glow effect. Iteratons increase for rays passing close to objects. 
}


// Approximate normal calculation. Uses GetDist to "find" the actual object.
//  Needs p on object for relevant result.
// The approximate surface of different objects is used if they are close.
// Approxmation of normal can be constrained to surface of the object found at point p.
vec3 GetNormal(vec3 p, bool useObjConstraint) {
    int objConstraint;
    int dummy;
    float d =  GetDist(p, objConstraint, -1);	// Since p is passed here, the distance to p is likely known.
	vec2 eps = vec2(0.01 ,0);

    if (!useObjConstraint) objConstraint = -1;

    // Get tangent plane
    float xd =  GetDist(p + eps.xyy, dummy, objConstraint);

    float yd = GetDist(p + eps.yxy, dummy, objConstraint);

    float zd = GetDist(p + eps.yyx, dummy, objConstraint);

    // Normal?? Detta Ã¤r vÃ¤l planet?
    // Det Ã¤r normalen. FÃ¤rglÃ¤gger man blir planet grÃ¶nt, dvs y = 1.
    return normalize( vec3(xd, yd, zd) - d );
}


// Get the material properties for the selected object.
Material GetMaterial(int objIndex) {
    Material material;

    // Material constants
    material.ambFac = 0.2;
    material.diffFac = 1.0;
    material.specFac = 1.0;
    material.shininess = 4.0;

    // Material colors
    material.ambCol = vec3(0);				// Init to 0 if no object found for debug.
    material.specCol = vec3(1, 1, 0.6);

    if (objIndex == 1) { 
        material.diffCol = vec3(1, 0, 0);
        material.specFac = 3.;
	    material.shininess = 4.;
    }
    else if (objIndex == 2) { 
        material.diffCol = vec3(0, 1, 0);
        material.specFac = 3.;
	    material.shininess = 8.;
    }
    else if (objIndex == 3) {
		material.diffCol = vec3(0, 0, 1);
        material.specFac = 3.;
	    material.shininess = 16.;
    }
    else if (objIndex == 4) {
        material.diffCol = vec3(1, 0, 1);
        material.specFac = 3.;
	    material.shininess = 32.;
    }
    else if (objIndex == 5) {	// Sphere
        material.diffCol = vec3(1, 1, 1);
        material.specFac = 3.;
	    material.shininess = 8.;
    }
    else if (objIndex == 6) {
        material.diffCol = vec3(1, 1, 0);
        material.specFac = 3.;
	    material.shininess = 32.;
    }
    else if (objIndex == 7) {
        material.diffCol = vec3(0, 1, 1);
        material.specFac = 3.;
	    material.shininess = 32.;
    }

    material.ambCol = material.diffCol;		// Make the same. 

    if (BLINN) {
    	material.shininess *= 3.5;	// For BlinnPhong compared to Phong.
    }
    return material;
}


vec3 BlinnPhong(vec3 p, int objIndex, vec3 ro) {
    vec3 v_N = GetNormal(p, true);		// Normal

    // Define light sources ///////////////////////////////
    // Point light
    lights[0].ambCol = vec3(1);
    lights[0].diffCol = vec3(1);
    lights[0].specCol = vec3(1);
    lights[0].ambFac = 0.4;
    lights[0].diffFac = 1.0;
    lights[0].specFac = 1.0;
    lights[0].power = 2500.0 / float(NBR_LIGHT);
    lights[0].falloffQuadFac = 12.56637;

    lights[0].pos = vec3(10.0, 25, -5);
    lights[0].dist = length(lights[0].pos - p);
	lights[0].powerAtt = lights[0].power/(lights[0].falloffQuadFac*pow(lights[0].dist, 2.0));


    lights[1] = lights[0];
	lights[1].pos = vec3(-8, 15, 4);
    lights[1].dist = length(lights[1].pos - p);

    lights[2] = lights[0];
	lights[2].pos = vec3(0, 15, 20);
    lights[2].dist = length(lights[2].pos - p);

    // Ambient should probably be treated outside lights. An approximated value anyway.
    for (int i = 0; i < NBR_LIGHT; i++) {
    	lights[i].ambFac /= float(NBR_LIGHT);
    }


    // Get material properties ////////////////////////////
    Material material = GetMaterial(objIndex);
    ///////////////////////////////////////////////////////


    // Calculate contribution of all light sources ////////
    // But not material colors.
    vec3 ambientSum = vec3(0);		// Combined contribution from all lights.
    vec3 diffuseSum = vec3(0);		// Combined contribution from all lights.
    vec3 specularSum = vec3(0);	// Combined contribution from all lights.
    for (int i = 0; i < NBR_LIGHT; i++) {
        Light light = lights[i];

        // Vectors ////////////////////////////////////////////
        vec3 v_L = normalize(light.pos - p);     // From point to light (depends on type of light).
        vec3 v_R = reflect(-v_L, v_N);  	// Reflected light in normal
        vec3 v_V = normalize( ro - p );		// View direction
        vec3 v_H = normalize(v_L + v_V); 	// Halfway direction - Blinn.

        float dotLN = dot(v_N, v_L);
        ///////////////////////////////////////////////////////

        // Ambient
        vec3 ambient = light.ambCol * light.ambFac * material.ambFac;

        // Diffuse 
        float diff = max(dotLN, 0.0);
        vec3 diffuse = diff * light.powerAtt * light.diffCol * light.diffFac * material.diffFac;

        // Specular
        float spec;
        spec = pow(max(dot(v_V, v_R), 0.0), material.shininess);	// Phong
        if (BLINN) {
            spec = pow(max(dot(v_N, v_H), 0.0), material.shininess);	// Blinn
        }
        vec3 specular = spec * light.powerAtt * light.specCol * light.specFac * material.specFac;


        // Additional shading. Not Blinn or Phong.
        // Add ambient occlusion where dot L N is -1, and the diffuse part is already 0.
        // This will make the core shadow darker. Sort of the same as adding diffuse light
        //  all the way round.
        // Material parameter?
        // An opposite would also be interesting - to lead the diffuse light further around,
        //  as if the material conducts light on the surface.
        float M_da = 0.7;	// Reduction of ambient light in the shadow. 1 = no reduction.
        ambient *= M_da * (1.0 + clamp(dotLN, -1.0, 0.0));


        // Add more shadows if no path to light source from point p.  //////////
        // This causes extra ray march. Approximation.
        // Go from point towards light. If hit an object first - no light path.
        //  But start a little bit above the surface of p.
        // Must shadow a point by its own object or else a strange gradient comes.
        float dShadowObject;
        int dummy_iterations;
        int shadowindex;
        const float SHADOW_FO = 2.5;	// Falloff
        RayMarch(p + 3.0*SURF_DIST*v_N, v_L, 
                 dShadowObject, shadowindex, dummy_iterations);
        bool inShade = dShadowObject < light.dist + SURF_DIST;
        bool selfShade = inShade && shadowindex == objIndex;
        if (inShade) {
            // Point is affected by shadow.
            float sf = clamp(M_da*dShadowObject/SHADOW_FO, 0.0, 1.0);
            sf = pow(sf, 2.0);
            diffuse *= sf;
            specular *= pow(sf, 1.1);
        }
        //if (selfShade) { I_ambient = vec3(1); I_diffuse = vec3(1); } // Debug
        //if (inShade && !selfShade) { I_ambient = vec3(0); I_diffuse = vec3(0); } // Debug
        ////////////////////////////////////////////////

        // Totalize contribution of this light
        ambientSum += ambient;
        diffuseSum += diffuse;
        specularSum += specular;
    }
    ///////////////////////////////////////////////////////


    return ambientSum * material.ambCol + 
           diffuseSum * material.diffCol + 
           specularSum * material.specCol;

}


// Ambient occlusion affect objects close to each other.
// It's an simplification. 
//  Don't detect the object intersect edge, but should shadow narrow angles more.
//  Improve by using normal of the other object. How to get that?
float AO(vec3 p, int objIndex) {
    // Point p is on the surface of object[objIndex], so the distance to other objects
    //  will be from this.
    // But when objects intersect the distance will be wrong - it should be only distances
    //  to visible parts. Can the GetDist / RayMarch also return p of all other distances, and
    //  not only the p of the "found" object?
    float ao, ao_sum = 0.0;			// 0: No ao at all
    const int NBR_RODS = 6;
    const int sphereIndex = 5;
    const float d_fo = 0.3;		// 0..r Fall off distance
    const float ao_max = 0.8;	// 0..1 Maximum amount of ao 

    // Make sure global distance variables are updated. XXX now it starts becoming unnecessary (costly).
    int dummy_index;
    GetDist(p, dummy_index, -1);

    // Point is on the sphere.
    if (objIndex == sphereIndex) {
        // Move the rod distances to array for iteration possibility.
        float rd[NBR_RODS];
        rd[0] = objectDistances[0];
        rd[1] = objectDistances[1];
        rd[2] = objectDistances[2];
        rd[3] = objectDistances[3];
        rd[4] = objectDistances[5];
        rd[5] = objectDistances[6];

        // Shadow depending on the distance to each rod.
        for (int i = 0; i < NBR_RODS; i++) {
            // Normalize distance between 0..1
            float d = clamp(rd[i] / d_fo, 0.0, 1.0);

            // Calculate ao based on distance function.
            ao = clamp(pow((1.0-d), 3.0), 0.0, 1.0);
            //ao = 0.3*d;

            // Add to existing ao with a reduction factor.
            ao_sum += clamp(0.75*min(ao, ao_max), 0.0, 1.0);
        }
    }
    else {
    	// Point is on a rod.
        // Normalize distance between 0..1
        float d = clamp(objectDistances[4] / d_fo, 0.0, 1.0);

        // Calculate ao based on distance function.
        ao = clamp(pow((1.0-d), 3.0), 0.0, 1.0);
        //ao = 0.3*d;

        // Add to existing ao with a reduction factor. Will matter when several nearby objects
        //  contribute to the same occlusion.
        ao_sum += clamp(0.75*min(ao, ao_max), 0.0, 1.0); 
    }

    return 1.0 - min(ao_sum, ao_max);
}


// Sky color
vec3 skyColor(vec2 uv) {
    // 077b88 = 
    // 9dc6ca = 
	vec3 col; 
    col = mix(vec3(0.615, 0.776, 0.792), vec3(0.027, 0.482, 0.533), 2.0*sqrt(abs(uv.y)));

    // Lighten middle, darken out. Multiply with > 1.
    col *= mix(vec3(2), vec3(1), 2.0*pow(abs(uv.y), 0.25));

    col = pow( clamp(col,0.0,1.0), vec3(2.2) );	// Inverse gamma bc sky color was chosen from palette.
	return col;
}

// Render the point of the ray
// returns: vec3 color, object index, object distance
vec3 Render(vec3 ro, vec3 rd, vec2 uv,
            out int objIndex, out float d) {

    // Ray march in direction for this uv-coordinate / screen pixel.
    int raySteps;
    RayMarch(ro, rd, 
             d, objIndex, raySteps);	// Returns (distance, object index, ray iterations)

    // Light and color for the intersected point.
    // Don't do color calculations if there was no hit.
    vec3 col = vec3(1);
    if (d < MAX_DIST) {
        vec3 p = ro + d*rd;
        col = BlinnPhong(p, objIndex, ro);

        // Ambient occlusion 
        col *= AO(p, objIndex);

        // Use the nbr of marched steps for glow.
        //if (objIndex > 1) {
        //    float stepF = float(raySteps)/float(MAX_STEPS); 
        //    difCol += (pow(stepF*10., 2.0)*1.);   
        //}

        // Do not shade the light ball
        //if (objIndex == 5) col = vec3(1, 1, 0);
    }
    else {
        // Background. Remove anything when marcher did not reach because of distance.
        col = skyColor(uv);
    }

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Rescale x so a circle looks like a circle, and move to new center.
    // uv.y: -0.5 .. 0.5
    // uv.x: ( -0.5 .. 0.5 ) * aspect ratio.
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    // Camera ////////////////////////////////////////////////////////////
    // Camera ray origin, ray direction, ro, rd
    // x, y view plane and z is depth.
    vec3 ro;

    // Camera ray direction - look from origin in the direction of each screen pixel.
    // XXX - Ã„r det ortogonal view genom att sÃ¤tta x, y, 1 alltid. Nej...
	vec3 rd;

    // Pixel color for what the ray hit.
    vec3 col;

    // Camera ray origin
    ro = vec3(0, 4, -8);	

    // Camera ray direction
    rd = normalize( vec3(uv.x, uv.y-0.1, 1) );
    //////////////////////////////////////////////////////////////////////

    // Cast original ray
	vec2 uvorg = uv;

    // Render what the ray hits.
    int objIndex;
    float dist;
    col = Render(ro, rd, uvorg,
          objIndex, dist);  // Color of hit point


    // Focus blur /////////////////////////////////////
    float fact;
    fact = 700.0;	// Use 700 for good AA.

    float range = 0.5;
    float fp = 4.5;
    fact =  clamp(pow(abs(dist-fp), 3.2), 20.0, 700.0);
    ///////////////////////////////////////////////////


    // Calculate distance
    //XXXvec3 p = GetDist();
    //float centerDist = p.z-ro.z;	// Orthogonal distance to object from camera.
    //if (objIndex1 == 0) centerDist = MAX_DIST;

    // Cast ray
    // How to calculate backwards from uv into a ray direction?
    // Seems better than to ray with small angular difference.

    // Anti-aliasing - sample 4 points (or perhaps only affect when point is on object edge).
    // 4 sample is just blur everywhere.
    // 

    // Procesing saving algorithm for at least few objects:
    //  Cast the ray.
    //  If ray hits an object
    //    Cast the AA rays.
    //    Optional:
    //    Blur only if one or more AA ray does not hit object. Prevents pure object internal blur.
    //
    // Rays cast: 1 per "normal cast". 5 per normal cast that hits an object.

    // And AA only on "edge between objects" but not "edges within the same object"
    //  Cast the ray
    //  If ray hits Object obj at Point p:
    //    Calculate a new point pp that is a small view angle in the normal direction of p
    //     Point pp distance from p depends on camera and perspective transform.
    //    Cast a ray towards pp
    //      If ray does not hit obj:
    //        Blur color of p with whatever color was found in direction of pp.
    //
    // Rays cast: 1 per "normal cast". 2 per normal cast that hits an object.

    // AA 2 is 4 passes. Costs.
    #define AA 2

    // Mouse top half - no AA
    // Mouse lower left - fixed blur
    // Mouse lower right - variable blur.
    bool useAA = (iMouse.y/iResolution.y < 0.5);

    // Don't cast additional AA rays unless the first ray hit an object.
    //useAA = useAA && objIndex > 0;	// XXX Have to or edge can be missed. Blur avoided by not blurring if all points was on object.

	vec3 tot = vec3(0.0);	// Summary of all render passes.
    int m, n;
    int samples = 0;
    bool allPointsOnObject = true;
    for( m=0; m<AA; m++ ) {
        for( n=0; n<AA; n++ ) {
            // How to calculate backwards from uv into a ray direction?
            // Seems better than to ray with small angular difference.
            vec2 uvorg = uv;

            // Create offset for the cast ray.
            vec2 of = vec2(m, n) - 0.5;
            of /= fact;

            // Modify camera ray direction a little for each AA pass.
            rd = normalize( rd + vec3(of.x, of.y, 0) );

            // Render what the ray hits.
            int objIndexOld = objIndex;
            float distDummy;
            vec3 col = Render(ro, rd, uvorg,
                              objIndex, distDummy);  // Color of hit point

 			// accumulate for AA
            tot += col;
            samples++;
            allPointsOnObject = allPointsOnObject && objIndexOld == objIndex;
  	    }        
    }
    if (useAA && true) {//!allPointsOnObject) {
    	col = tot / (float(samples));
    }

    //if (dist < 7.5) col*= 0.0;



    // hdr->ldr tonemap (iniqo)
    col = col*1.6/(1.0+col);
    col = col*col*(3.0-2.0*col);

    // Gamma
    col = pow( clamp(col,0.0,1.0), vec3(1.0 / 2.2) );

    // Output to screen
    fragColor = vec4( col, 1.0 );
}

