float HexDist(vec2 p)
{

    p = abs(p);

    float c = dot(p, normalize(vec2(1., 1.73)));
    c = max(c, p.x);

    return sin(c*10.+iTime);
    //return c;
}

vec4 HexCoords(vec2 uv)
{   
    vec2 r = vec2(1., 1.73);
    vec2 h = r*.5;
    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    vec2 gv;
    if(length(a)<length(b)) gv = a; else gv = b;

    vec2 id = uv-gv;
    float y = .5-HexDist(gv);
    float x = 0.;

    return vec4(x, y, id.x, id.y);
}

