void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy/iResolution.xy-0.5;
    uv *= 10.;
    uv.x *= iResolution.x/iResolution.y;

    float cellId = fract(sin(dot(floor(uv), vec2(11.989, 4.1414)))*44241.1123);

    uv = fract(uv) - 0.5;

    uv = abs(uv)/dot(uv, uv);
    uv = abs(uv)/dot(uv, uv) - cellId*cellId;
    uv = abs(uv)/dot(uv, uv);
    uv = abs(uv)/dot(uv, uv) - cellId;

    float c,
        s = fract(cellId+iTime/10.),
    	d = length(uv);

    for (float dt = 0.0; dt < 1.0; dt += 0.5) {
      float r = fract(iTime / 10. + dt+s);
      c += smoothstep(r + 0.05, r, d);
      r -=  0.07;
      c -= smoothstep(r + 0.05, r, d);
    }

    fragColor = vec4(vec3(c), 1.);
}

float f(vec2 uv)
{
    float x = uv.x;
    float l = length(uv - vec2(x, x * x + sin(iTime * 1.)));

    return l;
    return min(l, length(uv - vec2(x, sin(x + iTime * 10.))));
}

float grid(vec2 fragCoord)
{
    ivec2 coord = ivec2(fragCoord);
    coord -= ivec2(iResolution.xy) / 2;
    return float(1 - clamp(abs(coord.x) * abs(coord.y), 0, 1));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    uv *= 10.;

    float d = smoothstep(.2, .1, f(uv));

    d += grid(fragCoord);

    vec3 col = vec3(d);

    fragColor = vec4(col,1.0);
}

vec4  fC ( in vec2 fragCoord ) {
    vec3 col;
    float t = iTime*.05;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y+vec2(t+2.0);
        //uv *= 10.0;
        int c = 0;
        for(int i=0;i<9;i++)
        {
            c = i%3;
            float factor = 1.5;
            vec2 uv1 = uv;
            uv /= factor;
            uv += uv1;
            uv += (sin(uv.yx))/factor;
            uv *= factor;
            col[c] += sin(uv.x+uv.y);
        }
    return  vec4(col*100.0,1.0);
}


// Add this code to the bottom of any shader that has aliasing:
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    float A = 7.,  // Change A to define the level of anti-aliasing (1 to 16) ... higher numbers are REALLY slow!
          s = 1./A, x, y;

    for (x=-.5; x<.5; x+=s) for (y=-.5; y<.5; y+=s) fragColor += min ( fC(vec2(x,y)+fragCoord), 1.0);

	fragColor /= A*A;
}

float DENS = 10.;                         // target average density per cell

#define hash2(p)      fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123) // https://www.shadertoy.com/view/llySRh
#define lcg(p)        toFloat( p = p * 1664525u + 1013904223u )
#define toFloat(p)  ( float(p)  / float(0xffffffffu) )
#define toUint(p)     uint( (p) * float(0xffffffffu) )

// Poisson generator via Inverse transform sampling ( for small d ) https://en.wikipedia.org/wiki/Poisson_distribution#Generating_Poisson-distributed_random_variables
float Poisson(vec2 U, float d) {          // d = target average density
    float x = 0., p = exp(-d), s = p;
    uint r = toUint( hash2(U+.5).x );     // seed

    for( ; lcg(r) > s && x<50. ; ) 
        s += p *= d/++x; 

    return x;
}

void mainImage( out vec4 O,  vec2 u )
{
    vec2 R = iResolution.xy,
         M = length(iMouse.xy) < 10. ? vec2(0) : 2.*iMouse.xy/R-1.,
         S = 4.*exp2(2.*M.x) / R.yy,
         U = S * ( 2.*u - R ) - iTime,
         I = floor(U), F = fract(U), P;

 // O = vec4( 2.-R.y/8.*length(hash2(I)-F) ); return; // test: one single value per cell

    float d = 1e5, i = 0.,
           n = Poisson(I, DENS );         // number of dot per cell = Poisson law
    for( ; i < n; i++ )                   // then, generates n Uniform dots in the cell
        P = F - hash2(I+i/100.),
        d = min(d, dot(P,P) );
                         // dot size proportional if big or 1 pixel if small 
    O = vec4( max(0., 1. - min(R.y/8.,.5/S.x)* sqrt(d) ) );   // draw points 
    O.b += .2*mod(I.x+I.y,2.);            // show cells
    O = sqrt(O);                          // to sRGB
}

