vec2 rotate(vec2 v, float a, vec2 origin) {
    v = v - origin;
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return (m * v) + origin;
}

vec2 aspectCorrect( in vec2 uv )
{
   vec2 p = vec2(uv.x * (iResolution.x / iResolution.y), uv.y);
   return p;
}

// Angle between axis and coord, around center of screen
float angle( vec2 origin, vec2 axis, vec2 coord ) {
    vec2 a = normalize( axis );
    vec2 b = normalize( origin - coord );
    return acos( dot(a, b) );
}

void tunnel( out vec4 fragColor, in vec2 uv )
{
    uv = aspectCorrect(uv);
    vec2 origin = aspectCorrect(vec2(0.5));

    float anglePc = angle(origin, vec2(0.0, -1.0), uv) / radians(360.0);
    float dist = - (anglePc* 2.0) - distance(uv, origin);
    vec4 baseColour = vec4(sin(iTime), cos(iTime * 2.0), sin(iTime * 8.0), 1.0);

    fragColor = sin((iTime * 12.0) + (dist * 32.0)) * baseColour;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // read frequency info (for le bass)
    vec4 bassFFT1 = texture( iChannel0, vec2(0.05, 0.25) );
    vec4 bassFFT2 = texture( iChannel0, vec2(0.10, 0.25) );
    vec4 bassFFT3 = texture( iChannel0, vec2(0.15 , 0.25) );


    // Rotate around the center
    // uv = rotate(uv,(2.0 * /*sin*/(iTime * 0.5)) /*+ (bassFFT1.x)*/, vec2(0.0, 0.0));

	// Mirror
    uv = uv.x > 0.5 ? vec2(1.0 - uv.x, uv.y) : uv.xy;


    // Mirror again <3
    uv = uv.y > 0.5 ? vec2(uv.x, 1.0 - uv.y) : uv.xy;

    // Zoom/Repeat effect
    // uv = fract(uv + /*cos(iTime / 3.0)*/ + (bassFFT2.x * bassFFT1.x * 7.0));

    vec2 uvDir = uv - vec2(0.0);
    uv = fract((distance(uv, vec2(0.5)) + cos(iTime / 3.0) + (bassFFT2.r * bassFFT1.r)) * 2.0) * uvDir ;


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    tunnel(fragColor, uv);
}

//Implementation of Multiple Importance Sampling technique(E.Veach 1995)
//example shows MIS for direct light calculation
//Idea behind technique is that every monte-carlo sampling technique has 
//variance and this variance comes from low probability of success.
//So if you have 2 or more unbiased monte-carlo sampling techniques 
//which gives you different response on same situation you can predict 
//which of those responses contains more variance and weight them
