void initCamera( in vec3 pos, in vec3 frontDir, in vec3 upDir, in float fovV, out Camera dst ) {
	vec3 back = normalize( -frontDir );
	vec3 right = normalize( cross( upDir, back ) );
	vec3 up = cross( back, right );
    dst.rotate[0] = right;
    dst.rotate[1] = up;
    dst.rotate[2] = back;
    dst.fovV = fovV;
    dst.pos = pos;
}

Ray genRay( in Camera camera, in vec2 pixel ) {
	vec2 iPlaneSize=2.*tan(0.5*camera.fovV)*vec2(iResolution.x/iResolution.y,1.);
	vec2 ixy=(pixel/iResolution.xy - 0.5)*iPlaneSize;

    Ray ray;
    ray.origin = camera.pos;
	ray.dir = camera.rotate*normalize(vec3(ixy.x,ixy.y,-1.0));

	return ray;
}


bool raySceneIntersection( 	in Ray ray,
                          	in float distMin,
                          	out RaySurfaceHit hit ) {
    hit.obj_id = -1;
    hit.dist = 1000.0;
    hit.E = ray.dir*(-1.0);

    //check lights
    for( int i1=0; i1<LIGHT_COUNT; i1++ ){
        float dist;
        if( raySphereIntersection( ray, lights[i1], dist ) && (dist>distMin) && ( dist < hit.dist ) ) {
            hit.dist = dist;
          	vec3 hitpos = ray.origin + ray.dir*hit.dist;
    		hit.N = (hitpos - lights[i1].pos)*(1.0/lights[i1].radius);
    		hit.mtl_id = 4 + i1;
            hit.obj_id = i1;
        }
    }

    //check walls
    for( int i=0; i<WALL_COUNT; i++ ){
        float dist;
        if( rayPlaneIntersection( ray, walls[i], dist ) && (dist>distMin) && (dist < hit.dist ) ){
            hit.dist = dist;
//            hit.pos = ray.origin + ray.dir*hit.dist;
    		hit.N = walls[i].abcd.xyz;
    		hit.mtl_id = 0;
            hit.obj_id = LIGHT_COUNT + i;
        }
    }

#ifdef SHOW_PLANES
    //check planes
    for( int i=0; i<PLANE_COUNT; i++ ){
        float dist;
        if( rayPlaneIntersection( ray, planes[i], dist ) && (dist>distMin) && (dist < hit.dist ) ){
            vec3 hitPos = ray.origin + ray.dir*dist;
            if( (hitPos.z < planeZRanges[i].max_ ) && (hitPos.z > planeZRanges[i].min_) && (hitPos.x < planeHalfWidth ) && (hitPos.x > -planeHalfWidth ) ) {
                hit.dist = dist;
//                hit.pos = hitPos;
                hit.N = planes[i].abcd.xyz;
                hit.mtl_id = 1+i;
                hit.obj_id = LIGHT_COUNT + WALL_COUNT + i;
            }        
        }
    }
#endif

    return ( hit.obj_id != -1 );
}

void sampleSphericalLight( in vec3 x, in Sphere sphere, float Xi1, float Xi2, out LightSamplingRecord sampleRec ) {
#ifdef SAMPLE_LIGHT_AREA
    vec3 n = randomDirection( Xi1, Xi2 );
    vec3 p = sphere.pos + n*sphere.radius;
    float pdfA = 1.0/sphere.area;

    vec3 Wi = p - x;

    float d2 = dot(Wi,Wi);
    sampleRec.d = sqrt(d2);
    sampleRec.w = Wi/sampleRec.d; 
    float cosTheta = max( 0.0, dot(n, -sampleRec.w) );
    sampleRec.pdf = PdfAtoW( pdfA, d2, cosTheta );
#else
    vec3 w = sphere.pos - x;	//direction to light center
	float dc_2 = dot(w, w);		//squared distance to light center
    float dc = sqrt(dc_2);		//distance to light center

    if( dc_2 > sphere.radiusSq ) {
    	float sin_theta_max_2 = sphere.radiusSq / dc_2;
		float cos_theta_max = sqrt( 1.0 - clamp( sin_theta_max_2, 0.0, 1.0 ) );
    	float cos_theta = mix( cos_theta_max, 1.0, Xi1 );
        float sin_theta_2 = 1.0 - cos_theta*cos_theta;
    	float sin_theta = sqrt(sin_theta_2);
        sampleRec.w = uniformDirectionWithinCone( w, TWO_PI*Xi2, sin_theta, cos_theta );
    	sampleRec.pdf = 1.0/( TWO_PI * (1.0 - cos_theta_max) );

        //Calculate intersection distance
		//http://ompf2.com/viewtopic.php?f=3&t=1914
        sampleRec.d = dc*cos_theta - sqrt(sphere.radiusSq - dc_2*sin_theta_2);
    } else {
        sampleRec.w = randomDirection( Xi1, Xi2 );
        sampleRec.pdf = 1.0/FOUR_PI;
    	raySphereIntersection( Ray(x,sampleRec.w), sphere, sampleRec.d );
    }
#endif
}

float sphericalLightSamplingPdf( in vec3 x, in vec3 wi, float d, in vec3 n1, in Sphere sphere ) {
#ifdef SAMPLE_LIGHT_SOLIDANGLE
    float solidangle;
    vec3 w = sphere.pos - x;	//direction to light center
	float dc_2 = dot(w, w);		//squared distance to light center
    float dc = sqrt(dc_2);		//distance to light center

    if( dc_2 > sphere.radiusSq ) {
    	float sin_theta_max_2 = clamp( sphere.radiusSq / dc_2, 0.0, 1.0);
		float cos_theta_max = sqrt( 1.0 - sin_theta_max_2 );
    	solidangle = TWO_PI * (1.0 - cos_theta_max);
    } else { 
    	solidangle = FOUR_PI;
    }

    return 1.0/solidangle;
#else
    float lightPdfA = 1.0/sphere.area;
    float cosTheta1 = max( 0.0, dot( n1, -wi ) );
    return PdfAtoW( lightPdfA, d*d, cosTheta1 );
#endif
}

float lightChoosingPdf(in vec3 x, in int lightId) {
#ifdef IMPORTANCE_SAMPLE_LIGHT_SOURCE
    float cdf[LIGHT_COUNT];

    for(int i=0; i<LIGHT_COUNT; i++) {
        float len = length(lights[i].pos - x);
        cdf[i] = 1.0 / (len * len);
    }

    for(int i=1; i<LIGHT_COUNT; i++) {
        cdf[i] += cdf[i - 1];
    }

    for(int i=0; i<LIGHT_COUNT; i++) {
        cdf[i] /= cdf[LIGHT_COUNT-1];
    }

    return cdf[lightId] - (lightId==0? 0.0 : cdf[lightId-1]);
#else
   	return 1.0/float(LIGHT_COUNT);
#endif
}  	

int chooseOneLight(in vec3 x, in float Xi, out float pdf) {
#ifdef IMPORTANCE_SAMPLE_LIGHT_SOURCE
    float cdf[LIGHT_COUNT];

    for(int i=0; i<LIGHT_COUNT; i++) {
        float len = length(lights[i].pos - x);
        cdf[i] = 1.0 / (len * len);
    }

    for(int i=1; i<LIGHT_COUNT; i++) {
        cdf[i] += cdf[i - 1];
    }

    for(int i=0; i<LIGHT_COUNT; i++) {
        cdf[i] /= cdf[LIGHT_COUNT-1];
    }

    int id = 0;
    for(int i=0; i<LIGHT_COUNT; i++) {
        if(Xi < cdf[i]) {
        	id = i;
            pdf = cdf[i] - (i==0? 0.0 : cdf[i-1]);
            break;
        }
    }

    return id;
#else
   	pdf = 1.0/float(LIGHT_COUNT);
    return int(Xi*float(LIGHT_COUNT));
#endif
}

vec3 sampleBSDF( in vec3 x, in RaySurfaceHit hit, in Material mtl, in bool useMIS, vec2 xi ) {
    vec3 Lo = vec3( 0.0 );
    float bsdfSamplingPdf = 1.0/float(BSDF_SAMPLES);
    vec3 n = hit.N * vec3((dot(hit.E, hit.N) < 0.0) ? -1.0 : 1.0);

    for( int i=0; i<BSDF_SAMPLES; i++ ) {
        //Generate direction proportional to bsdf
        vec3 bsdfDir;
        float bsdfPdfW;
        float Xi1 = xi.x;//rnd();
        float Xi2 = xi.y;//rnd();
        float strataSize = 1.0 / float(BSDF_SAMPLES);
        Xi2 = strataSize * (float(i) + Xi2);
        float brdf;

        if( mtl.bsdf_ == BSDF_R_GLOSSY ) {
            bsdfDir = sampleBlinn( n, hit.E, mtl.roughness_, Xi1, Xi2, bsdfPdfW );
            brdf = evaluateBlinn( n, hit.E, bsdfDir, mtl.roughness_ );
        } else {
            bsdfDir = sampleLambertian( n, Xi1, Xi2, bsdfPdfW );
            brdf = evaluateLambertian( n, bsdfDir );
        }

        float dotNWi = dot( bsdfDir, n );

        //Continue if sampled direction is under surface
        if( (dotNWi > 0.0) && (bsdfPdfW > EPSILON) ){
            //calculate light visibility
            RaySurfaceHit newHit;
            if( raySceneIntersection( Ray( x, bsdfDir ), EPSILON, newHit ) && (newHit.obj_id < LIGHT_COUNT) ) {
                //Get hit light Info
                vec3 Li;
                Sphere lightSphere;
                getLightInfo( newHit.obj_id, lightSphere, Li );

                //Read light info
                float weight = 1.0;
				float lightPdfW;
                if ( useMIS ) {
                    lightPdfW = sphericalLightSamplingPdf( x, bsdfDir, newHit.dist, newHit.N, lightSphere );
                    lightPdfW *= lightChoosingPdf(x, newHit.obj_id);
                    weight = misWeight( bsdfPdfW, lightPdfW );
                }

                Lo += brdf*dotNWi*(Li/bsdfPdfW)*weight;
            }
        }
    }

    return Lo*bsdfSamplingPdf;
}     

vec3 sampleLight( 	in vec3 x, in RaySurfaceHit hit, in Material mtl, in bool useMIS, vec2 xi, float sl ) {
    vec3 Lo = vec3( 0.0 );	//outgoing radiance
    float lightSamplingPdf = 1.0/float(LIGHT_SAMPLES);

    for( int i=0; i<LIGHT_SAMPLES; i++ ) {
        //select light uniformly

        float Xi = xi.x;
        float strataSize = 1.0 / float(LIGHT_SAMPLES);
        Xi = strataSize * (float(i) + Xi);
        float lightPickPdf;
        int lightId = chooseOneLight(x, sl, lightPickPdf);

        //Read light info
        vec3 Li;				//incomming radiance
        Sphere lightSphere;
        getLightInfo( lightId, lightSphere, Li );

        float Xi1 = xi.x;//rnd();
        float Xi2 = xi.y;//rnd();
        LightSamplingRecord sampleRec;
        sampleSphericalLight( x, lightSphere, Xi1, Xi2, sampleRec );

        float lightPdfW = lightPickPdf*sampleRec.pdf;
        vec3 Wi = sampleRec.w;

        float dotNWi = dot(Wi,hit.N);

        if ( (dotNWi > 0.0) && (lightPdfW > EPSILON) ) {
            Ray shadowRay = Ray( x, Wi );
            RaySurfaceHit newHit;
            bool visible = true;
#ifdef SHADOWS
            visible = ( raySceneIntersection( shadowRay, EPSILON, newHit ) && EQUAL_FLT(newHit.dist,sampleRec.d,EPSILON) );
#endif
            if(visible) {
                float brdf;
    			float brdfPdfW;			//pdf of choosing Wi with 'bsdf sampling' technique

                if( mtl.bsdf_ == BSDF_R_GLOSSY ) {
                    brdf = evaluateBlinn( hit.N, hit.E, Wi, mtl.roughness_ );
                    brdfPdfW = pdfBlinn(hit.N, hit.E, Wi, mtl.roughness_ );	//sampling Pdf matches brdf
                } else {
                    brdf = evaluateLambertian( hit.N, Wi );
                    brdfPdfW = pdfLambertian( hit.N, Wi );	//sampling Pdf matches brdf
                }

                float weight = 1.0;
                if( useMIS ) {
                    weight = misWeight( lightPdfW, brdfPdfW );
                }

                Lo += ( Li * brdf * weight * dotNWi ) / lightPdfW;
            }
        }
    }

    return Lo*lightSamplingPdf;
}

vec3 Radiance( in Ray ray, vec2 xi1, vec2 xi2, float sl ) {
    RaySurfaceHit hit;
    if( raySceneIntersection( ray, 0.0, hit ) ) {
    	Material mtl = getMaterialFromLibrary( hit.mtl_id );

        vec3 f, Le;

        if( mtl.bsdf_ == BSDF_R_LIGHT ) {
            Le = mtl.color;
            f = vec3( 1.0, 1.0, 1.0 );
        } else {
            Le = vec3( 0.0 );
            f = mtl.color;
        }

        vec3 hitPos = ray.origin + ray.dir*hit.dist;

        vec3 directLight = vec3(0.0);
        directLight += sampleBSDF( hitPos, hit, mtl, true, xi2 );
        directLight += sampleLight( hitPos, hit, mtl, true, xi1, sl );
        return Le + f * directLight;
    }

    return backgroundColor;
}

vec4 bluenoise(int s, vec2 fc) {
    vec2 blue_noise_res = iChannelResolution[0].xy;
    vec2 tileRes = vec2(256.0, 256.0);
	int tilex = s % 4;
    int tiley = s / 4;

    vec2 coord = mod(fc, tileRes);

    return texture( iChannel0, (vec2(tilex, tiley) * tileRes + coord) / blue_noise_res);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    seed = /*iTime +*/ iResolution.y * fragCoord.x / iResolution.x + fragCoord.y / iResolution.y;

	float sinTime = sin(iTime*0.2);

    Camera camera;
    initScene();
    initMaterialLibrary();

    vec2 splitterPos = iMouse.xy;
    if ( splitterPos.x == 0.0 && splitterPos.y == 0.0 ) {
        splitterPos = iResolution.xy*0.5;
    }
    initSamplingTechnique((iMouse.x==0.0)?iResolution.x*0.5:fragCoord.x);

    if (samplingTechnique == SAMPLING_NONE) {
        fragColor = vec4( 1.0 );
    } else {
        Ray ray;
        vec3 accumulatedColor = vec3( 0.0 );
        for(int si=0; si<PIXEL_SAMPLES; ++si ){
            vec4 xi;
            if(samplingTechnique == SAMPLING_BLUE_NOISE) {
                xi = bluenoise(si, fragCoord);
            } else {
                xi = vec4(rnd(), rnd(), rnd(), rnd());
            }

            //stratified sampling for t
            float tprev = iTime;
            float tnext = iTime+FRAME_TIME;
            float tStrata = 1.0/float(PIXEL_SAMPLES);
            float tnorm = tStrata*(float(si)  + xi.w);
            float t = mix(tprev,tnext,tnorm);


            //for object motion blur
            updateScene(t);

            //update camera for camera motion blur
            vec3 cameraPos = vec3( 0.0, 1.0 + sin(t*0.45), 3.0 + sin(t*0.4)*3.0 );
            vec3 cameraTarget = vec3( sin(t*0.4)*0.3, 0.0, -5.0 );
            initCamera( cameraPos, cameraTarget - cameraPos, vec3( 0.0, 1.0, 0.0 ), radians(45.0), camera );

            vec2 subPixelCoord = vec2(rnd(), rnd());
            vec2 screenCoord = fragCoord.xy + subPixelCoord;
            ray = genRay( camera, screenCoord );

            accumulatedColor += Radiance( ray, xi.xy, xi.xy, xi.z );
        }

        //devide to sample count
        accumulatedColor = accumulatedColor*(1.0/float(PIXEL_SAMPLES));

        //gamma correction
        accumulatedColor = pow( accumulatedColor, vec3( 1.0 / GAMMA ) );

        fragColor = vec4( accumulatedColor,1.0 );
    }
}

#define TWO_PI 6.2832

bool ALIASED = false;

float MOON_SIZE = 0.02;
float BREATHING_ROOM = 0.01;
float R_STEP;

float ANTI_ALIAS_RADIUS;
float ARC_ANTI_ALIAS_RADIUS;

float DISPLACEMENT_FACTOR;

void setup() {
    R_STEP = (MOON_SIZE + BREATHING_ROOM)*2.;

    float rel_moon_size = 0.02 / MOON_SIZE;

    ARC_ANTI_ALIAS_RADIUS = (0.05 * rel_moon_size);
    ANTI_ALIAS_RADIUS = 1. - ARC_ANTI_ALIAS_RADIUS;

    ANTI_ALIAS_RADIUS *= MOON_SIZE;

    DISPLACEMENT_FACTOR = 0.01 / pow(rel_moon_size, 0.5);

}

vec2 findClosestMoonCenter(vec2 point) {

    float r = length(point);
    float theta = atan(point.y, point.x) + TWO_PI*float(point.y<0.);

    float new_r = (floor(r / R_STEP) + 0.5) * R_STEP;

    float THETA_STEP = TWO_PI / floor(new_r * TWO_PI / R_STEP);

    float new_theta = (floor(theta / THETA_STEP) + 0.5) * THETA_STEP;


    return vec2(cos(new_theta),sin(new_theta)) * new_r;
}



float withinMoon(vec2 point, vec2 center, float phase0) {  
    vec2 p = point - center;
    bool inside_circle = length(p) <= MOON_SIZE;

    if (!inside_circle) { return 0.0; }

    float phase = mod(phase0, 2.);

    bool color_on = false;
    float brightness = 0.;

    bool on_dark_side = (p.x <= 0.) ^^ (phase >= 1.);
    if (phase == 1.)
    {
        on_dark_side = true;
    }
    else if (phase == 0.) {
        on_dark_side = false;
    }

    bool crescent = abs(phase - 1.) <= 0.5;

    vec2 p_sub_arc = p;
    p_sub_arc.x /= (0.5 - mod(phase, 1.)) * 2.;

    bool inside_arc = length(p_sub_arc) <= MOON_SIZE;


    if (crescent && (!on_dark_side && !inside_arc)) {
        color_on = true;
	} 
	else if (!crescent && (!on_dark_side || inside_arc)) {
    	color_on = true;
	}


    if (color_on) {
        float circle_brightness = smoothstep(MOON_SIZE, ANTI_ALIAS_RADIUS, length(p));

        float arc_brightness = 1.;

        if (on_dark_side ^^ crescent) 
        	arc_brightness = smoothstep(0., ARC_ANTI_ALIAS_RADIUS, abs(length(p_sub_arc)-MOON_SIZE)/MOON_SIZE);

        brightness = min(circle_brightness, arc_brightness);

        if (ALIASED)
            brightness = 1.;
    }

    return brightness;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    setup();

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
     uv -= vec2(0.5, 0.5);
    uv.x *= iResolution.x / iResolution.y;

    vec2 closest_center = findClosestMoonCenter(uv);

    float new_theta = atan(closest_center.y, closest_center.x) + TWO_PI*float(closest_center.y<0.);
    float new_r_index = (floor(length(closest_center) / R_STEP) + 0.5);

    float phase = new_theta*2./TWO_PI + new_r_index*0.25 + iTime * 0.25;

    vec3 col = vec3(0.0);

    for (int i = -1; i < 2; i++) {
        //vec2 displacement = vec2(new_r_index * new_r_index * float(i) * 0.00005;
        vec2 displacement = float(i) * DISPLACEMENT_FACTOR * length(closest_center) * closest_center;

    	float brightness = withinMoon(uv, closest_center - displacement, phase);
        col[int(mod(3. + float(i), 3.))] += brightness;

    }
    fragColor = vec4(col, 1.0);

}

// https://www.shadertoy.com/view/llGSzw
uint hash1(uint n)
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return n;
}

float extract(uint n)
{
    return float(n & 0x7fffffffU)/float(0x7fffffff);
}

uint next(uint n)
{
    //return hash1(n); // hash != random number sequence, it will converge in less than 30 iterations

    // https://en.wikipedia.org/wiki/Linear_congruential_generator
    return n * 134775813U + 1U; // Pascal 32-bit
}

void mainImage(out vec4 C, in vec2 O)
{
    // perturb sequence
    uint seed = 0u;
    seed = hash1(seed + uint(iFrame));
    seed = hash1(seed + uint(O.x));
    seed = hash1(seed + uint(O.y));

    //for( int i=0; i<30; i++ ) seed = next(seed);

    // extract value
    float r = extract(seed);

    // advance generator
    seed = next(seed);

    // profit ...
    float g = extract(seed);
    seed = next(seed);
    float b = extract(seed);
    seed = next(seed);

    C = vec4(r, g, b, 1.0);
}

