// Visualizing marching-squares on meta-balls generated data using only the fragment shader. 
// For full explanation, check out: https://iradicator.com/2d-surface-reconstruction-marching-squares-with-meta-balls.
// Based on ideas from Zero-Wind: http://jamie-wong.com/2014/08/19/metaballs-and-marching-squares/

#define clamp01(x) clamp((x),0.0,1.0)
#define smoothstep01(x) smoothstep(0.0,1.0,(x))
#define sqr(x) ((x)*(x))
#define lerp(a,b,t) mix((a),(b),(t))

float sqrMagnitude(in vec2 v) { return dot(v,v); }
float sqrMagnitude(in vec3 v) { return dot(v,v); }
float sqrMagnitude(in vec4 v) { return dot(v,v); }

float sdSegment(in vec2 p, in vec2 a, in vec2 b)
{
  vec2 pa = p-a, ba = b-a;
  float h = clamp01(dot(pa,ba)/dot(ba,ba));
  return length(pa - ba*h);
}

float sceneWeight(in vec2 p, in float t)
{    
	float aspect = iResolution.x / iResolution.y;

    vec3[17] aSpheres;
    aSpheres[1 ] = vec3(aspect*(0.5 + 0.5*sin(2.124*t+8.164)), 0.5 + 0.5*sin(3.823*t+5.812), 2.4 / 32.0);
    aSpheres[2 ] = vec3(aspect*(0.5 + 0.5*sin(2.878*t+1.414)), 0.5 + 0.5*cos(2.816*t+0.752), 2.0 / 32.0);
    aSpheres[3 ] = vec3(aspect*(0.5 + 0.5*cos(0.818*t+4.142)), 0.5 + 0.5*sin(1.183*t+4.127), 4.1 / 32.0);
    aSpheres[4 ] = vec3(aspect*(0.5 + 0.5*sin(5.123*t+8.214)), 0.5 + 0.5*sin(6.182*t+5.612), 5.6 / 32.0);
    aSpheres[5 ] = vec3(aspect*(0.5 + 0.5*sin(5.123*t+1.814)), 0.5 + 0.5*cos(3.182*t+8.834), 0.5 / 32.0);
    aSpheres[6 ] = vec3(aspect*(0.5 + 0.5*sin(8.179*t+8.174)), 0.5 + 0.5*sin(6.182*t+3.178), 2.3 / 32.0);
    aSpheres[7 ] = vec3(aspect*(0.5 + 0.5*sin(2.812*t+7.214)), 0.5 + 0.5*sin(8.182*t+6.714), 0.3 / 32.0);
    aSpheres[8 ] = vec3(aspect*(0.5 + 0.5*cos(5.902*t+9.123)), 0.5 + 0.5*sin(2.232*t+2.873), 3.4 / 32.0);
    aSpheres[9 ] = vec3(aspect*(0.5 + 0.5*sin(3.173*t+9.124)), 0.5 + 0.5*cos(6.743*t+8.238), 1.0 / 32.0); 
    aSpheres[10] = vec3(aspect*(0.5 + 0.5*sin(8.145*t+8.145)), 0.5 + 0.5*sin(5.743*t+8.475), 4.2 / 32.0); 
    aSpheres[11] = vec3(aspect*(0.5 + 0.5*sin(1.723*t+3.134)), 0.5 + 0.5*sin(4.127*t+1.456), 1.9 / 32.0); 
    aSpheres[12] = vec3(aspect*(0.5 + 0.5*cos(5.823*t+1.824)), 0.5 + 0.5*cos(7.151*t+5.235), 3.8 / 32.0); 
    aSpheres[13] = vec3(aspect*(0.5 + 0.5*sin(5.823*t+1.235)), 0.5 + 0.5*sin(3.131*t+2.327), 4.2 / 32.0); 
    aSpheres[14] = vec3(aspect*(0.5 + 0.5*sin(9.823*t+8.153)), 0.5 + 0.5*cos(5.912*t+4.235), 2.5 / 32.0); 
    aSpheres[15] = vec3(aspect*(0.5 + 0.5*cos(1.742*t+8.193)), 0.5 + 0.5*sin(3.874*t+4.121), 1.8 / 32.0); 
    aSpheres[16] = vec3(aspect*(0.5 + 0.5*sin(9.187*t+1.792)), 0.5 + 0.5*cos(1.894*t+1.247), 3.3 / 32.0); 

    float w = 0.0;
    for (int k = 0; k < aSpheres.length(); ++k)
		w += sqr(aSpheres[k].z) / sqrMagnitude(p - aSpheres[k].xy);                
    return w;
}

float marchsquares(
    in float alpha, in float isovalue, in vec2 gridSize, 
    in vec2 uv, in vec2 uv00, in int code, 
    in float w00, in float w10, in float w11, in float w01
)
{    
  const float thick = 0.0018;

  vec2 px0 = lerp(vec2(0.0,0.0), vec2(1.0,0.0), (isovalue - w00) / (w10 - w00));
  px0 = lerp(vec2(0.5,0.0),px0,alpha);
  px0 = uv00 + px0 / vec2(gridSize);

  vec2 px1 = lerp(vec2(0.0,1.0), vec2(1.0,1.0), (isovalue - w01) / (w11 - w01));
  px1 = lerp(vec2(0.5,1.0),px1,alpha);
  px1 = uv00 + px1 / vec2(gridSize);

  vec2 p0y = lerp(vec2(0.0,0.0), vec2(0.0,1.0), (isovalue - w00) / (w01 - w00));
  p0y = lerp(vec2(0.0,0.5),p0y,alpha);
  p0y = uv00 + p0y / vec2(gridSize);

  vec2 p1y = lerp(vec2(1.0,0.0), vec2(1.0,1.0), (isovalue - w10) / (w11 - w10));
  p1y = lerp(vec2(1.0,0.5),p1y,alpha);
  p1y = uv00 + p1y / vec2(gridSize);

  switch(code)
  {
    case 0:
    {
      return 0.0;
    } break;
    case 1:
    {
      return smoothstep(thick,0.0,sdSegment(uv, p0y, px0));
    } break;
    case 2:
    {
      return smoothstep(thick,0.0,sdSegment(uv, px0, p1y));
    } break;
    case 3:
    {
      return smoothstep(thick,0.0,sdSegment(uv, p0y, p1y));
    } break;
    case 4:
    {
      return smoothstep(thick,0.0,sdSegment(uv, px1, p1y));
    } break;
    case 5:
    {
      return smoothstep(thick,0.0,sdSegment(uv, px0, p1y))
           + smoothstep(thick,0.0,sdSegment(uv, p0y, px1));
    } break;
    case 6:
    {
      return smoothstep(thick,0.0,sdSegment(uv, px0, px1));
    } break;
    case 7:
    {
      return smoothstep(thick,0.0,sdSegment(uv, p0y, px1));
    } break;
    case 8:
    {
      return smoothstep(thick,0.0,sdSegment(uv, p0y, px1));
    } break;
    case 9:
    {
      return smoothstep(thick,0.0,sdSegment(uv, px0, px1));
    } break;
    case 10:
    {
      return smoothstep(thick,0.0,sdSegment(uv, px1, p1y))
           + smoothstep(thick,0.0,sdSegment(uv, p0y, px0));
    } break;
    case 11:
    {
      return smoothstep(thick,0.0,sdSegment(uv, px1, p1y));
    } break;
    case 12:
    {
      return smoothstep(thick,0.0,sdSegment(uv, p0y, p1y));
    } break;
    case 13:
    {
      return smoothstep(thick,0.0,sdSegment(uv, px0, p1y));
    } break;
    case 14:
    {
      return smoothstep(thick,0.0,sdSegment(uv, p0y, px0));
    } break;
    case 15:
    {
      return 0.0;
    } break;
  }
}

float isosurface(
    in float isovalue, in float t, in vec2 gridSize, 
    in vec2 uv, in vec2 uv00, 
    in float w00, in float w10, in float w11, in float w01
)
{
  int code = 15;
  if (w00 < isovalue) code -= 1;
  if (w10 < isovalue) code -= 2;
  if (w01 < isovalue) code -= 8;
  if (w11 < isovalue) code -= 4;
  return marchsquares(t, isovalue, gridSize, uv, uv00, code, w00, w10, w11, w01);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
	const float kisovalue = 1.0;

    float timer = 76.0 * fract(iTime / 76.0);
    float sceneTimer = iTime / 15.6;    

    vec2 gridSize = vec2(lerp(8.0,128.0,smoothstep01(step(27.0,timer)*(timer-27.0)/(32.0-27.0))));

    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;        
    uv.x *= aspect;

    vec2 uvc  = floor(vec2(gridSize) * uv + vec2(0.5,0.5)) / vec2(gridSize);
  	vec2 uv00 = floor(vec2(gridSize) * uv + vec2(0.0,0.0)) / vec2(gridSize);
  	vec2 uv10 = floor(vec2(gridSize) * uv + vec2(1.0,0.0)) / vec2(gridSize);
  	vec2 uv01 = floor(vec2(gridSize) * uv + vec2(0.0,1.0)) / vec2(gridSize);
  	vec2 uv11 = floor(vec2(gridSize) * uv + vec2(1.0,1.0)) / vec2(gridSize); 
  	vec2 fuv  = fract(vec2(gridSize) * uv + vec2(0.5));

    float w   = sceneWeight(uv,   sceneTimer);
    float wc  = sceneWeight(uvc,  sceneTimer);
    float w00 = sceneWeight(uv00, sceneTimer);
    float w10 = sceneWeight(uv10, sceneTimer);
    float w01 = sceneWeight(uv01, sceneTimer);
    float w11 = sceneWeight(uv11, sceneTimer);

  	if (w < kisovalue) w = 0.0;
    if (wc < kisovalue) wc = 0.0;    

  	vec3 col = w * vec3(1.0);

    float showVoxels = 0.95*(smoothstep(11.0,15.0,timer)-smoothstep(40.0,42.0,timer));
    col = lerp(col, wc * vec3(1.0), showVoxels);

    col *= 1.0-smoothstep(0.05,0.0,abs(kisovalue-w));
  	col = lerp(col, 90.0*vec3(0.03,0.01,0.8), smoothstep(0.05,0.0,abs(kisovalue-w)));

    float showGrid = smoothstep(5.0,7.0,timer-length(uv)) - smoothstep(45.0,52.0,timer+0.45*length(uv));
    col *= lerp(1.0,smoothstep01(50.0*sqrMagnitude(fuv-vec2(0.5))),showGrid);
