    col = 0.06*col + vec3(0.003,0.005,0.004);

    col *= pow(clamp(0.5*(smoothstep(60.0,56.0,timer)+smoothstep(65.0,68.0,timer)),0.0005,1.0),0.92);

    float a0 = smoothstep(45.0,48.0,timer)-smoothstep(69.0,71.0,timer);
  	float a1 = smoothstep(20.0,22.0,timer)-smoothstep(73.0,76.0,timer);
    float a2 = smoothstep(51.0,54.0,timer)-smoothstep(69.0,71.0,timer);
    float a3 = smoothstep(50.0,54.0,timer)-smoothstep(70.0,72.0,timer);

    float isovalue0 = lerp(1.0,0.5,smoothstep(45.0,48.0,timer)-smoothstep(69.0,71.0,timer));
    float isovalue1 = 1.0;
    float isovalue2 = lerp(1.0,2.0,smoothstep(51.0,54.0,timer)-smoothstep(69.0,72.0,timer));
    float isovalue3 = lerp(1.0,3.0,smoothstep(50.0,54.0,timer)-smoothstep(70.0,72.0,timer));

    float edgeInterp = smoothstep(22.0,23.0,timer) - smoothstep(25.0,26.0,timer) + smoothstep(37.0,42.0,timer);
    float a = a0 * 0.05 * isosurface(isovalue0, edgeInterp, gridSize, uv, uv00, w00, w10, w11, w01)
            + a1 * 0.5  * isosurface(isovalue1, edgeInterp, gridSize, uv, uv00, w00, w10, w11, w01)
        	+ a2 * 5.0  * isosurface(isovalue2, edgeInterp, gridSize, uv, uv00, w00, w10, w11, w01)
