// This work is a modification of https://www.shadertoy.com/view/Mt3GWs by huwb

// by sending rays through bevelled cube faces. 
// 4 normals are considered to cover all directions: 1 for axes, 3 for planes between axes

//The noise,mapping logic and ray marching (except for choosing "t") are taken as-is without any modifications

#define SAMPLE_COUNT 40
#define PERIOD 1.
#define M_PI 3.141592657

// mouse toggle
bool STRUCTURED;

// cam moving in a straight line
vec3 sundir;

// LUT based 3d value noise
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
    vec2 rg = textureLod( iChannel0, (uv+ 0.5)/256.0, 0.0 ).yx;
    return mix( rg.x, rg.y, f.z );
}


vec4 map( in vec3 p )
{
	float d = 0.1 + .8 * sin(0.6*p.z)*sin(0.5*p.x) - p.y;

    vec3 q = p;
    float f;

    f  = 0.5000*noise( q ); q = q*2.02;
    f += 0.2500*noise( q ); q = q*2.03;
    f += 0.1250*noise( q ); q = q*2.01;
    f += 0.0625*noise( q );
    d += 2.75 * f;

    d = clamp( d, 0.0, 1.0 );

    vec4 res = vec4( d );

    vec3 col = 1.15 * vec3(1.0,0.95,0.8);
    col += vec3(1.,0.,0.) * exp2(res.x*10.-10.);
    res.xyz = mix( col, vec3(0.7,0.7,0.7), res.x );

    return res;
}

