// https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
    p += vec2(523.124, 244.155);
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float sDistToArc(vec2 uv, vec2 A, vec2 B, float d){ 
    vec2 v = B - A;
    vec2 n = normalize(vec2(-v.y, v.x));
    vec2 c = A + v / 2.;
    float l = length(v) / 2.;
    vec2 p;
    p.x = dot(normalize(v), uv - c);
    if (abs(d) < 1e-6){
        return abs(p.x) > l ?
            1e9
            : dot(n, uv - c);
    }
    float h = (l * l) / 2. / d - d / 2.;
    p.y = dot(n, uv - c) + h;
    return (d * p.y < 0. || abs(p.x) > l) ?
        1e9
        : (length(p) - abs(h + d)) * sign(d);
}

float getD(vec2 id){
    float d = 1./sqrt(3.) - .5,
          k = 1.2;

#define get(op)  floor( hash12( vec2( hash12(id), op(iTime*k) ) ) * 2.)

    return d * ( mix( get(floor), get(ceil), fract(iTime*k) ) * 2. - 1. );
}

float getDist(vec2 lv, vec2 A, vec2 B, vec2 id){
    float s = sign((A - B).x + (A - B).y);
    return sDistToArc(lv, A, B, getD(id + (A + B) / 2.) * s);
}

vec3 TriCoord(vec2 uv){
    vec2 id;
    const float sqrt3 = sqrt(3.);
    vec2 v = normalize(vec2(3., sqrt3));
    id.y = floor(uv.y / sqrt3 * 2.) * sqrt3 / 2. + sqrt3 / 4.;
    id.x = floor(dot(uv, v) / sqrt3 * 2.) - id.y / sqrt3 + 0.5;
    float h = sqrt3 / 6.;
    vec2 lv = uv - id;
    float d = sqrt(0.25 + h * h) - 0.5;
    vec2 LC = vec2(-0.25, h / 2.);
    vec2 RC = vec2(0.25, -h / 2.);
    vec2 LLC = vec2(-0.75, -h / 2.);
    vec2 RDC = vec2(0.25, -h / 2. - h * 2.);
    vec2 RRC = vec2(0.75, h / 2.);
    vec2 UC = vec2(-0.25, h / 2. + h * 2.);
    float dd = abs(getDist(lv, RC, LC, id));
    dd = min(dd, abs(getDist(lv, LC, LLC, id)));
    dd = min(dd, abs(getDist(lv, RC, RDC, id)));
    dd = min(dd, abs(getDist(lv, RRC, RC, id)));
    dd = min(dd, abs(getDist(lv, UC, LC, id)));
    dd *= 40.;
    dd = min(dd, 1.);
    if (getDist(lv, LC, RC, id) < 0. || getDist(lv, LLC, LC, id) < 0. || getDist(lv, RC, RDC, id) < 0.){
        return cols[int(mod(id.x + mod(id.y, 2.), 3.))] * dd;
    }
    if (getDist(lv, RC, LC, id) < 0. || getDist(lv, RRC, RC, id) < 0. || getDist(lv, LC, UC, id) < 0.){
        return cols[int(mod(id.x + mod(id.y, 2.) + 2., 3.))] * dd;
    }
    return cols[int(mod(id.x + mod(id.y, 2.) + 1., 3.))] * dd;
    return vec3(dd * 20.);
}

void mainImage( out vec4 ans, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    ans.rgb = TriCoord(uv * 5.);
}

