// inspired by : https://www.shadertoy.com/view/4dfSDj
// source: https://www.shadertoy.com/view/ttc3zr
uint murmurHash11(uint src) {
    const uint M = 0x5bd1e995u;
    uint h = 1190494759u;
    src *= M; src ^= src>>24u; src *= M;
    h *= M; h ^= src;
    h ^= h>>13u; h *= M; h ^= h>>15u;
    return h;
}
float hash11(float src) { return uintBitsToFloat(murmurHash11(floatBitsToUint(src)) & 0x007fffffu | 0x3f800000u) - 1.0; }

uint murmurHash12(uvec2 src) {
    const uint M = 0x5bd1e995u;
    uint h = 1190494759u;
    src *= M; src ^= src>>24u; src *= M;
    h *= M; h ^= src.x; h *= M; h ^= src.y;
    h ^= h>>13u; h *= M; h ^= h>>15u;
    return h;
}
float hash12(vec2 src) { return uintBitsToFloat(murmurHash12(floatBitsToUint(src)) & 0x007fffffu | 0x3f800000u) - 1.0; }

uvec2 murmurHash21(uint src) {
    const uint M = 0x5bd1e995u;
    uvec2 h = uvec2(1190494759u, 2147483647u);
    src *= M; src ^= src>>24u; src *= M;
    h *= M; h ^= src;
    h ^= h>>13u; h *= M; h ^= h>>15u;
    return h;
}
vec2 hash21(float src) { return uintBitsToFloat(murmurHash21(floatBitsToUint(src)) & 0x007fffffu | 0x3f800000u) - 1.0; }

#define rot2(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float band (vec2 id, float threshold) {
    return step(hash12(id), threshold);
}

float randomLines(vec2 uv, float seed) {
	// dilated uv on y axis
    vec2 duv = uv;
    duv.x *= 0.8*(1.+.5*(1.+mod(iTime/20., 0.3)*cos(iTime/10.*seed)));
    duv.y *= 0.02*(1.+mod(floor(iTime*0.1), seed+.1)/(seed+.1)); 
    return max(band(floor(duv*100.), .25), band(floor((duv +0.01*mod(iTime/10., seed+.1)/(seed+.1)*cos(iTime/2.) )*80.), .25));
}

float rottingSquareMask(vec2 uv, float seed, float radius) {
// rotating squares
    float a = clamp(cos((iTime/1.) * (.5 + 0.1*hash11(seed))), -.1, .1) / .1 + clamp(cos(seed + (iTime/2.) * .6), -.15, .15) / .12;
    vec2 center = vec2(cos(seed + mod(iTime/12., hash11(seed))/0.5), sin(mod(iTime/10., hash11(seed))/hash11(seed))) /2.0;
    vec2 suv = uv * rot2(a) + center;
    return step(abs(suv.x) + abs(suv.y), radius);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)  / iResolution.y;
    vec2 Ouv = uv; // keep original UV
    // adding noise to uv to generate motion
    float uvNoise = 0.05*((1.+cos(iTime))/20. + mod(iTime/10., 0.2)*cos(1.*iTime)*0.4);
    uv += uvNoise;

    float color = 1.; // color in b&w 

    // basic bg lines
    color *= randomLines(uv, 1.);

    // horizontal band
    float hm = step(0.3, abs(uv.y));
    color = mix(color, randomLines(uv, 2.), hm);

    //  cubes 
    for(float i; i < 8.; ++i) {
    	float cubemask = rottingSquareMask(uv, i*10., 0.45 + 0.3*hash11(i*10.0));
    	//color *= cubemask;
    	color = mix(color, randomLines(uv, i), cubemask);
    }

	// horizontal band
	hm = step(0.3, abs(uv.y + mod(iTime/10., 0.2)*cos(1.*iTime)*0.5));
    color = mix(color, randomLines(uv, 5.), hm);

    // periodic inversion
    color = mix(color, (1.-color), step(.08, iTime / 60.) * round(cos(iTime) *.5+.5));

    // white border
    vec2 w = step(abs(Ouv), vec2(.42));
    float borderMask = w.x*w.y;
    color = mix(1., color, borderMask);

    // square mask
    vec2 s = step(abs(Ouv), vec2(.45));
    float squareMask = s.x*s.y;

    vec3 yColor = vec3(1., 1., 0.95); // yellowish background color
    vec3 finalColor = mix(vec3(.92), yColor * color, squareMask);


    fragColor = vec4(finalColor, 1);
}

