// #define FREE_CAM

// ----- Render options ----- //
#define AA_SAMPLES 1 // antialisaing

// ----- Ray marching options ----- //
// #define LOW_QUALITY // if your computer isn't powerful enough
#ifdef LOW_QUALITY
    #define MAX_STEPS 25
#else
    #define MAX_STEPS 150
#endif
#define MAX_DIST 50.
#define SURF_DIST 0.0001
#define NORMAL_DELTA 0.0001

// ----- Post process ----- //
#define VIGNETTAGE_STRENGHT 0.8

// ----- Useful constants ----- //
#define PI  3.14159265358979323846264338327
#define TAU 6.28318530717958647692528676655

// ----- useful functions ----- //
float maxComp(vec2 v) { return max(v.x , v.y); }
float maxComp(vec3 v) { return max(maxComp(v.xy), v.z); }
float dot2(vec2 v) { return dot(v,v); }
float cro(in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }
float map(float t, float c, float d) {  return c + t * (d - c);} // considering that t is in [0-1]

vec2 matMin(vec2 a, vec2 b) { return (a.x < b.x) ? a : b; }
vec3 saturate(vec3 a) { return clamp(a, 0.0, 1.0); }
vec2 saturate(vec2 a) { return clamp(a, 0.0, 1.0); }
float saturate(float a) { return clamp(a, 0.0, 1.0); }

// ----- easingFunct -----//
float quadin(float t) { return t*t;}
float quadOut(float t) { return -t*(t-2.0);}
float cubicIn(float t) { return t*t*t;}
float cubicOut(float t) { return -t*t*t+1.0;}
float circleOut(float t) { return pow(1.0-(1.0-t)*(1.0-t), 0.5); }
float circleIn(float t) { return 1.0- pow(1.0-t*t, 0.5); }

// ----- distance functions modifiers ----- //
// source: https://www.iquilezles.org/www/articles/smin/smin.htm

float smin( float a, float b, float k ) {
    float h = max(k-abs(a-b), 0.0)/k;
    return min(a, b) - h*h*0.25;
}

float polysmin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// ----- distance functions for 3D primitives ----- //
// source: http://iquilezles.org/www/articles/distfunctions/distfunctions.htm

float cylSDF(vec2 p, float radius) { return length(p) - radius; }

float boxSDF(vec3 p, vec3 boxDim) {
    vec3 q = abs(p) - boxDim;
    return length(max(q, 0.0)) + min(maxComp(q), 0.0);
}

// ---- Hash functions ---- //
float v31(vec3 a) { return a.x + a.y * 37.0 + a.z * 521.0; }
float v21(vec2 a) { return a.x + a.y * 37.0; }
float Hash21(vec2 uv) {return fract(sin(v21(uv))*104003.9);}

vec2 glow = vec2(0, 0);


vec2 sceneSDF(vec3 p) {

    float freq = 5.0;
	float theta;
	float snakeRadius = 0.8;
	float distoRadius = 0.15;
	float thickness = 0.02;

    float angle = atan(p.z, p.x);

    float amp = cubicIn(sin(angle + iTime/2.0) * 0.5 + 0.5);

	vec2 cylWarp = vec2(cylSDF(p.xz, snakeRadius), p.y + cos((angle+iTime)*3.0)*0.1);
	// cylWarp = vec2(cylSDF(p.xz, 1.0), cos(p.y)/15);

    theta = angle*freq;
    float wave1 = boxSDF(vec3(cylWarp + vec2(sin(theta), cos(theta)) * distoRadius * amp, 0.0), vec3(thickness));

    theta = angle*freq + TAU / 3.0;
    float wave2 = boxSDF(vec3(cylWarp + vec2(sin(theta), cos(theta)) * distoRadius * amp, 0.0), vec3(thickness));

	theta = angle*freq - TAU / 3.0;
    float wave3 = boxSDF(vec3(cylWarp + vec2(sin(theta), cos(theta)) * distoRadius * amp, 0.0), vec3(thickness));

	float snake = polysmin(wave1, wave2, 0.1);
	snake = polysmin(snake, wave3, 0.1);

	float centerGlow = boxSDF(vec3(cylWarp, 0), vec3(thickness*2.0, vec2(thickness*0.5)));
    vec2 snakeMat = matMin(vec2(snake, 1.0), vec2(centerGlow, -1.0));

    float innerRadius = 0.5;
    float center = cylSDF(vec2(cylSDF(p.xz, innerRadius), p.y), 0.01);
    float nb = 5.0;
   	for(float i = 0.0; i < nb; ++i) {
   		center = min(center, cylSDF(vec2(cylSDF(p.xz, circleOut((nb-i-0.8f)/nb)*innerRadius), p.y+i/nb*innerRadius), 0.01));
   		center = min(center, cylSDF(vec2(cylSDF(p.xz, circleOut((nb-i-0.8f)/nb)*innerRadius), p.y-i/nb*innerRadius), 0.01));
   	}
    // center = max(center, abs(fract(cylWarp.z*freq-2.0*iTime)-0.5)-0.4);

    const float fallOffStrenght = 0.5; 
    glow.y += fallOffStrenght / (center * center + fallOffStrenght);

    return matMin(snakeMat, vec2(center, -1.0));
}


// source: https://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 getNormal(vec3 p) {
    const float h = NORMAL_DELTA;
	const vec2 k = vec2(1., -1.);
    return normalize( k.xyy * sceneSDF( p + k.xyy*h ).x + 
                      k.yyx * sceneSDF( p + k.yyx*h ).x + 
                      k.yxy * sceneSDF( p + k.yxy*h ).x + 
                      k.xxx * sceneSDF( p + k.xxx*h ).x );
}

// return dist, mat, marchingCount
vec3 rayMarching(vec3 O, vec3 D) { // ray origin and dir
    vec2 sceneHit;
    float d = 0.0;
    float marchingCount = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
    	vec3 newPos = O + D * d;
    	sceneHit = sceneSDF(newPos);
        float distToScene = sceneHit.x;

        d += distToScene * 0.7; // precision handling
        // If we are very close to the object, consider it as a hit and exit this loop
        if( d > MAX_DIST || abs(distToScene) < SURF_DIST*0.99) break;
        ++marchingCount;

        // glow
        const float fallOffStrenght = 2.0; 
        glow.x += fallOffStrenght / (distToScene * distToScene + fallOffStrenght); // with gaussian fallOff
    }
    return vec3(d, sceneHit.y, marchingCount);
}

vec3 render(vec3 O, vec3 D) { // ray origin and dir

	vec3 finalColor = vec3(2,43,58)/255.0; // background

	glow = vec2(0, 0); // reset glow
    vec3 marching = rayMarching(O, D);

    // if we hit an object

    if( marching.x <= MAX_DIST) {
		// material handling
        if (marching.y < 0.0) {
        	finalColor = vec3(255,166,43)/255.0;
        }else {
        	finalColor = vec3(130,192,204)/255.0;
        }
    }

    // Glow if we're close to the part of the ring with the braid.
    float cyc = (-sin(marching.y * TAU))*0.5+0.7;

    // glow

    finalColor +=  vec3(255,166,43)/255.0 * cyc * glow.x * 0.01;
    // glow if we are close to the center of our cyl
    finalColor += vec3(35,100,170)/255.0 * glow.y * 0.01 ;

    // finalColor *= vec3(0.2); // mate

	// return finalColor;
	return vec3(saturate(finalColor));
}

vec3 computeCamDir(vec2 uv, vec3 camPos, vec3 camUp, vec3 lookAtPos) {
	vec3 camVec = normalize(lookAtPos - camPos);
	vec3 sideNorm = normalize(cross(camUp, camVec));
	vec3 upNorm = cross(camVec, sideNorm);
	vec3 worldFacing = (camPos + camVec);
	vec3 worldPix = worldFacing + uv.x * sideNorm + uv.y * upNorm;
	return normalize(worldPix - camPos);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = (fragCoord-.5 * iResolution.xy)/iResolution.y * 2.0; // get centered & normalized [-1.0, 1.0]
    uv /= 2.7; // zoom

    // setup camera
    vec3 camPos;
    vec3 camUp = vec3(0, 1, 0);
#ifdef FREE_CAM
    const float mx = iMouse.x/iResolution.x*PI*2.0;
	const float my = -iMouse.y/iResolution.y*10.0;
	camPos = vec3(cos(my)*cos(mx),sin(my),cos(my)*sin(mx))*3.0;
#else
    camPos = vec3(0.0);
    camPos.y = sin(iTime*0.4)*3.0;
    camPos.z = cos(iTime*0.4)*3.0;
    camUp.y = camPos.z;
    camUp.z = -camPos.y;
    camUp = normalize(camUp);
#endif
	vec3 camDir = computeCamDir(uv, camPos, camUp, vec3(0));
    vec3 finalColor = vec3(0.0);

#if AA_SAMPLES > 1
    for (float i = 0.0; i < AA_SAMPLES; i++) {
    	for (float j = 0.0; j < AA_SAMPLES; j++) {
	        vec2 deltaUV = (vec2(i, j) / float(AA_SAMPLES) *2.0 - 1.0) / iResolution.y;
	        uv += deltaUV;
#endif
	    	finalColor += render(camPos, camDir);
#if AA_SAMPLES > 1
    	}
    }
    finalColor /= float(AA_SAMPLES * AA_SAMPLES); // Average samples
#endif

    // gamma corection
    // finalColor = pow( finalColor, vec3( 1 / 2.2));

    // color grading
    finalColor *= vec3(1.0 ,1.05, 0.95);

    // vignetting 
#ifdef VIGNETTAGE_STRENGHT
    finalColor *= clamp(1.0 - length(uv * VIGNETTAGE_STRENGHT), 0.0, 1.0); //  saturate(1.0 - length(uv * VIGNETTAGE_STRENGHT));
#endif

    fragColor = vec4(sqrt(clamp(finalColor, 0.0, 1.0)),1.0);
}

//this is based on https://www.shadertoy.com/view/4tcGDr

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

const int MAX_MARCHING_STEPS = 1000;
const float MIN_DIST = 20.0;
const float MAX_DIST = 1000.0;
const float EPSILON = 0.1;

const float size = 1000.0;


const float scale = EPSILON*.1; //to prevent rendering artifacts

/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */

float heightmap(vec2 uv)
{
    	uv /= 25.0;
        float factor = 1.5;
    	float col = 0.0;
        for(int i=0;i<6;i++)
        {
            uv *= -factor*factor;
            uv += sin(uv.yx/factor)/factor;
            col += (sin(uv.x-uv.y+col)+cos(uv.y-uv.x+col));
        }
    return -col/2.0;
}

float sceneSDF(vec3 p) {
    //float result = sceneSDF1(p/1000.0+sceneSDF1(p/1000.0));
    p.y *= 1.0;
    return (-p.y+20.0-heightmap(p.xz))*scale/2.0;
}

vec3 surface_color(vec3 p)
{
    vec3 p1 = p;
    p /= scale*500.0;
    return vec3(heightmap(p.xz*100.0)+1.0)/20.0 + vec3(.6-p.y,.5-p1.y/10.0,.2+p1.y);
}


/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    lightPos = eye;
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0,
                          2.0,
                          4.0);
    vec3 light1Intensity = vec3(0.8);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);   
    return color;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    viewDir.yz *= rot(3.14*0.666);
    float speed = 2.0;
    float t1 = speed*iTime+5.0;
	vec3 eye = vec3(-t1,-10,-t1*2.0);
    eye.y -= 20.0;

    mat3 viewToWorld = -viewMatrix(eye, eye+vec3(0,5,1), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = viewToWorld * viewDir;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    // Use the surface normal as the ambient color of the material
    vec3 K_a = surface_color((p));
    vec3 K_d = K_a;
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 100.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

// Created by eimink
// Based on https://www.shadertoy.com/view/MdfGRX by inigo quilez - iq/2013
