float hash12(vec2 p){
	return fract(sin(dot(p, vec2(3.9898, 78.233))) * 803758.5453);
}

float noiseMod( in vec3 x )
{
    vec3 p = floor(x);
	return hash12(p.xy);
}

vec4 map( vec3 p )
{
	float den = 0.3 - p.y;

    // invert space	
	p = -9.0*p/dot(p,p);

    // twist space	
	float co = cos(den - 0.45*iTime);
	float si = sin(den - 1.25*iTime);
	p.xz = mat2(si,-co,-si,co)*p.xz;

    // smoke	
	float f;
	vec3 q = p                          - vec3(0.0,1.0,0.0)*iTime;;
    f  = 0.40000*noiseMod( q ); q = q*3.02 - vec3(0.0,1.0,1.0)*iTime;
    f += 0.25000*noiseMod( q ); q = q*2.03 - vec3(1.0,1.0,1.0)*iTime;
    f += 0.12500*noiseMod( q ); q = q*2.01 - vec3(1.0,1.0,0.0)*iTime;
    f += 0.06250*noiseMod( q ); q = q*1.02 - vec3(0.0,1.0,1.0)*iTime;
    f += 0.03125*noiseMod( q );

	den = clamp( den +4.0*f, 0.0, 3.0+2.0*sin(iTime) );

	vec3 col = mix( vec3(1.0,0.9,0.8), vec3(0.4,0.15,0.1), den );

	return vec4( col, den );
}

vec3 raymarch( in vec3 ro, in vec3 rd, in vec2 pixel )
{
	vec4 sum = vec4( 0.0 );

	float t = 0.0;

    // dithering	
	t += 0.07*hash12(pixel);

	for( int i=0; i<100; i++ )
	{
		if( sum.a > 0.99 ) break;

		vec3 pos = ro + t*rd;
		vec4 col = map( pos );

		col.xyz *= mix( 3.1*vec3(0.2,0.5,1.05), vec3(0.48,0.53,1.5), clamp( (pos.y-0.2)/2.0, 0.0, 1.0 ) );

		col.a *= 0.3;
		col.rgb *= col.a;

		sum = sum + col*(1.0 - sum.a);	

		t += 0.05;
	}

	return clamp( sum.xyz, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 1.7*q;
    p.x *= iResolution.x/ iResolution.y;

    // camera
    vec3 ro = 3.0*normalize(vec3(0.2,.4 - 1.0*(-.1), 0.3));
	vec3 ta = vec3(sin(iTime), cos(iTime), 0.1);
	float cr = 0.3 * hash12(ro.xy);		

	// build ray
    vec3 ww = normalize( ta - ro);
    vec3 uu = normalize(cross( vec3(cr,cr,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

    // raymarch	
	vec3 col = raymarch( ro, rd, fragCoord );

	// contrast and vignetting	
	col = col*0.5 + 0.5*col*col*(3.0-2.0*col);
	col *= 0.25 + 0.75*pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.1 );

    fragColor = vec4( col, 1.0 );
}

