surface sdBozeGate(vec3 p, vec3 sc, float ms)
{    
    surface result = SURF_NOHIT(1e5);

    float minsc = min(sc.x, min(sc.y, sc.z));
    p /= sc;

    // head
	float d = sdCapsule(p, vec3(0,0.05,0), vec3(0, 0.11, 0), 0.125);

    float d1 = sdRoundedCylinder(p + vec3(0,0.025,0), 0.095, 0.05, 0.0);

    d = smin(d, d1, 0.1);

    vec3 mxp = vec3(-abs(p.x), p.yz);

    // ear
    float d2 = sdEar(mxp);
    d = opU(d, d2);

	surface head = SURF_FACE(d);

	// eye
    float d4 = sdCapsule(mxp, vec3(-EYE_SPACE, 0.06, 0.13), vec3(-EYE_SPACE, 0.08, 0.125), 0.0175);
    surface eye = SURF_BLACK(d4);

    // mouse
    float d6 = sdMouseTunnel(p, ms);
    surface mouse = SURF_MOUSE(d6);

    // cheep
    float d7 = sdCheep(mxp);
    surface cheep = SURF_CHEEP(d7);

    // eyebrows
    float d9 = sdEyeBrow(mxp);
    eye.dist = opU(eye.dist, d9);

    // integration
    mouse = opU(eye, mouse);
    result = opS(mouse, head);
    result = opU(cheep, result);

    result.dist *= minsc;

    return result;
}

// Base Pumpkin Distance function @ P_Malin
// https://www.shadertoy.com/view/MdBXR3
float sdPumpkinBody(vec3 p, float radius, float height)
{
	float fAngle1 = atan(p.x, p.z);
    float fSin = sin(fAngle1 * 16.0);
    fSin = 1.0 - sqrt(abs(fSin));
    vec3 sc = 1.0 + fSin * vec3(0.05, 0.025, 0.05);
    p *= sc;
    p.y *= height + 0.5 * (radius - length(p.xz)) / radius;

    return length(p) - radius;
}

surface sdPumpkinBoze(vec3 p, float ms)
{
    surface result = SURF_NOHIT(1e5);

    float fSphereRadius = 0.2;

	vec3 vOffset = p;
	float fFirstDist = length(vOffset);

	//float fOutDist;
	if(fFirstDist < 1.5)
	{
        // body
        float bottom = sdPumpkinBody(vOffset, fSphereRadius, 1.75);
        float top = sdPumpkinBody(vOffset + vec3(0, -0.075, 0), fSphereRadius * 0.65, 0.5);

        result = SURF_PUMPKIN(smin(bottom, top, 0.05));

        // stalk(heta)
        vec3 vStalkOffset = p;
        vStalkOffset.x += -(vStalkOffset.y - fSphereRadius) * 0.1;
        float fDist2d = length(vStalkOffset.xz);
        float fStalkDist = fDist2d - 0.02;
        fStalkDist = max(fStalkDist, p.y - 0.25 + p.x * 0.2);
        fStalkDist = max(fStalkDist, -p.y);
        surface stalk = SURF_PUMPKIN_STALK(fStalkDist);

        result = opSU(result, stalk, 0.02);

        vec3 mxp = vec3(-abs(p.x), p.yz);

        // ear
        float d2 = sdEar(mxp + vec3(0.01,-0.01,0));
        result.dist = smin(result.dist, d2, 0.005);

        // eye
#define eyeZ 0.12
        //float d4 = sdCapsule(mxp, vec3(-EYE_SPACE, 0.06, 0.13), vec3(-EYE_SPACE, 0.08, 0.125), 0.0175);
        float d4 = sdCapsule(mxp, vec3(-EYE_SPACE, 0.06, eyeZ+0.01), vec3(-EYE_SPACE, 0.1, eyeZ), 0.0175);
        surface eye = SURF_BLACK(d4);

        // mouse
        float d6 = sdMouse(p, ms);
        surface mouse = SURF_MOUSE(d6);

        // cheep
        float d7 = sdCheep(mxp + vec3(0,0,-0.015));
        surface cheep = SURF_CHEEP(d7);

        // eyebrows
        float d9 = sdEyeBrow(mxp);
        eye.dist = opU(eye.dist, d9);

        // integration
        mouse = opU(eye, mouse);
        result = opS(mouse, result);
        //result = opU(mouse, result);
        result = opU(cheep, result);
	}


	return result;
}
