surface sdSushi(vec3 p, vec3 sc, vec3 col) {
    surface result = SURF_NOHIT(1e5);

	float minsc = min(sc.x, min(sc.y, sc.z));
    p /= sc;

    // shari
    float shari = sdRoundBox(p, vec3(0.75,0.5,1.75), 0.125);

    result = SURF_SHARI(shari);

    //float neta = sdRoundBox(p-vec3(0,0.5625,0), vec3(1,0.25,2), 0.05);

    surface neta = SURF_NETA(sdRoundBox(p-vec3(0,0.5625,0), vec3(1,0.25,2), 0.05), col);

    result = opU(result, neta);

    result.dist *= minsc;

    return result;
}

