surface map(vec3 p)
{
	surface result = SURF_NOHIT(1e5);

    float ms = 1.;
    // boze
    //vec3 q = rotate(p, iTime * 0.2, vec3(0,1,0));
    //vec3 q = p;
    //surface boze = sdBoze(q-vec3(0.5,0.0,0), vec3(1), ms);

    surface pumpkin = sdPumpkinBoze(p, ms);

    // sushi
#define SUSHI_REP_Y 0.2

    float r = M_PI2 / 27.;
    for(int i = 0; i < 3; i++)
    {
        float index;
        float angle = float(i) * r;

        float yindex = floor((p.y +0.5*SUSHI_REP_Y + iTime * 0.1) / SUSHI_REP_Y );

        vec3 q = rotate(p, angle + iTime + hash11(yindex * 30.2)*RAD90, vec3(0,1,0));
        vec2 sr = foldRotate(q.xz, 9., index);

        q.y = mod(q.y+0.5*SUSHI_REP_Y + iTime * 0.1, SUSHI_REP_Y)-0.5 * SUSHI_REP_Y;
        vec3 sq =vec3(sr.x, q.y - abs(sin(index * 0.85+ iTime * 5. + angle))*0.05, sr.y -0.35 + sin(hash11(yindex * 13.24) * M_PI2 + iTime * 3.6 + hash11(index) * 0.6)*0.05);

        float sp = step(hash11(yindex * 561.817), 0.1);
        sq = rotate(sq, RAD90 * sp, vec3(1,0,0));
        sq = rotate(sq, (iTime *10.+ angle) * sp, vec3(0,0,1));

        // maguro
        vec3 col1 = mix(vec3(1,0.8,0.9), vec3(0.95,0,0.01), smoothstep(0.0, 0.2, fract(abs(sq.x + sq.y + sq.z)*100.)));
        // tamago
