#define nTiles 50.0

#define quant(v, n) floor((v) * (n)) / (n)
#define line(e0, d, f) (smoothstep(e0-d, e0, f)-smoothstep(e0, e0+d, f))
#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

vec3 colormap(float t)
{
    return 0.5 + 0.5 * cos(6.283 * (t + vec3(0.0, 0.1, 0.2)));
}

void mainImage( out vec4 C, in vec2 U )
{
    // pixel -> uv
    float dn = 1.0 / (2.*nTiles);
    vec2 p = (U - 0.5*iResolution.xy) / iResolution.x + dn;

    float t = iTime;
    vec3 col = vec3(0.0);

    float pix = nTiles / iResolution.y;
    float wave_t = mix(-.3, .75, fract(t * 0.15));
    float shape = line(wave_t, 1.5*pix, length(p));

    vec2 qp = rot(shape * 1.571)*vec2(-dn)+dn+p;
    float f = length(quant(qp, nTiles));
    shape = line(wave_t, 3.*pix, f);
    col = colormap(shape)*shape;

    float pts = line(0.0, pix, length(fract(qp * nTiles)-.5));
    col = mix(vec3(pts), col*(1.-pts), shape);

    C = vec4(col, 1.0);
}

