vec3 blobLayer(vec2 uv, vec2 timeOffset)
{
    vec3 col = vec3(0.);
    uv += iTime * timeOffset;

    float r = (sin(uv.x)/(2. + abs(4.*sin(iTime))) + cos(uv.y)/(2. + abs(4.*cos(iTime))));
    float g = (sin(uv.x)/(2. + abs(2.2*sin(iTime))) + cos(uv.y)/(2.523 + abs(4.*cos(iTime))));
    float b = (sin(uv.x)/(2. + abs(3.14*cos(iTime))) + cos(uv.y)/(2. + abs(4.*cos(iTime))));

    col += abs (vec3(r,g,b) / 3.);

    return col;   
}

