    d = d.x < dt.x  ? d : dt;

    q = rotate_y(q,  -PI * iTime * .25);

    vec2 d3 = sdBox(q, vec3(0.5,0.5,0.5), .3 );
    vec2 d4 = sdBox(q, vec3(0.6,0.1,0.6), .3 ); 
    dt = max(d3,d4);
    vec2 d5 = sdBox(q, vec3(0.4,0.8,0.4),  .5 );
    vec2 dt2 = max(-d5, dt);
    g+=(0.1/(.01+d5.x*d5.x*999.) * sin(iTime  * .5));
    d = d.x < dt.x  ? d : dt2;

    dt = max(-d5,d);

    d = d.x < dt.x  ? d : dt;

    q = rotate_z(q, PI * iTime * .5);
    vec2 d6 = sdBox(q, vec3(.3,0.1, .3), .7 );


    d = d.x < d6.x  ? d : d6;

    return d;
}
// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    const float ep = 0.0001;
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep ).x + 
					  e.yyx*map( pos + e.yyx*ep ).x + 
					  e.yxy*map( pos + e.yxy*ep ).x + 
					  e.xxx*map( pos + e.xxx*ep ).x );
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, float tmin, float tmax, const float k )
{
	float res = 1.0;
    float t = tmin;
    for( int i=0; i<50; i++ )
    {
		vec2 h = map( ro + rd*t );
        res = min( res, k*h.x/t );
        t += clamp( h.x, 0.02, 0.20 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec2 glitch(vec2 v){

  vec2 vt=v;
  for(int i=0;i<6;i++)
  {
    float fac=4.0*pow(2.2,-float(i));
    float s=fractsin(lofi(vt.x,1.6*fac));
    s+=fractsin(lofi(vt.y,0.4*fac));
    s+=fractsin(time);
    float proc=fractsin(s);
    vt+=0.2*step(proc,0.4*exp(-3.0*mod(BEAT,8.0))-0.01)*(vec2(
      fractsin(s+22.56),
      fractsin(s+17.56)
    )-0.5);
  }
  return vt;
}

