void rot3(inout vec3 p,vec3 a,float t){
	a=normalize(a);
	vec3 q=cross(a,p),u=cross(q,a);
	p=u*cos(t)+q*sin(t)+a*dot(p,a);   
}

#define sabs(p) sqrt((p)*(p)+2e-3)
#define smin(a,b) (a+b-sabs(a-b))*.5
#define smax(a,b) (a+b+sabs(a-b))*.5

float dodec(vec3 p,float r){
	float G=sqrt(5.)*.5+.5;
	vec3 n=normalize(vec3(G,1,0));
	float d=0.;
    p=sabs(p);
    d=smax(d,dot(p,n));
    d=smax(d,dot(p,n.yzx));
    d=smax(d,dot(p,n.zxy));
	return d-r;
}

float icosa(vec3 p,float r){
	float G=sqrt(5.)*.5+.5;
	vec3 n=normalize(vec3(G,1./G,0));
	float d=0.;
    p=sabs(p);
    d=smax(d,dot(p,n));
    d=smax(d,dot(p,n.yzx));
    d=smax(d,dot(p,n.zxy));
	d=smax(d,dot(p,normalize(vec3(1))));
    return d-r;
}

float map(vec3 p){
	rot3(p,vec3(1,2,3),iTime);
	return (fract(iTime*.2)<.5)?icosa(p,1.):dodec(p,1.);
}

