#define PI acos(-1.)
#define TAU 6.283185

#define time iTime
#define dt(speed) fract(time*speed)
#define swanim(speed) floor(sin(dt(speed)*TAU)+1.)

#define moda(puv,rep) float a=mod(atan(puv.y,puv.x),TAU/rep)-(TAU/rep)*0.5;puv=vec2(cos(a),sin(a))*length(puv)
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define hash21(x) fract(sin(dot(x,vec2(12.5,32.4)))*1284.4)


float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

float caps( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length (pa - ba*h) - r*h;
}

#define anim (swanim(0.1) < 0.5)
float g1 = 0.;

float SDF (vec3 p)
{
    if (anim) p.yz *= rot(PI/2.);

    float s = length(p-vec3(0.,2.,0.))-0.6;
    g1 += 0.01/(0.01+s*s);
    float body = caps(p,vec3(0.,8.,0.),vec3(0.,4.5,0.), 1.);

    p.xz *= rot(sin(p.y*0.5+dt(0.1)*TAU));
    moda(p.xz, 7.);
    p.z += cos(p.y*2.+dt(.5)*TAU)*0.1;
    p.x -= 1.2-s*0.4;
    float d = smin(cyl(p.xzy, 0.05+p.y*0.06, 4.),cyl(p.xzy-vec3(0.,0.,4.),0.25, 0.1),0.5);

    d = min(d, s);

    d = smin(d,body,0.3);

    return d;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

    float dither = hash21(uv);

    vec3 ro;
    if (anim) ro = vec3(0.001,0.001,-1.2);
    else   ro = vec3(0.001,3.5,-5.); 

    vec3 rd = normalize(vec3(uv,1.)), p = ro, col = vec3(0.,0.,0.05);

    bool hit = false;
    float shad, d = 0.;

    for (float i=0.; i<64.;i++)
    {
        d = SDF(p);
        if (d<0.01)
        {
            hit=true; shad = i/64.; break;
        }
        d *= 0.5+dither*0.2;
        p += d*rd;
    }

    if (hit) col = vec3(shad);

    col += g1*vec3(length(uv)*0.5,0.05,0.3)*texture(iChannel0, vec2(0.0001,0.25)).r*2.;

    fragColor = vec4(col,1.);
}

#define PI 3.1415

struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Sphere {
    vec3 center;
    float radius;
};


struct ConeFrustum {
	vec3 origin;
    vec3 axis;
    vec2 radii;
    float height;
};


// Perspective projection ray from screen coordinate
Ray getScreenRay(in vec2 uv)
{
    float vFov = 3.14 / 6.0;
    float hFov = atan(iResolution.x / iResolution.y * tan(vFov));

    vec2 coord = 2.0 * uv - 1.0;

    //// Orthographic projection
    //return Ray(2. * vec3(coord.x * iResolution.x / iResolution.y, coord.y, -2), vec3(0, 0, 1));

    float dv = atan(coord.y * tan(vFov));
    float dh = atan(coord.x * tan(hFov));

    vec3 direction = vec3(coord.x * iResolution.x / iResolution.y, coord.y, -1.5);
    return Ray(vec3(0,0,3), normalize(direction));
}

bool intersectConeFrustum( in Ray ray, in ConeFrustum cone, out float t, out vec3 normal )
{
    float dr = cone.radii.y - cone.radii.x;
    float r = 1. + pow(dr / cone.height, 2.);
    float R = cone.radii.x * dr / cone.height;

    vec3 D = ray.origin - cone.origin;
    float DdA = dot(D, cone.axis);
    float DdD = dot(D, D);
    float VdA = dot(ray.direction, cone.axis);
    float VdD = dot(ray.direction, D);
    float VdV = dot(ray.direction, ray.direction);

    float c0 = pow(cone.radii.x, 2.) + 2. * R * DdA + r * pow(DdA, 2.) - DdD;
    float c1 = R * VdA + r * DdA * VdA - VdD;
    float c2 = r * pow(VdA, 2.) - VdV;

    // Quadratic equation, solve with discriminant
    if ( c2 != 0. ) {
    	float discr = c1 * c1 - c2 * c0;

        // No real solution, no intersection
        if ( discr < 0. ) 
            return false;

        // Two intersection points. Select best or reject.
        else if ( discr > 0. ) {
        	float root = sqrt(discr);
            int quantity = 0;

            float x0 = (-c1 -root) / c2;
            vec3 p = D + x0 * ray.direction;

            // Check if valid ray-cone section intersection
            float d = dot(cone.axis, p);
            if (x0 >= 0. && d >= 0. && d <= cone.height) {
                vec3 n = normalize(p - d * cone.axis);
                float theta = atan((cone.radii.x - cone.radii.y) / cone.height);
                normal = normalize(cone.axis * sin(theta) + cos(theta) * n);
            	t = x0;
                quantity += 1;
            } 

            float x1 = (-c1 + root) / c2;
            p = D + x1 * ray.direction;
            d = dot(cone.axis, p);

            // Check if valid ray-cone section intersection and if x0 isn't a more adequate solution
            if (x1 >= 0. && (x1 < x0 || quantity == 0) && d >= 0. && d <= cone.height) {
                vec3 n = normalize(p - d * cone.axis); 
                float theta = atan((cone.radii.x - cone.radii.y) / cone.height);
                normal = normalize(cone.axis * sin(theta) + cos(theta) * n);
                t = x1;
                quantity += 1;
            }

            return quantity > 0;

        // One intersection. Ray tangent to the cone surface, but not parallel
        } else {
            float x = -c1 / c2;
            vec3 p = D + x * ray.direction;
            float d = dot(cone.axis, p);

            // Check if valid ray-cone section intersection
            if (x > 0. && d >= 0. && d <= cone.height) {
                vec3 n = normalize(p - d * cone.axis); 
                float theta = atan((cone.radii.x - cone.radii.y) / cone.height);
                normal = normalize(cone.axis * sin(theta) + cos(theta) * n);
                t = x;
                return true;
            }
            return false;
        }

    // Linear solution. Ray is parallel to cone surface.
    } else if (c1 != 0.) {
        float x = 0.5 * c0 / c1;
        vec3 p = D + x * ray.direction;
        float d = dot(cone.axis, p);
        if (x > 0. && d >= 0. && d <= cone.height) {
            vec3 n = normalize(p - d * cone.axis); 
            float theta = atan((cone.radii.x - cone.radii.y) / cone.height);
            normal = normalize(cone.axis * sin(theta) + cos(theta) * n);
            t = x;
            return true;
        }
        return false;
    }

    return false;        
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 origin; vec3 dir;
    Ray screenRay = getScreenRay(uv);
    screenRay.origin += 0.2 * screenRay.direction;

    float t = PI +iTime;
    vec3 normal = vec3(0,0,0);

    vec3 axis = vec3(0,1,0.4);
    ConeFrustum cone;
    cone.origin = vec3(0,-1.2,0.1);
    cone.axis = normalize(axis);
    cone.radii = vec2(0.7, 0.7 + 0.4 * cos(iTime / 3.));
    cone.height = 2.;

    bool intersect = intersectConeFrustum(screenRay, cone, t, normal);


    // Output to screen 
    if (intersect) {
        fragColor = vec4(vec3(1) * (dot(normalize(-vec3(1,1,1)), -normal) * 0.7 + 0.3), 1);
        fragColor = vec4(normal * 0.5 + 0.5, 1);
    } else {
        fragColor = vec4(screenRay.direction*0.5+0.5,1.0);
        fragColor = vec4(1);
    }
}

