#define TRLIMIT 256.
vec2 tr(vec3 ro, vec3 rd){
    vec2 h,t=vec2(.1);
    for(float i=0.;i<TRLIMIT;i++){
        h= sdf(ro+rd*t.x);
        if(h.x < .0001 || t.x > TRLIMIT) break;
        t.x+=h.x;
        t.y=h.y;
