void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float R = 10000.0;
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = (fragCoord - iResolution.xy / 2.)/iResolution.x * 4.;
    uv.x = uv.x;
    float cx = uv.x / (iTime * iTime * iTime) + 0.26010 ;
    float cy = uv.y / (iTime * iTime * iTime) + 0.49210 ;
    float zx = 0.0;
    float zy = 0.0;
    float iteration = 0.0;
    float max_iteration = 1000.0;

    while (zx * zx + zy * zy < R*R  &&  iteration < max_iteration) 
    {
        float xtemp = zx * zx - zy * zy;
        zy = 2. * zx * zy  + cy;
        zx = xtemp + cx;

        iteration = iteration + 1.;
    }

    if (iteration == max_iteration){
        fragColor = vec4(0.);
    }
    else{
        fragColor = vec4(hsv2rgb(vec3(1.0 - iteration / max_iteration,1.0,1.0)),1.);
    }

    // Output to screen
}

