                vec2 xf = uv * R + vec2(0, iTime * t * 1.);
                vec2 xi = floor(xf);
                xf -= xi + .5;
                vec2 off = 1. * (hash(xi + ij + t * vec2(2223.5, 3125.0)) - .5);
