vec3 rotate_y(vec3 v, float angle)
{
	float ca = cos(angle); float sa = sin(angle);
	return v*mat3(
		+ca, +.0, -sa,
		+.0,+1.0, +.0,
		+sa, +.0, +ca);
}

vec3 rotate_x(vec3 v, float angle)
{
	float ca = cos(angle); float sa = sin(angle);
	return v*mat3(
		+1.0, +.0, +.0,
		+.0, +ca, -sa,
		+.0, +sa, +ca);
}

vec3 proj(vec3 a , vec3 b){
    return (dot(a,b)/dot(b,b))*b;
}

float line(vec3 a, vec3 b, vec3 c, float r){
    vec3 norm = (a-c)-proj(a-c,a-b);
    return length(norm)-r;
}


float bell(vec3 p, float o){
    vec3 n=vec3(0.,-1.,0.);
    vec3 pproj=vec3(p.x,0.,p.z);

    float sintime=sin(iTime+o);

    n=rotate_x(n,sintime*0.2);
    p=rotate_x(p,-sintime*0.2);

    float angle=acos(dot(pproj,vec3(1.,0.,0.)/(length(pproj))));

    float plane = dot(p-n*0.1,n);
    float planar0=-sin(iTime+angle*2.+o+1.)*0.1;
    float planar1=-abs(sin(iTime+angle*4.+o*2.))*0.2;

    p*=smoothstep(0.,1.,1.-plane+planar1);

    //float radial0=0.;
    float radial0=sin(iTime+angle*3.)*0.07;
    float radial1=-abs(sin(angle*6.+iTime+o)*0.05);
	//float radial0=0.;

    float r=sintime*0.1+0.6;
    float f=max(
        abs(
            radial0+radial1+(length(p))-r)-0.04/(1.+o),
        plane+planar0);

    float bump = 0.01 * texture(iChannel0, p.xy*(5.+o)).r/(1.+o);

    f-=bump;

    return f;
}


float beaded(vec3 a, vec3 b, vec3 p){
   float r=abs(sin(dot(p,a-b)*0.5)*0.1)+0.01;
    return line(a,b,p,r);
}



float map(vec3 p){
    float bells=min(min(bell(p,0.), bell(p*0.75+vec3(0.,-0.5,0.),1.)),bell(p*0.5+vec3(0.,-0.7,0.),2.));
    vec3 a=vec3(-2.,20.,10.);
    vec3 b=vec3(-3.,0.,4.);

    vec3 c=vec3(0.,10.,10.);
    vec3 d=vec3(3.,-5.,4.);

    vec3 e=vec3(0.,10.,2.);
    vec3 f=vec3(2.,-5.,-2.);

    vec3 pwarp= vec3(p.xy+sin(iTime)*0.1,p.z);

    float lines= min(beaded(a,b,pwarp),beaded(c,d,pwarp));
    lines=min(lines,beaded(e,f,pwarp));
    return min(bells,lines);
}

vec3 norm(vec3 p){
    //approx

    float d=map(p);
    vec2 e = vec2(0.02,0.);
    vec3 n= d- vec3(map(p-e.xyy),
                    map(p-e.yxy),
                    map(p-e.yyx));


	return normalize(n);
}


float march(vec3 ro, vec3 rd){
    float d=0.;
    for(int i=0;i<80;i++){
        vec3 p= ro+d*rd;
        float dist = map(p);
        d+=dist;
        if(abs(dist)<0.01 || d>10.) break;
    }
    return d;
}

float getLight(vec3 p, vec3 n){
    vec3 lp=vec3(0.,5.,-4.);
    vec3 l=normalize(lp-p);
    float d =march(p,l);
    float nl = dot(n,l)*5.;
    if(d<length(p-lp))nl*=0.1;




	vec3 r = reflect(l,n);

	float reflectance = dot(l,r)*.5+.5;


    float fog=1./(1.+(p.z*p.z));
    //float fog=1.;


    return nl*reflectance*reflectance*fog;
    //return nl;
}

vec3 pal(float i, vec3 p, vec3 n){
    p.y=clamp(0.,1.,p.y);
    float T = 1500. + 1400.*i;
    vec3 L = mix(vec3(6.4, 1.6, 3.4),vec3(8.4, 6.4, 6.4),1.2-p.y);
    L = pow(L,vec3(5.0)) * (exp(1.43876719683e5/(T*L))-1.0);
    vec4 c =texture(iChannel1, p.xz*0.1)*(p.y+1.);
    c=c*c*c*c;
    c*=max(dot(n,vec3(0.,1.,0.)),0.);
    return 1.0-exp(-8e8/L)+c.xyz;
}



