float sdfLineSegment(vec2 p, vec2 a, vec2 b)
{
    float h = clamp(dot(p - a, normalize(b - a)), 0.0, 1.0);
    return length(p - (a + (b - a) * h) );
}

float sdf0(vec2 uv, vec2 p, vec2 middle_point, float size, float invalidation)
{
    float d = sdfLineSegment(uv, p, middle_point);
    if (invalidation > 0.5)
    {
        vec2 offset = p - middle_point;

        d = 0.5;
    }

    return d;
}

vec2 T(vec2 v, int i)
{
    float t = (float(i) + 1.0) * 3.14159265 * 0.25;

    float scale = pow(2.0, float(i - 1) / 2.0);
    mat2x2 change_of_basis = mat2x2(cos(t), sin(t), -sin(t), cos(t)) / scale;
    mat2x2 invert_of_basis = mat2x2(cos(t), -sin(t), sin(t), cos(t)) * scale;

    vec2 v_index = floor((change_of_basis*v)*0.5 + vec2(0.5, 0.5));
    float direction = 1.0 - 2.0 * ( mod(v_index.x + v_index.y + 4096.0, 2.0));

    vec2 v_center = (invert_of_basis*v_index*2.0);
    vec2 v_off = v - v_center;

    vec2 v_prime = vec2(v_off.x + v_off.y * direction, v_off.y - v_off.x * direction) + v_center;

    return v_prime;
}

