//----------------------------------------------------------------------------------------
///  2 out, 2 in...
vec2 hash22norm(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}
vec2 hash22(vec2 p)
{
	return hash22norm(p) * 2. - 1.;

}

// From https://www.shadertoy.com/view/Msf3WH
float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash22(i+0.0)), dot(b,hash22(i+o)), dot(c,hash22(i+1.0)));
    return dot( n, vec3(70.0) );
}
float noise2(in vec2 p) {
	return noise(p + 100.);
}
float noise3(in vec2 p) {
	return noise2(p + 100.);
}

// From https://www.shadertoy.com/view/XlB3zW
float voronoi(vec2 n)
{
    float i = 0.0;
    float dis = 2.0;
    for(int x = -1;x<=1;x++)
    {
        for(int y = -1;y<=1;y++)
        {
            vec2 p = floor(n)+vec2(x,y);
            float d = length(hash22norm(p)+vec2(x,y)-fract(n));
            if (dis>d)
            {
             	dis = d;
                i = hash22norm(p).x;
            }
        }
    }
    return dis;
}


mat3 rotationX(float angle) {
    vec3 u = vec3(1, 0, 0);
    vec3 v = vec3(0, cos(angle), -sin(angle));
    vec3 w = vec3(0, sin(angle), cos(angle));
    return mat3(u, v, w);
}

mat3 rotationY(float angle) {
    vec3 u = vec3(cos(angle), 0, sin(angle));
    vec3 v = vec3(0, 1, 0);
    vec3 w = vec3(-sin(angle), 0, cos(angle));
    return mat3(u, v, w);
}

mat3 rotationZ(float angle) {
    vec3 u = vec3(cos(angle), -sin(angle), 0);
    vec3 v = vec3(sin(angle), cos(angle), 0);
    vec3 w = vec3(0, 0, 1);
    return mat3(u, v, w);
}

mat3 rotationMatrix(vec3 angles) {
    return rotationZ(angles.z) * rotationY(angles.y) * rotationX(angles.x);
}


struct DistanceObject {
    float distance;
    vec3 color;
};
struct HitData {
    vec3 color;
    float distance;
    int iterations;
    vec3 position;
};

float sdSphere(vec3 pos, float rad) {
    return length(pos) - rad;
}


float sdPlane(vec3 pos) {
	return pos.y;
}


float dSkin(vec2 pos) {
    float voro = max(-.05, -voronoi(pos*20.)*.005);

    return (1.-exp(sin(noise2(pos*.3)*100.+pos.y*10.*5.)*.5 +.5)) * 0.005 + voro;
    //return noise(vec2(noise(pos + 100.) * 10., 0.)) * .01;
}

DistanceObject map(vec3 pos) {

    DistanceObject nearest = DistanceObject(MAX_DIST, vec3(1, 0, .5));


    //  WRITE OBJECTS HERE

    //SET_NEAREST(nearest, DistanceObject(sdPlane(pos), vec3(.9, .5, .3)));
    float baseDistance = pos.y - (noise(pos.xz * .2) * .5 + .5) * .1;

    //float dist = getHoleDistance(pos.xz * .5);
    float n = noise(pos.xz) * .5 + .5 + noise(vec2(iTime, 0)) * .05;

    if (n > HOLE_THRESH) {
		float holeDepth = (n - HOLE_THRESH) * 5. - BORDER_HEIGHT;
        //Hole walls
        SET_NEAREST(nearest, DistanceObject(baseDistance + holeDepth, BORDER_COLOR));
    } else if (n > BORDER_THRESH) {
        float h = (n - BORDER_THRESH) / (HOLE_THRESH - BORDER_THRESH);
        float bumps = dSkin(pos.xz)*(1.-h);
        h *= BORDER_HEIGHT;
        SET_NEAREST(nearest, DistanceObject(baseDistance - h + bumps, BORDER_COLOR));
    } else {
        //Skin surface
	    float skinDistance = baseDistance + dSkin(pos.xz);
        //float skinDistance = baseDistance;
        SET_NEAREST(nearest, DistanceObject(skinDistance, SKIN_COLOR));
    }
    //Hole bottom surface
    float r = noise3(pos.xz*2.) * .5 + .5;
    r *= .8;
    //r = round(r * 10.) / 10.;
    SET_NEAREST(nearest, DistanceObject(sdPlane(pos + vec3(0, HOLE_DEPTH, 0)), vec3(1, .72*r, .55*r)));


    return nearest;
}


HitData render(vec3 pos, vec3 rayDir) {
	DistanceObject obj;
    int i;
    float dist = 0.;
    for (i = 0; i < MAX_ITER; i++) {
    	obj = map(pos);
        obj.distance *= .2;
        dist += obj.distance;
        pos += rayDir * obj.distance;

        if (obj.distance < .0001) {
        	break;
        }
    }

    if (i == MAX_ITER || dist > MAX_DIST) {
    	dist = MAX_DIST;
        obj.color = vec3(0.);
    }

    HitData res = HitData(obj.color, dist, i, pos);

    return res;
}


