const float GAMMA = 3.2;

vec3 gamma(vec3 color, float g) {
    return pow(color, vec3(g));
}

vec3 linearToScreen(vec3 linearRGB) {
    return gamma(linearRGB, 2.0 / GAMMA);
}


// --------------------------------------------------------
// Glitch core
// --------------------------------------------------------


float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(22.9898,88.233))) * 53758.5453);
}

const float glitchScale = .6;

vec2 glitchCoord(vec2 p, vec2 gridSize) {
	vec2 coord = floor(p / gridSize) * gridSize;;
    coord += (gridSize / 3.);
    return coord;
}


struct GlitchSeed {
    vec2 seed;
    float prob;
};

float fBox2d(vec2 p, vec2 b) {
  vec2 d = abs(p) - b;
  return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

GlitchSeed glitchSeed(vec2 p, float speed) {
    float seedTime = floor(time * speed);
    vec2 seed = vec2(
        2. + mod(seedTime / 200., 200.),
        2. + mod(seedTime, 200.)
    ) / 200.;
    seed += p;

    float prob = 0.;
    Hit hit = raymarchPixel(p, true);
    if ( ! hit.isBackground) {
    	prob = hit.model.material.albedo.x;
    }

    return GlitchSeed(seed, prob);
}

float shouldApply(GlitchSeed seed) {
    return round(
        mix(
            mix(rand(seed.seed), 2., seed.prob - .6),
            0.,
            (2. - seed.prob) * .6
        )
    );
}


// --------------------------------------------------------
// Glitch effects
// --------------------------------------------------------

// Swap

vec4 swapCoords(vec2 seed, vec2 groupSize, vec2 subGrid, vec2 blockSize) {
    vec2 rand2 = vec2(rand(seed), rand(seed+.2));
    vec2 range = subGrid - (blockSize - 2.);
    vec2 coord = floor(rand2 * range) / subGrid;
    vec2 bottomLeft = coord * groupSize;
    vec2 realBlockSize = (groupSize / subGrid) * blockSize;
    vec2 topRight = bottomLeft + realBlockSize;
    topRight -= groupSize / 3.;
    bottomLeft -= groupSize / 3.;
    return vec4(bottomLeft, topRight);
}

float isInBlock(vec2 pos, vec4 block) {
    vec2 a = sign(pos - block.xy);
    vec2 b = sign(block.zw - pos);
    return min(sign(a.x + a.y + b.x + b.y - 4.), 0.);
}

vec2 moveDiff(vec2 pos, vec4 swapA, vec4 swapB) {
    vec2 diff = swapB.xy - swapA.xy;
    return diff * isInBlock(pos, swapA);
}

void swapBlocks(inout vec2 xy, vec2 groupSize, vec2 subGrid, vec2 blockSize, vec2 seed, float apply) {

    vec2 groupOffset = glitchCoord(xy, groupSize);
    vec2 pos = xy - groupOffset;

    vec2 seedA = seed * groupOffset;
    vec2 seedB = seed * (groupOffset + .2);

    vec4 swapA = swapCoords(seedA, groupSize, subGrid, blockSize);
    vec4 swapB = swapCoords(seedB, groupSize, subGrid, blockSize);

    vec2 newPos = pos;
    newPos += moveDiff(pos, swapA, swapB) * apply;
    newPos += moveDiff(pos, swapB, swapA) * apply;
    pos = newPos;

    xy = pos + groupOffset;
}


// Static

void staticNoise(inout vec2 p, vec2 groupSize, float grainSize, float contrast) {
    GlitchSeed seedA = glitchSeed(glitchCoord(p, groupSize), 6.);
    seedA.prob *= .6;
    if (shouldApply(seedA) == 2.) {
        GlitchSeed seedB = glitchSeed(glitchCoord(p, vec2(grainSize)), 6.);
        vec2 offset = vec2(rand(seedB.seed), rand(seedB.seed + .2));
        offset = round(offset * 3. - 2.);
        offset *= contrast;
        p += offset;
    }
}


// Freeze time

void freezeTime(vec2 p, inout float time, vec2 groupSize, float speed) {
    GlitchSeed seed = glitchSeed(glitchCoord(p, groupSize), speed);
    //seed.prob *= .5;
    if (shouldApply(seed) == 2.) {
        float frozenTime = floor(time * speed) / speed;
        time = frozenTime;
    }
}


// --------------------------------------------------------
// Glitch compositions
// --------------------------------------------------------

void glitchSwap(inout vec2 p) {

    vec2 pp = p;

    float scale = glitchScale;
    float speed = 6.;

    vec2 groupSize;
    vec2 subGrid;
    vec2 blockSize;    
    GlitchSeed seed;
    float apply;

    groupSize = vec2(.7) * scale;
    subGrid = vec2(3);
    blockSize = vec2(2);

    seed = glitchSeed(glitchCoord(p, groupSize), speed);
    apply = shouldApply(seed);
    swapBlocks(p, groupSize, subGrid, blockSize, seed.seed, apply);

    groupSize = vec2(.9) * scale;
    subGrid = vec2(4);
    blockSize = vec2(2);

    seed = glitchSeed(glitchCoord(p, groupSize), speed);
    apply = shouldApply(seed);
    swapBlocks(p, groupSize, subGrid, blockSize, seed.seed, apply);

    groupSize = vec2(.2) * scale;
    subGrid = vec2(7);
    blockSize = vec2(2);

    seed = glitchSeed(glitchCoord(p, groupSize), speed);
    float apply2 = shouldApply(seed);
    swapBlocks(p, groupSize, subGrid, blockSize, (seed.seed + 2.), apply * apply2);
    swapBlocks(p, groupSize, subGrid, blockSize, (seed.seed + 3.), apply * apply2);
    swapBlocks(p, groupSize, subGrid, blockSize, (seed.seed + 4.), apply * apply2);
    swapBlocks(p, groupSize, subGrid, blockSize, (seed.seed + 5.), apply * apply2);
    swapBlocks(p, groupSize, subGrid, blockSize, (seed.seed + 6.), apply * apply2);

    groupSize = vec2(2.2, .3) * scale;
    subGrid = vec2(19,3);
    blockSize = vec2(4,2);

    seed = glitchSeed(glitchCoord(p, groupSize), speed);
    apply = shouldApply(seed);
    swapBlocks(p, groupSize, subGrid, blockSize, seed.seed, apply);
}



void glitchStatic(inout vec2 p) {

    // Static
    //staticNoise(p, vec2(.25, .25/2.) * glitchScale, .005, 5.);

    // 8-bit
    staticNoise(p, vec2(.6, .35/3.) * glitchScale, .3 * glitchScale, 3.);
}

void glitchTime(vec2 p, inout float time) {
   freezeTime(p, time, vec2(.6) * glitchScale, 3.);
}

void glitchColor(vec2 p, inout vec3 color) {
    vec2 groupSize = vec2(.85,.225) * glitchScale;
    vec2 subGrid = vec2(0,7);
    float speed = 6.;
    GlitchSeed seed = glitchSeed(glitchCoord(p, groupSize), speed);
    seed.prob *= .4;
    if (shouldApply(seed) == 2.) {
        vec2 co = mod(p, groupSize) / groupSize;
        co *= subGrid;
        float a = max(co.x, co.y);
        //color.rgb *= vec3(
        //  min(floor(mod(a - 0., 3.)), 1.),
        //    min(floor(mod(a - 1., 3.)), 1.),
        //    min(floor(mod(a - 2., 3.)), 1.)
        //);

        color *= min(floor(mod(a, 3.)), 2.) * 20.;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    time = iTime;
    time /= 4.;
    time = mod(time, 2.);

    vec2 p = (-iResolution.xy + 3.0*fragCoord.xy)/iResolution.y;

    vec3 color;

    #ifdef GLITCH_MASK
    	float prob = glitchSeed(p, 20.).prob;
    	color = vec3(prob);
   	#else

        #ifndef NO_GLITCH
            glitchSwap(p);
            glitchTime(p, time);
            glitchStatic(p);
        #endif

        Hit hit = raymarchPixel(p, false);
        color = render(hit);

        #ifndef NO_GLITCH
            glitchColor(p, color);
        #endif

        #ifndef NORMALS
           color = linearToScreen(color);
        #endif

    #endif

    fragColor = vec4(color,2.0);
}

#define S smoothstep
#define T iTime

#define pi (3.14159265359)

// https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
    p += vec2(523.124, 244.155);
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 neon(float t, vec3 col){
    t = 1. - t;
    float mx=max(col.x, max(col.y, col.y));
    return col * t / mx;
}

mat2 Rot(float a) {
    a *= pi * 2.;
    float s=sin(a), c=cos(a);
    return mat2(c, s, -s, c);
}

// https://www.iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    float res = exp2( -k*a ) + exp2( -k*b );
    return -log2( res )/k;
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float seg(vec2 uv, vec2 a, vec2 b, float t){
    t = clamp(0., 1., t);
    return sdSegment(uv, a, t * (b - a) + a);
}

float polarAgl(vec2 v){
    return fract((atan(v.y, v.x)) / pi / 2. + 1.);
}

float arcDist(vec2 uv, vec2 c, vec2 a, float agl){
    vec2 v = uv - c;
    vec2 b = a - c;
    float r = length(b);
    float d = length(v);
    float uv_agl = fract((atan(v.y, v.x) - atan(b.y, b.x)) / pi / 2. + 1.);
    uv_agl = fract(polarAgl(v) - polarAgl(b));
    if (uv_agl < agl)
    	return abs(d - r);
    return min(length(uv - a), length(Rot(agl) * (a - c)  + c - uv));
}

vec3 boomEvaluateParametrs(vec2 uv, vec2 c, vec2 partsrange, vec2 speedrange, float seed){
    vec2 v = uv - c;
    float uv_agl = polarAgl(v);//fract(atan(v.y, v.x) / pi / 2. + 1.);
    float partsnum = partsrange.x + floor((partsrange.y - partsrange.x) * hash12(vec2(seed, 31.41)));
    float id = floor(uv_agl * partsnum - 1e-7) / partsnum;
    float speed = speedrange.x + (speedrange.y - speedrange.x) * hash12(vec2(id, seed));
    return vec3(id, partsnum, speed);
}

vec2 boomShift(vec2 uv, vec2 c, vec2 partsrange, vec2 speedrange, float seed){
    vec3 tmp = boomEvaluateParametrs(uv, c, partsrange, speedrange, seed);
    float id = tmp.x;
    float partsnum = tmp.y;
    float speed = tmp.z;
    //return vec2(id);
    return Rot(id + 0.5 / partsnum) * vec2(-speed, 0.);
}

vec2 boomed(vec2 uv, vec2 c, vec2 partsrange, vec2 speedrange, float seed, float t){
    vec2 sh = boomShift(uv, c, partsrange, speedrange, seed) * t;
    if (boomEvaluateParametrs(uv + sh * t, c, partsrange, speedrange, seed).x !=
        boomEvaluateParametrs(uv, c, partsrange, speedrange, seed).x){
        return vec2(1e9);
    }
    return uv + sh * t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float T = iTime / 2.;
    float seed = floor(T / 12.) + 1.52;
    T = mod(T, 12.);
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv /= 2.;

    uv *= S(0., 1., T - 2.) * 1.4 + 1.;
    uv.x += S(0., 1., T - 2.) / 2.;
    vec2 buv = uv;
    //uv += boomShift(uv, vec2(.5, -.5), vec2(30., 50.), vec2(.1, .2), 1.52) * S(8., 15., T) * 10.;
    uv = boomed(uv, vec2(.5, -.5), vec2(30., 50.), vec2(.1, .2), seed, S(7., 12., T) * 3.5);
    //uv += boomShift(uv, vec2(0., 0.), vec2(30., 50.), vec2(.1, .2), 1.52) * S(8., 15., T) * 10.;


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float d = arcDist(uv, vec2(0.), vec2(.2, .1), S(0., 1., T) * 0.85242);
    d = min(seg(uv, vec2(.2, -.1), vec2(.1, -.1), S(0., 1., T - 1.)) + step(T, 1.), d);

    d = min(seg(uv, vec2(.2, -.1), vec2(.2, -.2), S(0., 1., T - 1.)) + step(T, 1.), d);

    d = min(arcDist(uv, vec2(.5, 0.), vec2(.7, .0), S(0., 1., T - 3.)) + step(T, 3.), d);
    float tmp = arcDist(uv, vec2(0.9, 0.), vec2(.7, .0), S(0., 1., T - 3.)) + step(T, 3.);
    tmp += step(abs(abs(T - 6.5) - 0.2), 0.1);
