    sdf = max(sdf,d);

    return sdf;
}



// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.125*iTime;
	vec3 ro = vec3( 6.0*cos(an), .8+sin(iTime)*0.4, 6.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));



    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

        // raymarch
        const float tmax = 30.0;
        float t = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }

        // shading/lighting
        float v = 1.0-abs(p.y);
        vec3 col = vec3(v*0.1);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);

            vec3 ldir = normalize(vec3(0.5,0.2,0.4));

            float dif = clamp( dot(nor,ldir), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,0.8,0.6));
            col = vec3(0.2,0.2,0.2)*amb + vec3(0.8,0.7,0.5)*dif;
   			col+= pow(dif, 40.);

        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

const float PI = 3.1415926538;
const float epsilon = 0.001;

const int nSpheres = 20;
const int nSamples = 8;
const int maxDepth = 4;

const int lightIndex = 0;

vec2 NewSeed(vec2 seed)
{
	return seed += 0.1;
}

float repeatRand(inout vec2 seed)
{
	seed = NewSeed(seed);
	return fract(sin(dot(seed.xy ,vec2(12.3215,78.233))) * 9.5453);
}

vec2 repeatRand2(inout vec2 seed)
{
	seed = NewSeed(seed);
	return vec2(fract(sin(dot(seed.xy ,vec2(12.9898,78.233))) * 43758.5453),
				fract(sin(dot(seed.xy ,vec2(35431.1234,45.567))) * 15454.5675));
}

vec3 repeatRand3(inout vec2 seed)
{
	seed = NewSeed(seed);
	return vec3(fract(sin(dot(seed.xy ,vec2(1334.9898,128.233))) * 7.5453),
				fract(sin(dot(seed.xy ,vec2(1342.1234,45.567))) * 53.5675), 
				fract(sin(dot(seed.xy ,vec2(5904.2344,2.244))) * 234.6451));
}

float SameRand(vec2 seed)
{
	return fract(sin(dot(seed.xy ,vec2(12.3215,78.233))) * 9.5453);
}

vec3 SameRand3(vec2 seed)
{
	return vec3(fract(sin(dot(seed.xy ,vec2(12.9898,78.233))) * 43758.5453),
				fract(sin(dot(seed.xy ,vec2(1.1234,45.567))) * 23454.5675), 
				fract(sin(dot(seed.xy ,vec2(56.2344,23.244))) * 46442.6451));
}

struct Material
{
	vec3 albedo;
	vec3 emission;
	int type; //0 = diffuse, 1 = specular, 2 = refractive

};

struct Sphere
{
	vec3 pos;
	float radius;
	Material material;
};

struct Intersection
{
	bool isFound;
	float dist;
	vec3 pos;
	Material material;
	vec3 normal;
};

vec3 GenerateRayDirection(vec2 uv, vec2 seed)
{
	vec2 pixelScale = 1.0 / iResolution.xy;
	float xOffset = repeatRand(seed) * pixelScale.x;
	float yOffset = repeatRand(seed) * pixelScale.y;
	return normalize(vec3(uv.x + xOffset, uv.y + yOffset, 1));
}

void GenerateSpheres(inout Sphere spheres[nSpheres])
{
	vec2 seed = vec2(2);
	for(int i = 0; i < nSpheres; i++)
	{
		spheres[i].pos = SameRand3(seed);
		spheres[i].pos.x = (spheres[i].pos.x - 0.5) * 10.0;
		spheres[i].pos.y = (spheres[i].pos.y - 0.5) * 5.0 * sin(iTime * 1.1234 * SameRand(vec2(float(i)))) - 3.;
		spheres[i].pos.z = spheres[i].pos.z * 20.0 + 5.0;
		if(false) //toggle side view
		{
			float temp = spheres[i].pos.x;
			spheres[i].pos.x = spheres[i].pos.y;
			spheres[i].pos.y = temp;
		}

		spheres[i].radius = 1.0;
		Material m;
		m.albedo = repeatRand3(seed) * 0.5 + 0.5;
		m.emission = vec3(0);
		spheres[i].material = m;
	}
	//Add light source
	spheres[lightIndex].pos = vec3(sin(iTime) * 2.0, 3.0, 15.0 + cos(iTime) * 2.0);
	//spheres[lightIndex].pos = vec3(0.0, 4.0, 15.0);
	spheres[lightIndex].material.emission = vec3(1);
	spheres[lightIndex].radius = 1.0;
	//Add walls
	spheres[5].pos = vec3(1000, 0, 0);
	spheres[5].radius = 995.0;
	spheres[5].material.albedo = vec3(0.9, 0, 0);

	spheres[4].pos = vec3(-1000, 0, 0);
	spheres[4].radius = 995.0;
	spheres[4].material.albedo = vec3(0, 0.9, 0);

	spheres[3].pos = vec3(0, 1000, 0);
	spheres[3].radius = 995.0;
	spheres[3].material.albedo = vec3(0.9, 0.9, 0.9);

	spheres[2].pos = vec3(0, -1000, 0);
	spheres[2].radius = 995.0;
	spheres[2].material.albedo = vec3(0.9, 0.9, 0.9);

	spheres[1].pos = vec3(0, 0, 1000);
	spheres[1].radius = 970.0;
	spheres[1].material.albedo = vec3(0.9, 0.9, 0.9);
}

void MakeOrthonormal(vec3 v1, inout vec3 v2, inout vec3 v3)
{
	if(abs(v1.x) > abs(v1.y))
	{
		//Project to the y = 0 plane and construct a normalized orthogonal vector in this plane
		float invertedLength = 1.0 / sqrt(v1.x * v1.x + v1.z * v1.z);
		v2 = vec3(-v1.z * invertedLength, 0.0, v1.x * invertedLength);
	}
	else
	{
		//Project to the x = 0 plane and construct a normalized orthogonal vector in this plane
		float invertedLength = 1.0 / sqrt(v1.y * v1.y + v1.z * v1.z);
		v2 = vec3(0.0, v1.z * invertedLength, -v1.y * invertedLength);
	}
	v3 = cross(v1, v2);
}

vec3 HemisphereDirection(vec2 uv)	//Projects flat points onto a hemisphere
{
	float r = sqrt(1.0 - uv.x * uv.x);
	float phi = 2.0 * PI * uv.y;
	return vec3(cos(phi) * r, sin(phi) * r, uv.x);
}

vec3 RandomSpherePoint(inout vec2 seed, float radius)
{
	while(true)
	{
		vec3 result = repeatRand3(seed);
		result * radius;
		if(length(result) < radius)
		{
			return result;
		}
	}
}

vec3 RandomSphereDirection(inout vec2 seed) {
    vec2 h = repeatRand2(seed) * vec2(2.,6.28318530718)-vec2(1,0);
    float phi = h.y;
	return vec3(sqrt(1.-h.x*h.x)*vec2(sin(phi),cos(phi)),h.x);
}

bool TraceLightRay(vec3 rayOrigin, inout Sphere spheres[nSpheres], inout vec2 seed)
{
	vec3 randomLightPoint = RandomSphereDirection(seed) * spheres[lightIndex].radius + spheres[lightIndex].pos;
	vec3 rayDirection = normalize(randomLightPoint - rayOrigin);
	int intersectionIndex = 1000;
	float closestDistance = 5000.0;
	for(int i = 0; i < nSpheres; i++)
	{
		float t = dot(spheres[i].pos - rayOrigin, rayDirection);
		vec3 p = rayOrigin + rayDirection * t;
		float y = length(spheres[i].pos - p);
		if(y < spheres[i].radius)
		{
			float x = sqrt(spheres[i].radius * spheres[i].radius - y * y);
			float t1 = t - x;
			float t2 = t + x;
		   	float dist;
			if(t1 < epsilon)
			{
				dist = t2;
			}
			else if(t2 < epsilon)
			{
				dist = t1;
			}
			else
			{
				dist = min(t1, t2);
			}
			if(dist < closestDistance && dist > epsilon)
			{
				intersectionIndex = i;
				closestDistance = dist;
			}
		}
	}
	return intersectionIndex == lightIndex;
}

vec3 TraceRay(vec3 rayOrigin, vec3 rayDirection, vec2 uv, int depth, inout vec2 seed, bool justAlbedo)
{
	Sphere spheres[nSpheres];
	GenerateSpheres(spheres);

	vec3 result = vec3(0);//0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));
	vec3 collectedAlbedo[maxDepth];
	vec3 collectedEmission[maxDepth];
	vec3 collectedIncomingLight[maxDepth]; //Collects incoming light from the light source at this poin

	for(int i; i < maxDepth; i++)
	{
		Intersection intersection;
		intersection.dist = 5000.0;
		intersection.isFound = false;

		for(int j = 0; j < nSpheres; j++)
		{
			//Calculate collision with sphere
			float t = dot(spheres[j].pos - rayOrigin, rayDirection);
			vec3 p = rayOrigin + rayDirection * t;
			float y = length(spheres[j].pos - p);
			if(y < spheres[j].radius)
			{
				float x = sqrt(spheres[j].radius * spheres[j].radius - y * y);
				float t1 = t - x;
				float t2 = t + x;
			   	float dist = min(t1, t2);
				if(dist < intersection.dist && dist > epsilon)
				{
					vec3 intersectionPoint = rayOrigin + rayDirection * dist;
					intersection.isFound = true;
					intersection.dist = dist;
					intersection.pos = intersectionPoint;
					intersection.material = spheres[j].material;
					intersection.normal = normalize(intersectionPoint - spheres[j].pos);
				}
			}
		}

		if(intersection.isFound)
		{
			Material m = intersection.material;
			if(justAlbedo)
			{
				if(TraceLightRay(intersection.pos, spheres, seed))
				{
					return m.albedo;
				}
				else
				{
					return m.albedo * 0.2;
				}
			}

			//Calculate direction for next ray
			vec3 rotationX;
			vec3 rotationY;
			MakeOrthonormal(intersection.normal, rotationX, rotationY);
			vec3 sampleDirection = HemisphereDirection(repeatRand2(seed));
			vec3 rotatedDirection;
			rotatedDirection.x = dot(vec3(rotationX.x, rotationY.x, intersection.normal.x), sampleDirection);
			rotatedDirection.y = dot(vec3(rotationX.y, rotationY.y, intersection.normal.y), sampleDirection);
			rotatedDirection.z = dot(vec3(rotationX.z, rotationY.z, intersection.normal.z), sampleDirection);
			float cosTerm = dot(rotatedDirection, intersection.normal);

			//Set up next ray
			rayOrigin = intersection.pos;
			rayDirection = rotatedDirection;//sampleDirection;

			//Store emitted light and albedo for current ray
			collectedAlbedo[i] = m.albedo * cosTerm;
			collectedEmission[i] = m.emission * 2.0; //2.0 to account for lack of infinite light bounces

			//Direct light sampling
			if(TraceLightRay(intersection.pos, spheres, seed))
			{
				collectedIncomingLight[i] = spheres[lightIndex].material.emission;
			}
			else
			{
				collectedIncomingLight[i] = vec3(0.0);
			}
		}
	}

	//Get light from the final bounce
	vec3 transmittedRadiance = vec3(0.0);//vec3(0.3);//collectedEmission[maxDepth - 1];
	//Apply reflected light in reverse order
	for(int i = maxDepth - 1; i >= 0; i--)
	{
		transmittedRadiance = collectedEmission[i] + collectedAlbedo[i] * transmittedRadiance + collectedAlbedo[i] * collectedIncomingLight[i];
	}

	return result = transmittedRadiance;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

	vec2 seed = uv * PI * fract(1.12345314312*iTime);

	vec3 col = vec3(0);

	bool justAlbedo;
	if(sin(iTime * 1.1) < 0.0)
	{
		justAlbedo = true;
	}
	else
	{
		justAlbedo = false;
	}

	//Trace ray through scene to retrieve color for current pixel
	for(int i = 0; i < nSamples; i++)
	{
		//Generate ray
		vec3 rayOrigin = vec3(0);
		vec3 rayDirection = GenerateRayDirection(uv, seed);
		col += TraceRay(rayOrigin, rayDirection, uv, 0, seed, justAlbedo);
	}

	col /= float(nSamples);

	fragColor = vec4(col,1.0);
}

//=================================================================
//= Cyclostationary noise - paper 1152 - For Eurographics 2021
//=================================================================
//= Based on the shadertoys of Arthur Cavalier (LRPN, phasor) and Fabrice Neyret (Gabor noise).
//- Shaders : https://www.shadertoy.com/view/WscGD8
//-           https://www.shadertoy.com/view/WsSSWG
//=================================================================

//The spectrum model is based on manipulating the frequency, orientation and range
//of a bi-lobe spectrum according to the spatial coordinates with a period.
//The viewer is invited to play with the definitions of F (range of frequencies) 
//and O (range of orientations) from line 188 to 202
//in order to modify the spatially-varying spectrum used.

//--------------------------------------------------------------------------------------------------------
//-- User Parameters -------------------------------------------------------------------------------------  
#define MOUSE_CONTROL
#define TIMER_OFFSET
const int   LRPN_COSINES     = 3;
const float LRPN_RESOLUTION  = 5.0;
const float LRPN_SLOPE_SCALE = 0.1;
const uint  LRPN_GLOBAL_SEED = 0u;

float 		GABOR_SIZE   = 0.07;
float		GABOR_NB_KERNELS = 300.; //lower if running on a low-end PC

//--------------------------------------------------------------------------------------------------------
//-- Constants -------------------------------------------------------------------------------------------  
const float m_pi_2 		= 1.5707963267;         
const float m_pi   		= 3.1415926535;          
const float m_2_pi 		= 6.2831853071;
const float m_isqrt2 	= 0.7071067811865;


//--------------------------------------------------------------------------------------------------------
//-- Pseudo Random Number Generation ---------------------------------------------------------------------  
// From Texton Noise Source Code provided by Arthur Leclaire et al. 
// https://www.idpoisson.fr/galerne/texton_noise/index.html
// Sourced ::
/* 
 * From http://www.reedbeta.com/blog/2013/01/12/quick-and-easy-gpu-random-numbers-in-d3d11/
 * Same strategy as in Gabor noise by example
 * Apply hashtable to create cellseed
 * Use a linear congruential generator as fast PRNG
 */

uint  wang_hash(uint seed)                                          {seed=(seed^61u)^(seed>>16u);seed*=9u;seed=seed^(seed>>4u);seed*=668265261u;seed=seed^(seed>>15u);return(seed);}
uint  cell_seed(const in ivec2 c, const in uint offset)             {const uint period=1024u;uint s=((uint(c.y)%period)*period+(uint(c.x)%period))*period+offset; if(s==0u){s = 1u;}return(s);}
uint  myrand(inout uint p)                                          {p^=(p<<13u);p^=(p>>17u);p^=(p<<5u);return p;}
float myrand_uniform_0_1(inout uint p)                              {return float(myrand(p))/float(4294967295u);}
float myrand_uniform_m_M(inout uint p, in float mi, in float ma)    {return mi + (myrand_uniform_0_1(p) * (ma - mi));}
bool keyToggle(int ascii) 
{
	return (texture(iChannel2,vec2((.5+float(ascii))/256.,0.75)).x > 0.);
}

//--------------------------------------------------------------------------------------------------------
//-- Normal Mapping Function -----------------------------------------------------------------------------  
vec3 normal_map_from_slope(float dx, float dy) // using partial derivatives
{
    float norm = length(vec3(dx,dy,1.0));
    float nx = -dx / norm; 
    float ny = -dy / norm; 
    float nz = 1.0 / norm; 
    return 0.5 + 0.5 * vec3(nx,ny,nz);
}

//--------------------------------------------------------------------------------------------------------
//-- Gaussian Window Function ----------------------------------------------------------------------------  
float gaussian(in vec2 st, in float c, in vec2 mu, in mat2 sig)
{
    vec2 p = st-mu;
    float body = -0.5*dot(p,inverse(sig)*p);
    return c*exp(body);
}

//--------------------------------------------------------------------------------------------------------
//-- Profile Functions ----------------------------------------------------------------------------------- 
//-- From Thibault Tricard source : https://www.shadertoy.com/view/wlsXWf
float PWM(float x, float r) {return mod(x,m_2_pi)> m_2_pi *r ? 1.0 : 0.0;}
float wave_square(float x)  {return PWM(x,0.5);}
float wave_saw(float x)     {return mod(x,m_2_pi)/(m_2_pi);}
float wave_sine(float x)    {return 0.5+0.5*sin(x);}



//--------------------------------------------------------------------------------------------------------
//-- Local Random Phase Noise ----------------------------------------------------------------------------
vec4 local_random_phasor_noise(
        in vec2  texcoords,
        in float resolution,
        in int   cosines,
        in vec2  range_frequency,
        in vec2  range_orientation
    )
{
    vec2  scaled_coords = texcoords * resolution;
    vec2  cell_coords   = fract(scaled_coords);
    vec2  cell_index    = floor(scaled_coords);

    ivec2 cell_ID;
    uint  prng, seed;

    vec4  lrpn   = vec4(0.);
    float weight = 1. / float(cosines);
    mat2  sigma  = mat2( (1.2*1.2)/m_2_pi );

    for (int m=-1; m<=+1; m++)
    for (int n=-1; n<=+1; n++)
    {
        cell_ID.x = int(cell_index.x) + m;
        cell_ID.y = int(cell_index.y) + n;
        seed = cell_seed(cell_ID,LRPN_GLOBAL_SEED);
        prng = wang_hash(seed);

        vec2 xy = cell_coords - vec2(m,n) - vec2(0.5);
        float d = dot(xy,xy);

        vec2  sum_of_cosines    = vec2(0.); 
        float sum_of_partial_x  = 0.; 
        float sum_of_partial_y  = 0.; 
        for(int k=0; k<cosines; k++)
        {
            float fr = myrand_uniform_m_M(prng,range_frequency.x,range_frequency.y) * resolution;   // Scaled Frequency 
            float or = myrand_uniform_m_M(prng,range_orientation.x,range_orientation.y);            // Orientation
            float ph = m_pi*(myrand_uniform_0_1(prng)*2.-1.);                                       // Phase
            float ff = m_2_pi * dot(xy, fr * vec2(cos(or),sin(or)) );                               // Oriented Frequency
            vec2  sc = vec2( cos(ff+ph), sin(ff+ph) ); 

            sum_of_cosines   += sc;
            sum_of_partial_x += -m_2_pi * fr * cos(or) * sc.y;
            sum_of_partial_y += -m_2_pi * fr * sin(or) * sc.y; 
        }

        lrpn += gaussian(xy, 1., vec2(0.0), sigma) * weight * vec4(sum_of_cosines, sum_of_partial_x, sum_of_partial_y);
    }
    return vec4(lrpn); // vec3(Cosines, Sines, Partial X, Partial Y); 
}

