float looped_smooth_stairs(float t, float ratio, float nof_values)
{
    t*= nof_values;
    return mix(floor(mod(t/ratio, nof_values)), floor(mod((t+1.)/ratio, nof_values)), smoothstep(0.0, 1.0, fract(t)));
}

float gauss(float x,float s) {
    return exp(-.5*(x*x)/(s*s)); 
}
float gauss(vec2 v,float s) { return gauss(v.x,s)*gauss(v.y,s); }


float rndi(float i, float j) {
    return fract(1e5*sin(i+3.*j+0.567));
}


// --- complex Gabor ------------------------------
vec2 Gabor(vec2 pos, float freq, float a) {
    float t = (keyToggle(64+20))? 0. : iTime; // 'T'
    float g = gauss(pos, GABOR_SIZE);
    if (g < 1e-3) return vec2(0.);
    vec2 dir = m_isqrt2*(cos(a)*vec2(1.,1.)+ sin(a)*vec2(-1.,1.));
    float phi = freq*dot(pos,dir) * 2.* m_pi + 10.*t;

    return g*vec2(cos(phi),sin(phi)); // NB: only the real part is used, classically
}

// --- complex Gabor noise = kernel * point distrib
vec2 GaborNoise(vec2 uv, vec2 F, vec2 O) {
    const uint seed = uint(0);
    uint prng = wang_hash(seed);
    vec2 f=vec2(0.);
	for (float i=0.; i<GABOR_NB_KERNELS; i++) { 
		vec2 pos = vec2(1.8*rndi(i,0.),-1.+rndi(i,1.)*2.);
        float a = myrand_uniform_m_M(prng, O.x, O.y);
        float F = myrand_uniform_m_M(prng, F.x, F.y);
		f += Gabor(uv-pos, F, a);
	}
	return f *sqrt(200./GABOR_NB_KERNELS); // /6.;
}

