#define MAX_STEPS 1024
#define MIN_DIST 0.01
#define MAX_DIST 100.0

float getDist(vec3 p) {
    vec4 s = vec4(0, 1, 6, 1);

    float sphereDist = length(p - s.xyz) - s.w;
    float planeDist = p.y;

    return min(sphereDist, planeDist);
}

float rayMarch(vec3 orig, vec3 dir) {
   	vec3 curPoint;

    float dist = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
        curPoint = orig + dir * dist;

        float minDist = getDist(curPoint);

        dist += minDist;

        if (minDist < MIN_DIST || minDist > MAX_DIST) break;
    }

    return dist;
}

vec3 getNormal(vec3 p) {
    float d = getDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        getDist(p - e.xyy),
        getDist(p - e.yxy),
        getDist(p - e.yyx));

    return normalize(n);
}

float getLight(vec3 p) {
    vec3 lightPos = vec3(0, 4, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime))*2.;
    vec3 l = normalize(lightPos - p);
    vec3 n = getNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = rayMarch(p+n*MIN_DIST*2., l);
    if (d < length(lightPos - p)) dif *= .5;

    return dif;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // center coords
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 pos = vec3(0, 1, 0);
    vec3 dir = normalize(vec3(uv.x, uv.y, 1));
    float d = rayMarch(pos, dir);
    //d /= 10.;

    vec3 p = pos + dir * d;

    float dif = getLight(p);
	col = vec3(dif);

    // Output to screen
    fragColor = vec4(col, 1.);
}

float circle(vec2 uv, vec2 center, float min, float max)
{
    return (1. - smoothstep(min, max, length(uv - center)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.*iResolution.xy)/iResolution.y;
    uv.y -= 0.5;


    vec2 polar = vec2(length(uv), atan(uv.x / uv.y) * 1.14);
    //uv = polar;


    uv *= 15.;

    vec3 col = vec3(0.);

    float numCircles = cos(iTime) * 50.  + 55.;

    for(float i = 0.; i < numCircles; i = i + 1.){
          vec2 temp = uv;
          temp.x -= i*0.7 + 5.;
          vec3 color = vec3(0.8, 0.6, i / numCircles);
          float circ = circle(temp, vec2(sin(iTime * 7.1 - i*0.3), cos(iTime * 6. - i*0.3)), 0.1, 0.13);
          col += circ * color;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

