    // ç”¨å‰ä¹˜è®¡ç®—å¹³è¡Œå››è¾¹å½¢é¢ç§¯
    float s = abs(OA.y*OB.x-OA.x*OB.y);// æ€»é¢ç§¯
    float s2 = abs(OC.y*OB.x-OC.x*OB.y) / s;
    float s3 = abs(OC.y*OA.x-OC.x*OA.y) / s;
    //float s1 = 1.0 - s2 - s3;
    float s1 = abs(AC.y*AB.x-AC.x*AB.y) / s;

    vec3 col = c1*s1 + c2*s2 + c3*s3;
    float t = 1.0-smoothstep(1.0, 1.02, s1+s2+s3);// åœ¨ä¸‰è§’å½¢é‡Œä¸º1
