/*
    Rocket planet
    -------------

    Again the magic of domain repetition.

    Related references

    IQ Distance functions
    https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
    https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm    

    RayMarching starting point - BigWIngs
    https://shadertoy.com/view/WtGXDD

    Iridescent crystals - tdhooper
    https://www.shadertoy.com/view/llcXWM

*/

#define PHI (1.618033988749895)
#define PI acos(-1.)
#define TAU 6.283185
#define MAX_STEPS 100
#define MAX_DIST 10.
#define SURF_DIST .001
#define S smoothstep


// Nearest icosahedron vertex and id
float quadrant(float a, float b) {
	return ((sign(a) + sign(b) * 2.) + 3.) / 2.;
}
vec4 icosahedronVertex(vec3 p) {
    vec3 v1, v2, v3, result, plane;
    float id;
    float idMod = 0.;
	v1 = vec3(sign(p.x) * PHI,sign(p.y) * 1.,0);
	v2 = vec3(sign(p.x) * 1.,0,sign(p.z) * PHI);
	v3 = vec3(0,sign(p.y) * PHI,sign(p.z) * 1.);
    plane = normalize(cross(
        mix(v1, v2, .5),
        cross(v1, v2)
    ));
    if (dot(p, plane) > 0.) {
    	result = v1;
        id = quadrant(p.y, p.x);
    } else {
    	result = v2;
        id = quadrant(p.x, p.z) + 4.;
    }
    plane = normalize(cross(
        mix(v3, result, .5),
        cross(v3, result)
    ));
    if (dot(p, plane) > 0.) {
    	result = v3;
        id = quadrant(p.z, p.y) + 8.;
    }
    return vec4(normalize(result), id);
}

// --------------------------------------------------------
// http://math.stackexchange.com/a/897677
// --------------------------------------------------------

mat3 orientMatrix(vec3 A, vec3 B) {
    mat3 Fi = mat3(
        A,
        (B - dot(A, B) * A) / length(B - dot(A, B) * A),
        cross(B, A)
    );
    mat3 G = mat3(
        dot(A, B),              -length(cross(A, B)),   0,
        length(cross(A, B)),    dot(A, B),              0,
        0,                      0,                      1
    );
    return Fi * G * inverse(Fi);
}

// https://iquilezles.org/www/articles/noacos/noacos.htm
mat3 rotationAlign( vec3 d, vec3 z )
{
    vec3  v = cross( z, d );
    float c = dot( z, d );
    float k = 1.0f/(1.0f+c);

    return mat3( v.x*v.x*k + c,     v.y*v.x*k - v.z,    v.z*v.x*k + v.y,
                   v.x*v.y*k + v.z,   v.y*v.y*k + c,      v.z*v.y*k - v.x,
                   v.x*v.z*k - v.y,   v.y*v.z*k + v.x,    v.z*v.z*k + c    );
}


vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}


mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

//-------------------------------------------------
// https://www.shadertoy.com/view/4lyfzw
vec2 opRevolution( in vec3 p, float w )
{
    return vec2( length(p.xz) - w, p.y );
}
float opExtrussion( in vec3 p, in float sdf, in float h )
{
    vec2 w = vec2( sdf, abs(p.z) - h );
  	return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

// http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdVesica(vec2 p, float r, float d)
{
    p = abs(p);

    float b = sqrt(r*r-d*d); // can delay this sqrt
    return ((p.y-b)*d > p.x*b) 
            ? length(p-vec2(0.0,b))
            : length(p-vec2(-d,0.0))-r;
}
float sdCircle(vec2 p, float r)
{
    return length(p)-r; 
}
float sdCylinder( vec3 p, vec3 c )
{
  return length(p.xz-c.xy)-c.z;
}

// AKA ModPolar !
vec3 opRepRoundabout(vec3 p,float radius,float sectors) {
    float angle = TAU/sectors;
    float sector = round(atan(p.z,p.x)/angle); // thanks to IQ video https://youtu.be/sl9x19EnKng?t=1745
    p.xz *= Rot(-angle*sector);
    p.x -= radius;
    return p;
}

// http://iquilezles.org/www/articles/smin/smin.htm
float smax( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*0.25/k;
}


float sdRocket( vec3 pos)
{
	vec3 q=pos;
	float d = -0.03+abs(-0.1 + sdVesica(opRevolution(q,0.0), 1.4, 1.1 ));  // ABS for onioning
    // porthole
    // hole with cylinder
    d = max(d,-max(-q.z,sdCylinder(q.yzx, vec3(0.,0.,0.15))));
    // 3 sectors for engines
	q = opRepRoundabout(pos,0.,3.);
    d = min(d, -0.05 + sdVesica(opRevolution(vec3(q.x-.6,q.y+.8,q.z),0.0), 0.65, 0.5 )) ;
    // cut the bottom
	d = smax( d, -pos.y-1.1, 0.05 );
    // link between engines and rocket's body
	q += vec3(0.1,1.13,0.0);
    float lnd = sdCircle(q.xy, 1.00 );
        lnd = max(lnd,-sdCircle(q.xy+vec2(-0.3,0.3),0.6));
        lnd = max(lnd,-sdCircle(q.xy+vec2(0.8,-1.1),1.3));
        lnd = max(lnd,q.x-0.8);
        lnd = max(lnd,-q.y+0.2);
    d = min(d,opExtrussion (q,lnd,0.02)-0.01);
    return d;
}


float sdCone( vec3 p, vec2 c )
{
    // c is the sin/cos of the angle
    vec2 q = vec2( length(p.xz), p.y );
    float d = length(q-c*max(dot(q,c), 0.0));
    return d * ((q.x*c.y-q.y*c.x<0.0)?-1.0:1.0);
}

vec4 getRocketPath(float key) {
	float h=2.*S(1.,2.,key)*S(10.,9.,key);
	float r=S(1.2,3.,key)*S(9.8,8.,key);
	float a = 3.*TAU*S(1.2,9.5,key);
	return vec4(0.,h+r*sin(a),r*cos(a),a);    
}

vec3 animateRocket(vec3 p,float time) {
    time*=.3;
    vec3 q=p;
    float key = 20.*fract(time);
	float key2 =  20.*fract(time+.01);
	vec4 p1 = getRocketPath(key);
	q-=p1.xyz;
	q.y-=1.05;
    q.yz*=Rot(p1.w);
	return q;   
}


vec2 GetDist(vec3 p) {
    float time = iTime;
    p.xz *= Rot(time*.01*6.2831);        
    p.yz *= Rot(time*.005*6.2831);        
	time=time*.2;
	vec3 q=p;
    vec4 ico = icosahedronVertex(q);
    float id = ico.w;
    q*=transpose(rotationAlign(ico.xyz,vec3(0.,1.,0.)));
    float rocket = sdRocket(3.*animateRocket(q,time+id))*.333;
    float earth = length(p)-.7;
	float c = -sdCone(q,vec2(sin(PI/6.),cos(PI/6.)));   // disance to domain bouding cone 
    float d=min(rocket,earth);    
    d=min(d,max(c,.5));  // this is trying to slow down the ray marcher when approaching the bound of the cone over the face of the dode
    return vec2(d,id);
}

vec2 RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;    
    vec2 m;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        m = GetDist(p);
        float dS = m.x;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }    
    return vec2(dO,m.y);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);
    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    vec3 ro = vec3(0, 3, -3);
    if ( iMouse.x != 0. ) {
        vec2 mo = 2.*iMouse.xy/iResolution.xy-vec2(1.);
        ro.yz *= Rot(-mo.y);
        ro.xz *= Rot(-mo.x*6.2831);
    }
    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);
    vec2 m = RayMarch(ro, rd);
    float d=m.x;

    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;
    	vec3 n = GetNormal(p);
        vec3  sun_lig = normalize( vec3(0.2, 0.35, -0.5) );        
    	float dif = clamp(dot( n, sun_lig ), 0.0, 1.0 )+.1;
        vec3  sun_hal = normalize( sun_lig-rd );
		float sun_spe = pow(clamp(dot(n,sun_hal),0.0,1.0),8.0)*dif*(0.04+0.96*pow(clamp(1.0+dot(sun_hal,rd),0.0,1.0),5.0));
        float sun_sha = step(-RayMarch(p+0.01*n, sun_lig).x,-MAX_DIST);
        col += 1.*sun_spe*vec3(8.10,6.00,4.20)*sun_sha;
        vec3 c = hsv2rgb(vec3(m.y/12.+fract(iTime*.05),0.8,.9)); // nice ?
    	col += dif*c*(sun_sha*.9+.1);  
    } else { col = vec3(.1)*(.5-abs(uv.y));}
    col = pow(col, vec3(.4545));	// gamma correction
    fragColor = vec4(col,1.0);
}

// Shadertoy implementation of "Segment Tracing using Local Lipschitz Bounds" - Eurographics 2020
// Eric Galin, Eric GuÃ©rin, Axel Paris, Adrien Peytavie
// Paper:   https://hal.archives-ouvertes.fr/hal-02507361/document
// Video:	https://www.youtube.com/watch?v=NOinlrHyieE&feature=youtu.be
// Talk:	https://www.youtube.com/watch?v=KIOSbWNu-Ms&feature=youtu.be
// Github:  https://github.com/aparis69/Segment-Tracing
//
// Sphere tracing on the left - Segment tracing on the right.
// You can move the sliders with the mouse.
// MIT License

const int StepsMax  = 150;  // Maximum step count for sphere & segment tracing
const float Epsilon = 0.1;  // Marching epsilon
const float T       = 0.5;  // Surface threshold.

const float ra      = 20.0; // Ray start interval
const float rb      = 60.0; // Ray end interval
const float radius  = 8.0;  // Primitive radius
const float kappa   = 2.0;  // Segment tracing factor for next candidate segment

// Transforms
vec3 RotateY(vec3 p, float a)
{
    float sa = sin(a);
    float ca = cos(a);
    return vec3(ca * p.x + sa * p.z, p.y, -sa * p.x + ca * p.z);
}

// Cubic falloff
// x: distance
// R: radius
float Falloff(float x, float R)
{
    float xx = clamp(x / R, 0.0, 1.0);
    float y = (1.0 - xx * xx);
    return y * y * y;
}

// Computes the global lipschitz bound of the falloff function
// e: energy
// R: radius
float FalloffK(float e, float R)
{
    return e * 1.72 * abs(e) / R;
}

// Computes the local lipschitz bound of the falloff function
// a: value at first bound
// b: value at second bound
// R: radius
// e: energy
float FalloffK(float a, float b, float R, float e)
{
    if (a > R)
        return 0.0;
    if (b < R / 5.0)
    {
        float t = (1.0 - b / R);
    	return abs(e) * 6.0 * (sqrt(b) / R) * (t * t);
    }
	else if (a > (R * R) / 5.0)
    {
        float t = (1.0 - a / R);
    	return abs(e) * 6.0 * (sqrt(a) / R) * (t * t);
    }
    else
        return FalloffK(e, R);
}

// Primitives
// Point primitive field function
// p: world point
// c: center
// R: radius
// e: energy
float Vertex(vec3 p, vec3 c, float R, float e)
{
  	return e * Falloff(length(p - c), R);
}

// Evaluates the local lipschitz bound of a point primitive over a segment [a, b]
// c: center
// R: radius
// e: energy
// a: segment start
// b: segment end
float VertexKSegment(vec3 c, float R, float e, vec3 a, vec3 b)
{
    vec3 axis = normalize(b - a);  
    float l = dot((c - a), axis);
    float kk = 0.0;
    if (l < 0.0)
    {
        kk = FalloffK(length(c - a), length(c - b), R, e);
    }
    else if (length(b - a) < l)
    {
        kk = FalloffK(length(c - b), length(c - a), R, e);
    }
    else
    {
        float dd = length(c - a) - (l * l);
        vec3 pc = a + axis * l;
        kk = FalloffK(dd, max(length(c - b), length(c - a)), R, e);
    }
    float grad = max(abs(dot(axis, normalize(c - a))), abs(dot(axis, normalize(c - b))));
    return kk * grad;
}

// Tree root
float Object(vec3 p)
{
    float I = Vertex(p, vec3(-radius / 2.0, 0, 0), radius, 1.0);
    I += Vertex(p, vec3(radius / 2.0, 0, 0), radius, 1.0);
    I += Vertex(p, vec3(radius / 3.0, radius, 0), radius, 1.0);
    return I - T;
}

// K root
float KSegment(vec3 a, vec3 b)
{
    float K = VertexKSegment(vec3(-radius / 2.0, 0, 0), radius, 1.0, a, b);
    K += VertexKSegment(vec3(radius / 2.0, 0, 0), radius, 1.0, a, b);
    K += VertexKSegment(vec3(radius / 3.0, radius, 0), radius, 1.0, a, b);
    return K;
}
float KGlobal()
{
    return FalloffK(1.0, radius) * 3.0;
}

// Normal evaluation
vec3 ObjectNormal(in vec3 p )
{	
    float eps = 0.001;
    float v = Object(p);
    vec3 n;
    n.x = Object(vec3(p.x + eps, p.y, p.z)) - v;
    n.y = Object(vec3(p.x, p.y + eps, p.z)) - v;
    n.z = Object(vec3(p.x, p.y, p.z + eps)) - v;
    return normalize(n);
}

// Trace ray using sphere tracing
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float SphereTracing(vec3 o, vec3 u, out bool h, out int s)
{
    float kGlobal = KGlobal();
    float t = ra;
  	h = false;	
    s = 0;
  	for(int i = 0; i < StepsMax; i++)
    {
        vec3 p = o + t * u;
        float v = Object(p);
        s++;

        // Hit object
        if (v > 0.0)
        {
            h = true;
            break;
        }

        // Move along ray
        t += max(Epsilon, abs(v) / kGlobal);

        // Escape marched far away
        if (t > rb)
            break;
	}
    return t;
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float SegmentTracing(vec3 o, vec3 u, out bool h, out int s)
{
    float t = ra;
  	h = false;   
    float candidate = 1.0;
    for(int i = 0; i < StepsMax; i++)
    {
        s++;
        vec3 p = o + t * u;
        float v = Object(p);

        // Hit object
        if (v > 0.0)
        {
            h = true;
            break;
        }

        // Lipschitz constant on a segment
        float lipschitzSeg = KSegment(p, o + (t + candidate) * u);

        // Lipschitz marching distance
        float step = abs(v) / lipschitzSeg;

        // No further than the segment length
        step = min(step, candidate);

        // But at least, Epsilon
        step = max(Epsilon, step);

        // Move along ray
        t += step;

        // Escape marched far away
        if (t > rb)
            break;

        candidate = kappa * step;
	}
    return t;
}

// Shading functions
vec3 Background(vec3 rd)
{
    const vec3 C1 = vec3(0.8, 0.8, 0.9);
	const vec3 C2 = vec3(0.6, 0.8, 1.0); 
	return mix(C1, C2, rd.y * 1.0 + 0.25);
}
vec3 Shade(vec3 p, vec3 n)
{
    const vec3 l1 = normalize(vec3(-2.0, -1.0, -1.0));
    const vec3 l2 = normalize(vec3(2.0, 0.0, 1.0));
    float d1 = pow(0.5 * (1.0 + dot(n, l1)), 2.0);
    float d2 = pow(0.5 * (1.0 + dot(n, l2)), 2.0);
    return vec3(0.6) + 0.2 * (d1 + d2)* Background(n);
}
vec3 ShadeSteps(int n)
{
    const vec3 a = vec3(97, 130, 234) / vec3(255.0);
    const vec3 b = vec3(220, 94, 75) / vec3(255.0);
    const vec3 c = vec3(221, 220, 219) / vec3(255.0);
    float t = float(n) / float(StepsMax);   
    if (t < 0.5)
        return mix(a, c, 2.0 * t);
    else
        return mix(c, b, 2.0 * t - 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{         
    // Compute ray origin and direction
  	vec2 pixel = (gl_FragCoord.xy / iResolution.xy) * 2.0 - 1.0;
  	float asp = iResolution.x / iResolution.y;
  	vec3 rd = normalize(vec3(asp * pixel.x, pixel.y - 1.5, -4.0));
  	vec3 ro = vec3(0.0, 18, 40.0);
    vec2 mouse = (iMouse.xy / iResolution.xy) * 2.0 - 1.0;
    if (mouse.y <= -0.9999) // show cost at frame 0
        mouse.xy = vec2(0.0);

    float a = (iTime * 0.25);
    ro = RotateY(ro, a);
    rd = RotateY(rd, a);

    // Trace ray
    bool hit;   // Ray hit flag
    int s;      // Number of steps
    float t;    // Ray hit position
    float sep = mouse.x;

    // Sphere tracing on the left
   	if (pixel.x < sep)
    	t = SphereTracing(ro, rd, hit, s); 
    // Segment tracing on the right
    else
        t = SegmentTracing(ro, rd, hit, s);

    // Shade this with object
    vec3 rgb = Background(rd);
    if (pixel.y > mouse.y)
    {
        if (hit)
        {
            vec3 pos = ro + t * rd;    
            vec3 n = ObjectNormal(pos);
            rgb = Shade(pos, n);
        }       
    }
    else
    {
        rgb = ShadeSteps(s);
    }    
    rgb *= smoothstep(1.0, 2.0, abs(pixel.x-sep)/(2.0 / iResolution.x));
    rgb *= smoothstep(1.0, 2.0, abs(pixel.y-mouse.y)/(2.0 / iResolution.y));
    fragColor = vec4(rgb, 1.0);
}

// Created by David Gallardo - xjorma/2020
