const int	nbBall = 8;
const float ballRadius = 0.07;
const float maxLateralSpeed = 1.;
const float Tau = radians(360.);
const float minPhaseSpeed = 2.1;
const float maxPhaseSpeed = 3.2;
const float sinAmplitude = 1.2;
const vec3	drawColor = vec3(0.2,0.3,0.4);

// Hash without Sine, https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float udSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 ba = b-a;
    vec2 pa = p-a;
    float h =clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length(pa-h*ba);
}

float triangleSignal(float x, float f)
{
    f = 1. / f;
    return (abs((f * x - 4. * floor(0.25 * f * x)) - 2.) - 1.) / f;
}

float smoothFilter(float d)
{
    float v = 2. / iResolution.y;
    return smoothstep(v, -v, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2	p = (2. * fragCoord - iResolution.xy) / iResolution.y;
    float	ratio = iResolution.x / iResolution.y;
    vec3	col = vec3(0);

    // Compute and draw balls
    vec2	ballArray[nbBall + 1];
    for(int i = 0; i < nbBall; ++i)
    {
        float LateralSpeed = (hash11(float(i) * 1.2544) - 0.5) * 2. * maxLateralSpeed;
        float phaseOffset = hash11(float(i) * 1.4482) * Tau;
        float LateralOffset = hash11(float(i) * 1.9528) * (ratio * 2. - 2. * ballRadius);
        float phaseSpeed = mix(minPhaseSpeed, maxPhaseSpeed, hash11(float(i) * 1.1273));
        ballArray[i] = vec2(	triangleSignal(LateralOffset + LateralSpeed * iTime, ratio - ballRadius),
                       			sinAmplitude * (abs(sin(phaseOffset + phaseSpeed * iTime)) - 0.5) 
                           );
        float a = smoothFilter(sdCircle( ballArray[i] - p , ballRadius));
        col = mix(col, drawColor, a);           
    }

    // Sort Ball in X (Lame bubble)
    for(int i = 0; i < nbBall - 1; ++i)
    {
	    for(int j = 0; j < nbBall - 1 - i; ++j)
        {
            if(ballArray[j].x > ballArray[j + 1].x)
            {
                // templess swap, not sure it's a good idea, I didn't profile it.
                ballArray[j] += ballArray[j + 1];
                ballArray[j + 1] = ballArray[j] - ballArray[j + 1];
                ballArray[j] -= ballArray[j + 1];
            }
        }
    }

   	// Find convex using an algorithm similar to gift wraping
 	ballArray[nbBall] = vec2(ratio, 0);
    vec2 convexHull[nbBall + 2];			// Worst case size
    int  convexSize = 0;
    convexHull[convexSize++] = vec2(-ratio, 0);

    int	  SmallestIndex = 0;
    for(;;)
    {
        float SmallestDir = 1.;
        bool  found = false;
        for(int i = SmallestIndex; i < nbBall + 1; ++i)
        {
            vec2 dir = normalize(ballArray[i] - convexHull[convexSize - 1]);
            if(dir.y < SmallestDir)
            {

                SmallestDir = dir.y;
                SmallestIndex = i;
                found = true;
            }
        }
       	if(found)
        {
 	   		convexHull[convexSize++] = ballArray[SmallestIndex];
        }
        else
        {
            break;
        }
    }


    // Draw hull
    for(int i = 0; i < convexSize - 1; ++i)
    {
	    float a = smoothFilter(udSegment(p, convexHull[i] - vec2(0, ballRadius), convexHull[i + 1] - vec2(0, ballRadius)) - 0.005);
        col = mix(col, drawColor, a);

    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates: 0 (bottom left) to 1 (upper right)
    vec2 ouv = fragCoord/iResolution.xy;
    float r = iResolution.x/iResolution.y;

    // me grid
	float g1 = 1./9.;
    float g2 = 2./9.;
    float g3 = 3./9.;
    float g4 = 4./9.;
    float g5 = 5./9.;
    float g6 = 6./9.;
    float g7 = 7./9.;
    float g8 = 8./9.;
    float g9 = 9./9.;
    float a = .205;

    vec2 uv = ouv;
    uv.x *= r;
    uv.x -= g4;

    // rotating vector over time
    vec2 q = .5+.5*vec2(cos(iTime),sin(iTime));

    // color mix
    vec3 col = mix(vec3(1,1,.3),vec3(.8,.1,0),length(ouv-q)); // smoothstep removed

    // mask: mult color by qty one minus zero to one using step() to form letters
    // T
    col *= 1.-(step(g5, uv.y))*(1.-step(g8, uv.y))*(1.-step(g1, uv.x));
    col *= 1.-(step(g2, uv.x))*(step(g5, uv.y))*(1.-step(g3, uv.x))*(1.-step(g8, uv.y));
    // A
    col *= 1.-(step(g3, uv.x))*(step(g5, uv.y))*(1.-step(g2, uv.x-a*uv.y));
    col *= 1.-(1.-step(g6, uv.x))*(step(g5, uv.y))*(step(g7, uv.x+a*uv.y));
    col *= 1.-(step(g3, uv.x))*(step(g6, uv.y))*(1.-step(g6, uv.x+a*uv.y))*(step(g3, uv.x-a*uv.y));
    // L
    col *= 1.-(step(g7, uv.x))*(step(g6, uv.y));

    // gaps
    col *= 1.-(step(g4, uv.y))*(1.-step(g5, uv.y));
    col *= 1.-(step(-1., uv.x))*(1.-step(.01, uv.x))*(1.-step(g4, uv.y));
    col *= 1.-(step(g3, uv.x))*(1.-step(g3+.01, uv.x))*(1.-step(g4, uv.y));

    // F
    col *= 1.-(step(g1, uv.x))*(step(g2, uv.y))*(1.-step(g3, uv.x))*(1.-step(g3, uv.y));
    col *= 1.-(step(g1, uv.x))*(1.-step(g1, uv.y))*(1.-step(g3, uv.x));
    // R
    col *= 1.-(step(g4, uv.x))*(1.-step(g4, uv.y))*(1.-step(g3+g2*abs(cos(.1+uv.y*10.)), uv.x));
    col *= 1.-(step(g5+g1*abs(cos(.1+uv.y*10.)), uv.x))*(1.-step(g6, uv.x))*(1.-step(g4, uv.y));
    // I
    col *= 1.-(step(g6, uv.x))*(1.-step(g7, uv.x))*(1.-step(g5, uv.y));
    col *= 1.-(1.-step(g5, uv.y))*(step(g8, uv.x));

    // Output to screen
    fragColor = vec4(col,1.0);
}

// based on...
// https://www.shadertoy.com/view/lssfW2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 curve_p = vec2(0,200);
    float d = 10000.0 + 4200.*sin(iTime)+4200.*cos(iTime);
    float di = 2.;// +3.2*sin(iTime * .1);
    for(int i = 0;i<300;i++)
    {
        di = float(i)*.3;
        curve_p.x = .5-.3*sin(.3*iTime)-.05*float(di)*cos(float(di)+.4*iTime); // +.01*float(di)*sin(4.0*di+.2*iTime) + .03*sin(iTime*.1)*float(di)*sin(7.0*di+.4*iTime);
        curve_p.y = .5-.3*cos(.3*iTime)-.05*float(di)*sin(float(di)+.2*iTime); // +.01*float(di)*cos(4.0*di+.2*iTime) + .03*sin(iTime*.1)*float(di)*cos(7.0*di+.4*iTime);
        d = sin(min(d*1.0, 1.*sin(length(p-curve_p))));
        //d = length(p-curve_p)/(di+1.0);

    }
    float r =.05*d + .05*(.05 - .015*pow(dot(sin(p*p + d*d), log(20.0+2.*curve_p -d)),2.0));
    float g = .0008 + .005*cos(.001*d*d*d+3.1*length(p)); //+.03*dot(p,curve_p + .0005*vec2(iTime, iTime));
    float b=2.5*abs(.5+.3*cos(iTime*.8 - 2.5*pow(d,2.0))); // +.01*(dot(p,curve_p - .005*vec2(iTime-200.05, iTime-200.05)+10.0));
    if (d < 2.2)
    {
        b = .0 +.04*(.5+ .5*sin(iTime*.1 + 1.28*dot(p,curve_p)));
    }
    float checkers = mod(float(uv.x*uv.x +uv.y*uv.y)+r*r*1200.+b*20., .25);
    float checkers_02 =mod(float( -.01*curve_p.x*curve_p.y)+g*g*1000.,.25);
    if (checkers + checkers_02 > .25)
    {
        r = 0.06*r;
        g = 1.2*g;
        b = 1.2*b;
    }
    vec3 col = vec3(r, g, b);
    float dn = col.x + col.y + col.z;
    col /= dn;
    // Output to screen
    fragColor = vec4(col,1.0);
}

