#define AA 2

float pi = 3.14159265, tau = 6.2831853;

float box (in float x, in float x1, in float x2, in float a, in float b) {
	return tanh(a * (x - x1)) + tanh(-b * (x - x2));
}
float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float ex (in float z, in float z0, in float s) {
    return exp(-(z - z0) * (z - z0) / s);
}

float r(in float z, in float x) {
    float s = sin (tau * x), c = cos(tau * x),
		c2 = cos (pi * x), t4z = tanh(4. * z);
    return /* body */.4 * (1.0 - .4 * ex(z, .8, .15) +
		s * s + .6 * ex(z, .8, .25) * c * c + .3 * c) *
		0.5 * (1. + t4z) + /* legs */ (1. - .2 * ex(z, -1.3, .9)) *
		0.5 * (1. - t4z) * (.5 * (1. + s * s + .3 * c) *
		(pow(abs(s), 1.3) + .08 * (1. + t4z) ) ) +
		/* improve butt */ .13 * box(c2, -.45, .45, 5., 5.) *
		box(z, -.5, .2, 4., 2.) - 0.1 * box(c2, -.008, .008, 30., 30.) *
		box(z, -.4, .25, 8., 6.) - .05 * pow(abs(sin(pi * x)), 16.) * box(z, -.55, -.35, 8., 18.);
}

// $1M question: how close are we to ParametricPlot3D[...] surface?
float sd( in vec3 p )
{
	/* shift butt belly */
	float dx = .1 * exp(-pow((p.z-.8),2.)/.6) - .18 * exp(-pow((p.z -.1),2.)/.4);

    // on the surface, we have:
    // p.x = r * cos + dx
	// p.y = r * sin

    float jiggle = p.z*1.75;
    float jsize = 0.5;
    if (iMouse.z>0.5)
    {
        jiggle+=p.y*.5;
        jsize+=.175;
    }

	dx *= 1.5+(sin(jiggle+iTime*9.65)*jsize);

	float angle = atan(p.y, p.x - dx);
	float r_expected = r(p.z, angle / tau);
//	float d1 = (.5 + .5 * smoothstep(.4,1.,p.z)) * (length(vec2(p.y, p.x - dx)) - r_expected);
	float d1 = (length(vec2(p.y, p.x - dx)) - r_expected)*0.5;

    p.x -= dx;
	float d2 = sdSphere(p+vec3(-0.35,0.4,-1.875),0.4);
	float d3 = sdSphere(p+vec3(-0.35,-0.4,-1.875),0.4);
    d2 = smin(d2,d3,0.35);
    return smin(d1,d2,0.07);
    //return min(d1,min(d2,d3));
}


float map( in vec3 pos )
{
    return sd (pos.zxy);
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

float calcAO(in vec3 pos, in vec3 nor)
{
	float sca = 2.0, occ = 0.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + float(i)*0.5/4.0;        
        float dd = map(nor * hr + pos);
        occ += (hr - dd)*sca;
        sca *= 0.7;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an =2.+sin(iTime+2.15)*0.7;	//0.5*iTime - 0.8;
    // if (iMouse.z>0.5)
        //an+=iMouse.x/(iResolution.x*0.25);
    //an+=3.14;

	vec3 ro = vec3( 2.55*sin(an), 0.5, 2.55*cos(an) );
    vec3 ta = vec3( 0.0, .8, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    vec3 tot = vec3(0.0);


    vec2 p1 = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
    float val = sin(iTime+p1.x*3.0+p1.y*20.0)*(0.8+sin(p1.y*24.4+iTime*3.0)*0.3);
    val = clamp(pow(abs(val),2.6),0.0,2.0);
    vec3 bcol = vec3(val*0.6,val*0.1,val*0.9);
	bcol *= length(p1*p1*p1);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

        vec3 col = bcol;
        // raymarch
        if (abs(p.x)<0.8)
        {
            // create view ray
            vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

            const float tmax = 5.0;
            float t = 0.0;
            for( int i=0; i<200; i++ )
            {
                vec3 pos = ro + t*rd;
                float h = map(pos);
                if( h<0.0001 || t>tmax ) break;
                t += h;
            }
            // shading/lighting	
            if( t<tmax )
            {
                vec3 pos = ro + t*rd;
                vec3 nor = calcNormal(pos);

		float ao = calcAO(pos,nor);

		    vec3 ln = normalize(vec3(1.1,-.52,-.4));

	        float spec = pow(max( dot( reflect(-ln, nor), -rd ), 0.0 ), 20.0);

                float dif = clamp( dot(nor,ln), 0.0, 1.0 );
                float amb = 0.25 + 0.5*dot(nor,vec3(.2,0.2,0.2));
                col = vec3(0.4,0.05,0.3)*amb + (vec3(1.1,0.6,0.4)*2.0)*dif*ao;
		    col *= 1.1+spec;
            }
        }

        // gamma        
        //col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif
    // gamma        
    tot = sqrt( tot );

   	vec2 qq = fragCoord.xy / iResolution.xy;
    tot *= 0.5 + 0.5*pow( 16.0*qq.x*qq.y*(1.0-qq.x)*(1.0-qq.y), 0.95 );
	fragColor = vec4( tot, 1.0 );
}

#define pi 3.1415926

#define iterations 16

float mandelbrot(vec2 p)
{
    vec2 c = 4.0 * p - 1.0;
    c.x -= 1.0;
    vec2 z = vec2(0.0, 0.0);

    float h = 1.0;
    int i = 0;
    for(i = 0; i < iterations; i++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        if(length(z) > 2.0)
        {
            h = float(i) / float(iterations);
            break;
        }
    }

    return h;
}

vec2 lookup(vec2 p)
{
    return (p + vec2(iTime, 0.0));
}

float heightmap(vec2 p)
{
    vec2 l = lookup(p);
    //return 0.5 * (0.5 + 0.5 * sin(l.x * 2.0 * pi) * sin(l.y * 2.0 * pi)); // waves
    //return 0.5 * (1.0 - abs(sin(l.x * 2.0 * pi)) * abs(sin(l.y * 2.0 * pi))); // waffles
    //return 0.5 * ((1.0 - abs(sin(l.x * 2.0 * pi))) * (1.0 - abs(sin(l.y * 2.0 * pi)))); // spikes
    return 0.5 * mandelbrot(fract(l));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 d = vec3(2.0 * fragCoord/iResolution.xy - 1.0, 1.0);

    fragColor = vec4(0.0, 0.0, 0.5, 1.0);

    float s = 0.001;
    s /= abs(d.y); // larger step-size for shallow rays
    for(vec3 q = vec3(0.0); abs(q.y) < 1.0; q += d * s)
    {
        float c = heightmap(q.xz);
        float h = 1.0 * c - 1.0;
        if(q.y < h)
        {
            float W = heightmap(q.xz + vec2(-1.0,0.0)/iResolution.xy);
            float O = heightmap(q.xz + vec2(+1.0,0.0)/iResolution.xy);
            float S = heightmap(q.xz + vec2(0.0,-1.0)/iResolution.xy);
            float N = heightmap(q.xz + vec2(0.0,+1.0)/iResolution.xy);
            vec3 n = normalize(vec3(O - W, N - S, 0.01));
            //fragColor = vec4(c);
            //fragColor = vec4(n.z);
            float brightness = dot(n, normalize(vec3(1.0, 0.0, 1.0))); // lighting
            fragColor = vec4(brightness); 
            break;
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = iResolution.xy/2.;
    center.x = center.x + sin(iTime*6.)*iResolution.x*.03;
    center.y = center.y + cos(iTime*5.4)*iResolution.x*.033;
    float d = length(vec2(fragCoord.x,fragCoord.y) - center)/iResolution.x;
    float a = -atan(fragCoord.y-center.y,fragCoord.x-center.x)*6.;
    float r = (sin(a+3.14159*4./3.+4./d*1.+iTime*6.)*.5+.5)*d*2.;
    float g  = (sin(a+3.14159*2./3.+4./d*1.+iTime*6.)*.5+.5)*d*2.;
    float b = (sin(a+4./d*1.+iTime*6.)*.5+.5)*d*2.;
    float w = (sin(a*4.+3.14159*2./3.+3./d*1.+iTime*6.)*.5+.5)*1.*sin(a*7.);
    w=w>.6?3.:0.;
    w=w*d;
    fragColor = vec4(
        clamp(w+r,0.,1.),
        clamp(w+g,0.,1.),
        clamp(w+b,0.,1.),
        atan(5.-1./d)/3.14159*2.+4.);
}

float Hash21(in vec2 id) {
    vec2 n = id * fract(dot(id, vec2(534.75, 65.54)));
    return fract(dot(id + n, n));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = ((fragCoord - 0.5 * iResolution.xy) / iResolution.y) * 10.0 + iTime;
    vec2 cellLocation = floor(uv);
    vec2 cellLocalUV = fract(uv) - 0.5;
    cellLocalUV.x *= Hash21(cellLocation) < 0.5 ? -1.0 : 1.0;

    vec2 circleUV = cellLocalUV - (cellLocalUV.x + cellLocalUV.y < 0.0 ? -1.0 : 1.0) * 0.5;
    float circleDist = length(circleUV) - 0.5;
    vec3 color = vec3(smoothstep(0.3, 0.0, abs(circleDist)));

    fragColor = vec4(color, 1.0);
}

// "ShaderToy Tutorial - Value Noise" 
// by Martijn Steinrucken aka BigWings/CountFrolic - 2019
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
// This shader is part of a tutorial on YouTube
// https://youtu.be/zXsWftRdsvU

float N21(vec2 p) {
    return fract(sin(p.x*100.+p.y*6574.)*5647.);
}

float SmoothNoise(vec2 uv) {
    vec2 lv = fract(uv);
    vec2 id = floor(uv);

    lv = lv*lv*(3.-2.*lv);

    float bl = N21(id);
    float br = N21(id+vec2(1,0));
    float b = mix(bl, br, lv.x);

    float tl = N21(id+vec2(0,1));
    float tr = N21(id+vec2(1,1));
    float t = mix(tl, tr, lv.x);

    return mix(b, t, lv.y);
}

float SmoothNoise2(vec2 uv) {
    float c = SmoothNoise(uv*4.);

    // don't make octaves exactly twice as small
    // this way the pattern will look more random and repeat less
    c += SmoothNoise(uv*8.2)*.5;
    c += SmoothNoise(uv*16.7)*.25;
    c += SmoothNoise(uv*32.4)*.125;
    c += SmoothNoise(uv*64.5)*.0625;

    c /= 2.;

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv += iTime*.1;

    float c = SmoothNoise2(uv);

    vec3 col = vec3(c);

    fragColor = vec4(col,1.0);
}

vec2 uv_cover(in vec2 p, in vec2 rect) {
    vec2 uv = p / rect.x;
    uv.y += (1.0 - rect.y / rect.x) / 2.0;
    return uv;
}
vec2 uv_contain(in vec2 p, in vec2 rect) {
    vec2 uv = p / rect.y;
    uv.x += (1.0 - rect.x / rect.y) / 2.0;
    return uv;
}

const float PI = 3.141592653589793;

float safeAngle01(vec2 p, float offset) {

    float a = fract(atan(p.y, p.x) / PI * 0.5 + offset);
    return a < 0.0 ? a + 1.0 : a;
}

vec2 fromPolar(float angle, float radius) {

    return vec2(radius * cos(angle), radius * sin(angle));
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdSegment(in vec2 p, in vec2 a, in vec2 b) {

    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdSliceRing(vec2 p, float r, float r2, float opening, float openingOffset) {

    float angle01 = safeAngle01(p, -openingOffset);

    if (angle01 > opening) {
        float compOpening = 1.0 - opening;
        if (angle01 < opening + compOpening * 0.5) {
            float a = (opening + openingOffset) * 2.0 * PI;
	        return sdSegment(p, fromPolar(a, r - r2), fromPolar(a, r + r2));
        } else {
            float a = openingOffset * 2.0 * PI;
	        return sdSegment(p, fromPolar(a, r - r2), fromPolar(a, r + r2));
        }
    }

    float d = length(p) - r;
    d = abs(d) - r2;
    return d;
}

vec3 distanceToInigoColor(float d) {
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.8 + 0.2*cos(128.0*abs(d));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = uv_contain(fragCoord, iResolution.xy) - 0.5;

    float radius = .3 + .1 * sin(iTime);
    float opening = sin(iTime) * .5 + .5;
    float offset = fract(iTime * .02);
    float d = sdSliceRing(p, radius, 0.1, opening, offset);


    fragColor = vec4(distanceToInigoColor(d),1.0);
}

#define S(a,b,t) smoothstep(a,b,t)

mat2 Rot(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}


// Created by inigo quilez - iq/2014
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
vec2 hash( vec2 p )
{
    p = vec2( dot(p,vec2(2127.1,81.17)), dot(p,vec2(1269.5,283.37)) );
	return fract(sin(p)*43758.5453);
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    float n = mix( mix( dot( -1.0+2.0*hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                        dot( -1.0+2.0*hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                   mix( dot( -1.0+2.0*hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                        dot( -1.0+2.0*hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
	return 0.5 + 0.5*n;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x / iResolution.y;

    vec2 tuv = uv;
    tuv -= .5;

    // rotate with Noise
    float degree = noise(vec2(iTime*.1, tuv.x*tuv.y));

    tuv.y *= 1./ratio;
    tuv *= Rot(radians((degree-.5)*720.+180.));
	tuv.y *= ratio;


    // Wave warp with sin
    float frequency = 5.;
    float amplitude = 30.;
    float speed = iTime * 2.;
    tuv.x += sin(tuv.y*frequency+speed)/amplitude;
   	tuv.y += sin(tuv.x*frequency*1.5+speed)/(amplitude*.5);


    // draw the image
    vec3 colorYellow = vec3(.957, .804, .623);
    vec3 colorDeepBlue = vec3(.192, .384, .933);
    vec3 layer1 = mix(colorYellow, colorDeepBlue, S(-.3, .2, (tuv*Rot(radians(-5.))).x));

    vec3 colorRed = vec3(.910, .510, .8);
    vec3 colorBlue = vec3(0.350, .71, .953);
    vec3 layer2 = mix(colorRed, colorBlue, S(-.3, .2, (tuv*Rot(radians(-5.))).x));

    vec3 finalComp = mix(layer1, layer2, S(.5, -.3, tuv.y));

    vec3 col = finalComp;

    fragColor = vec4(col,1.0);
}

