//#define T(d)   hash(i+d)                                 // value noise
  #define T(d) ( .5+ .7*dot( d-F ,  2.*hash3(i+d)-1. ) )   // gradient noise

float noise(vec3 x) {  
    vec3 i = floor(x), 
         F = fract(x), e = vec3(1,0,0),
         f = smoothstep(0.,1.,F );
    vec4 T = mix ( vec4(T(e.zzz),T(e.zxz), T(e.zzx), T(e.zxx) ),
                   vec4(T(e.xzz),T(e.xxz), T(e.xzx), T(e.xxx) ),
                   f.x );
    vec2 v = mix( T.xz, T.yw, f.y);
    return mix(v.x,v.y,f.z);
        }

float add_noise(vec3 x) {  // --- regular Perlin noise
    float n = noise(x)/2.;  x *= 2.1; // return n*2.;
         n += noise(x)/4.;  x *= 1.9;
         n += noise(x)/8.;  x *= 2.3;
         n += noise(x)/16.; x *= 1.9;
         n += noise(x)/32.; x *= 2.1;
         n += noise(x)/64.;
    return n; 
}

float add_noise0(vec3 x) {  // --- cascade raw sum noise
    float i=0.,
         n =  noise(x)-.5; i++; x *= 2.1;  // return n*2.;
         n += noise(x)-.5; i++; x *= 1.9;
         n += noise(x)-.5; i++; x *= 2.3;
         n += noise(x)-.5; i++; x *= 1.9;
         n += noise(x)-.5; i++; x *= 2.1;
         n += noise(x)-.5; i++;
    return n/sqrt(i); 
}

float mul_noise(vec3 x) {   // --- multiplicative noise 
    float n = 2.*noise(x);  x *= 2.1;  // return n/2.;
         n *= 2.*noise(x);  x *= 1.9;
         n *= 2.*noise(x);  x *= 2.3;
         n *= 2.*noise(x);  x *= 1.9;
         n *= 2.*noise(x);  x *= 2.1;
         n *= 2.*noise(x);
    return n/2.; 
}

