	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

// noise and FBM
float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float a = textureLod(iChannel0,(p+vec2(0.5,0.5))/256.0,0.0).x;
	float b = textureLod(iChannel0,(p+vec2(1.5,0.5))/256.0,0.0).x;
	float c = textureLod(iChannel0,(p+vec2(0.5,1.5))/256.0,0.0).x;
	float d = textureLod(iChannel0,(p+vec2(1.5,1.5))/256.0,0.0).x;
    return mix(mix( a, b,f.x), mix( c, d,f.x),f.y);
}

const mat2 mtx = mat2( 0.85,  0.60, -0.60,  0.80 );

float fbm3( vec2 p )
{
    float f = 0.0;

    f += 0.8000*(-1.0+2.0*noise( p )); p = mtx*p*2.02;
    f += 0.250000*noise( p ); p = mtx*p*2.03;
    f += 0.5500*(-1.0+2.0*noise( p )); p = mtx*p*2.03;
    f += 0.1625*(-1.0+2.0*noise( p ));

    return f/1.8375;
}

float fbm7( vec2 p )
{
    float f = 0.0;

    f += 0.005000*noise( p ); p = mtx*p*2.02;
    f += 0.250000*noise( p ); p = mtx*p*2.03;
    f += 0.125000*noise( p ); p = mtx*p*2.01;
    f += 0.562500*noise( p ); p = mtx*p*2.04;
    f += 0.831250*noise( p ); p = mtx*p*2.01;
    f += 0.312500*noise( p ); p = mtx*p*2.02;
    f += 0.15625*noise( p );

    return f/5.41875;
}

vec2 fbm3_2( vec2 p )
{
    return vec2( fbm3(p+vec2(1.5)), fbm3(p+vec2(3.2)) );
}

vec2 fbm7_2( vec2 p )
{
    return vec2( fbm7(p+vec2(3.2)), fbm7(p+vec2(9.7)) );
}


// Domain Warping
float warp(vec2 q, out vec2 o, out vec2 n)
{
    // animate q here
    q += 0.1*sin(vec2(0.11,0.13)*.16*iTime + length( q )*4.0);
    q *= 0.8;

    o = 0.5 + 0.5*fbm3_2( q );

    o += 0.04*sin(vec2(0.11,0.11)*.1*iTime*length( o ));
	o *= 1.1;

    n = fbm7_2(.95*o);

    vec2 p = q + 2.0*n + 1.0;

    float f = 0.5 + 0.5*fbm3(2.0*p);

    f = mix(f, f*f*f*3.5, f*abs(n.x));

    f *= 1.0-0.5*pow( 0.5+0.5*sin(8.0*p.x)*sin(8.0*p.y), 8.0 );

    return f;
}

float warps( in vec2 q )
{
    vec2 t1, t2;
    return warp(q,t1,t2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 tot = vec3(0);
    for( int mi=0; mi<4; mi++ )
    for( int ni=0; ni<4; ni++ )
    {
    vec2 q = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 o, n;

    float f = warp(q, o, n);

    vec3 col = vec3(0.2,0.7,0.3);
        col = mix( col, vec3(0.2,0.25,0.35), f );
        col = mix( col, vec3(0.9,0.9,0.9), dot(n,n) );
        col = mix( col, vec3(0.5,0.4,0.1), 0.5*o.y*o.y );
        col = mix( col, vec3(0.6,0.2,0.4), 0.5*smoothstep(1.2,1.3,abs(n.y)+abs(n.x)) );
        col *= f*2.0;

    vec2 ex = vec2( 1.0 / iResolution.x, 0.0 );
    vec2 ey = vec2( 0.0, 1.0 / iResolution.y );

    vec3 nor = normalize( vec3( warps(q+ex) - f, ex.x, warps(q+ey) - f ) );

    vec3 lig = normalize( vec3( 0.9, -0.2, -0.4 ) );
    float dif = clamp( 0.3+0.7*dot( nor, lig ), 0.0, 1.0 );

    vec3 bdrf;
    bdrf  = vec3(0.85,0.90,0.95)*(nor.y*0.5+0.5);
    bdrf += vec3(0.15,0.10,0.55)*dif;
    bdrf  = vec3(0.85,0.90,0.95)*(nor.y*0.5+0.5);
    bdrf += vec3(0.15,0.10,0.05)*dif;

    col *= 2.1*bdrf;
    col = vec3(1.0)-col;
    col = col*col;
    col *= vec3(1.1,1.25,1.2);

    tot += col;
    }

    tot /= 16.0;

    vec2 p = fragCoord / iResolution.xy;
	tot *= 0.5 + 0.5 * sqrt(16.0*p.x*p.y*(1.0-p.x)*(1.0-p.y));

	fragColor = vec4(tot, 1.0);
}

// Rotating Vogel disk with per-frame radius jittering. This sampling pattern
// provides good spatial coverage with only a few sampling points and can be
// used in combination with temporal anti-aliasing for, e.g., SSAO, shadow map
// filtering, DoF, etc. The plot shows the distribution of NUM_SAMPLES over
// NUM_FRAMES.
//
// References:
// - Spreading points on a disc and on a sphere, http://blog.marmakoide.org/?p=1
// - M. Gjoel and M. Svendsen, The rendering of INSIDE, GDC 2016, p. 43
//   https://www.gdcvault.com/play/1023002/Low-Complexity-High-Fidelity-INSIDE.
//
// Author: Johan NysjÃ¶

#define NUM_SAMPLES 6
#define NUM_FRAMES 60
#define PI 3.14159265398

vec3 srgb2lin(vec3 color)
{
	return color * color;    
}

vec3 lin2srgb(vec3 color)
{
 	return sqrt(color);   
}

float hash(float seed)
{
    return fract(sin(seed * 12.9899) * 43758.5453); 
}

vec2 vogel_disk(int i, int num_samples, float r_offset, float phi_offset)
{
	float r = sqrt((float(i) + r_offset) / float(num_samples));
    float golden_angle = 2.399963229728;
    float phi = float(i) * golden_angle + 2.0 * PI * phi_offset;
    float x = r * cos(phi);
    float y = r * sin(phi);

    return vec2(x, y);
}

float aastep(float edge, float x)
{
    float aawidth = 0.7 * fwidth(x);
	return smoothstep(edge - aawidth, edge + aawidth, x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
    vec2 p = (2.0 * uv - 1.0) * vec2(aspect, 1.0);

    vec3 background_color = srgb2lin(vec3(0.8, 0.8, 0.8));
    vec3 glyph_color = srgb2lin(vec3(0.7, 0.1, 0.1));
    float glyph_size = 0.03;

    // NOTE: the seed should be fetched from, e.g., a tiled blue noise texture
    // when the vogel disk is used as a filter kernel for postprocessing effects
    vec2 seed = vec2(0.0, 0.0);

    vec3 output_color = background_color;
    for (int i = 0; i < NUM_FRAMES; ++i) {
        float r_offset = hash(seed.x + float((iFrame + i) % 1000));
        float phi_offset = fract(seed.y + sqrt(3.0) * float((iFrame + i) % 1000));
        float glyph_alpha = float(i + 1) / float(NUM_FRAMES);
        for (int j = 0; j < NUM_SAMPLES; ++j) {
            vec2 q = vogel_disk(j, NUM_SAMPLES, r_offset, phi_offset);
            float dist = length(p - q);
            float alpha = glyph_alpha * (1.0 - aastep(glyph_size, dist));
            output_color = alpha * glyph_color + (1.0 - alpha) * output_color;
        }
    }
	output_color = lin2srgb(output_color);

    fragColor = vec4(output_color, 1.0);
}

#define PI 3.1415926535897932384

void sinFunc3D(vec3 p, float freq, float amp, float phase, out float f, out vec3 df) {
    float m = length(p.xz);
    f = p.y-cos(freq*(m-phase))*amp*min(1.0, 5./m);
    df.y = 1.0;
    if (m == 0.0) {
        df.x = 0.0;
        df.z = 0.0;
    }
    else {
        df.x = p.x/m;
        df.z = p.z/m;
        df.xz *= freq*sin(freq*(m-phase))*amp*min(1.0, 5./m);
    }
}

float map(vec3 p) {
    vec3 p1 = p-vec3(-5.0*PI,0.0,0.0);
    vec3 p2 = p-vec3(5.0*PI,0.0,0.0);
    float f1, f2;
    vec3 df1, df2;
    sinFunc3D(p1, 1.0, 2.0, 10.0*iTime, f1, df1);
    sinFunc3D(p2, 1.0, 2.0, 10.0*iTime, f2, df2);
    return (f1+f2)/length(df1+df2)/1.5;
}

vec3 getNorm(vec3 p) {
    vec2 e = vec2(0.0, 0.001);
    vec3 n;
    n.x = map(p+e.yxx) - map(p-e.yxx);
    n.y = map(p+e.xyx) - map(p-e.xyx);
    n.z = map(p+e.xxy) - map(p-e.xxy);
    return normalize(n);
}

float trace(vec3 ro, vec3 rd) {
    float t = 0.001;
    for (int i=0; i<1000; i++) {
        float h = map(ro+t*rd);
        if (abs(h)<0.0001*t)
            return t;
        t += 1.0*h;
        if (t>1024.0) return -1.0;
    }
}

float softShadow(vec3 ro, vec3 rd) {
    float sha = 1.0;
    float t = 0.01;
    for (int i=0; i<256; i++) {
    	float h = map(ro+t*rd);
        sha = min(sha, 2.0*h/t);
        t += 1.0*clamp(h,0.02,0.20);
        if (t>16.0) break;
    }
    sha = clamp(sha, 0.0, 1.0);
    return sha*sha*(3.0-2.0*sha);
}

mat2 rotMat(float theta) {
    return mat2(cos(theta), sin(theta), -sin(theta), cos(theta));
}

float fresnel(vec3 inc, vec3 nor, float ior) {
    float cosi = clamp(-1., 1., dot(inc, nor));
    float n1, n2;
    if (cosi < 0.0) {
        n1 = 1.;
        n2 = ior;
        cosi = -cosi;
    }
    else {
        n1 = ior;
        n2 = 1.;
    }
    float sint = n1/n2 * sqrt(max(0., 1.-cosi*cosi));
    if (sint>=1.0) {
        return 1.0;
    }
    else {
        float cost = sqrt(max(0., 1.-sint*sint));
        float Rs = ((n1 * cosi) - (n2 * cost)) / ((n1 * cosi) + (n2 * cost));
        float Rp = ((n2 * cosi) - (n1 * cost)) / ((n2 * cosi) + (n1 * cost));
        return (Rs * Rs + Rp * Rp) / 2.;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0.0,20.0,-20.0);
    vec3 rd = normalize(vec3(uv, 1.));
    rd.zy = rotMat(-0.8) * rd.zy;

    vec3 lig = normalize(vec3(0.5, 1.0, -0.5));

    float t = trace(ro,rd);

    vec3 skyCol = vec3(0.9,1.0,1.2);
    float waterDepth = 2.0;
    vec3 col;
    if (t>=0.0) {
    	vec3 pos = ro + t*rd;
    	vec3 nor = getNorm(pos);
        //float lig1 = max(dot(nor,lig),0.0);// * softShadow(pos,lig);
        //float sky = 0.5 + 0.5*nor.y;
        float spec = max(0.0, dot(reflect(rd, nor), lig));
        float refl = fresnel(rd, nor, 1.33);
        float refr = 1.0-refl;
        vec3 refVec = refract(rd, nor, 1.0/1.33);
        vec2 refFloorPos = refVec.xz / refVec.y * (pos.y+waterDepth) + pos.xz;

        //just to make sure floor doesn't clip into water surface. There will be obvious artifacts if it does.
        if (pos.y+waterDepth<0.0) refFloorPos = vec2(0.0);

        spec = pow(spec, 50.0);
        //col = vec3(0.0);
        //col += vec3(0.6,0.6,0.5) * lig1;
        //col += vec3(0.15,0.2,0.3) * sky;
        //col *= vec3(0.2,0.26,0.3);
        col += 1.0*texture(iChannel0, refFloorPos*0.03).xyz*0.9;
        col += vec3(spec);
        col *= refr;
        col += refl*skyCol;
    }
    else {
        col = skyCol;
    }

    col = pow(col,vec3(0.45));

    fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 200
#define MAX_DIST 30.
#define SURF_DIST 0.001

float N31(vec3 p) {
    return fract(cos(p.x*25013.+p.y*3539.+p.z*751.)*39863.);
}

float SmoothNoise3D(vec3 p) {
    vec3 lv = fract(p);
    vec3 id = floor(p);

    lv = lv*lv*(3.-2.*lv);

    float fbl = N31(id+vec3(0,0,0));
    float fbr = N31(id+vec3(1,0,0));
    float fb = mix(fbl, fbr, lv.x);

    float ftl = N31(id+vec3(0,1,0));
    float ftr = N31(id+vec3(1,1,0));
    float ft = mix(ftl, ftr, lv.x);

    float bbl = N31(id+vec3(0,0,1));
    float bbr = N31(id+vec3(1,0,1));
    float bb = mix(bbl, bbr, lv.x);

    float btl = N31(id+vec3(0,1,1));
    float btr = N31(id+vec3(1,1,1));
    float bt = mix(btl, btr, lv.x);

    float f = mix(fb, ft, lv.y);
    float b = mix(bb, bt, lv.y);

    return mix(f,b, lv.z);
}

float SmoothNoise3DDetail(vec3 p) {
    float c = SmoothNoise3D(p*4.);
    c += SmoothNoise3D(p*8.)*.5;
    c += SmoothNoise3D(p*16.)*.25;
    c += SmoothNoise3D(p*32.)*.125;
    c += SmoothNoise3D(p*64.)*.0625;
    c += SmoothNoise3D(p*128.)*.03125;
    return c/(2.-0.03125);
}

float sdSphere(vec3 p, float r) {

  float ns = length(p) - r + SmoothNoise3DDetail(p)*0.175*r;
  float ws = length(p) - r + 0.1 + SmoothNoise3D(p*64.)*.0005 + SmoothNoise3D(p*128.)*.00025;
  return min(ns, ws);
}

vec3 spherePos() {
    return vec3(0,sin(iTime/2.)*.15,0);
}

float GetDist(vec3 p) {
  float t = iTime/15.;
  p.xz = vec2(p.x * cos(t) - p.z * sin(t),
              p.x * sin(t) + p.z * cos(t));
  float d = sdSphere(spherePos()-p, 1.);
  return d;
}

float RayMarch(vec3 ro, vec3 rd) {
  float d = 0.;
  for (int i = 0; i < MAX_STEPS; i++) {
    vec3 p = ro+d*rd;
    float dS = .9*GetDist(p);
    d += dS;
    if (dS < SURF_DIST || d > MAX_DIST) break;
  }

  return d;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    float d = GetDist(p);
    return normalize(d - vec3(
      GetDist(p-e.xyy),
      GetDist(p-e.yxy),
      GetDist(p-e.yyx)));
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/3.0;
        float d = GetDist(pos+h*nor);
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

float shadow(vec3 ro, vec3 rd)
{
    rd = normalize(rd);
    float mint = 0.1;
    float maxt = 200.0;

    float res = 1.0;

    for( float t=mint; t<maxt; )
    {
        float h = GetDist(ro + rd*t);
        if( h<0.001 )
            return 0.0;
        res = min(res, 4.*h/t);
        t += h;
    }
    return res;
}

vec3 GetLight(vec3 rd, vec3 p, vec3 n, vec3 m) {
    vec3 lo = normalize(vec3(10, 10, -8));

    float occ = 0.05 + 0.95*calcAO(p,n);
    float dif = 0.05 + 0.95*smoothstep(-0.2, 0.3, dot(n, lo));
    float sha = 0.05 + 0.95*shadow(p, lo);
          sha *= occ;
    float spe = dot(normalize((lo-p)-2.*dot(lo-p, n)*n), normalize(rd));
    spe = 1.+5.*smoothstep(0.7, 1.0, spe*smoothstep(0.8, 0.81, m.z));

    vec3 col = 1.2*vec3(1.0, 0.9, 0.9)*sha*dif*spe;

    return col;
}

vec3 materialComponents(vec3 p, vec3 n) {
    vec2 e = vec2(0, 1);
    vec3 coreN = normalize(p-spherePos());
    float steepness = dot(coreN,n);
    float height = length(p-spherePos());
    float st = 0.85;
    float sh = 0.925;
    float wh = 0.903;
    vec3 comp = mix(e.yxx, e.xyx, smoothstep(st,st+0.1, steepness));
          comp = mix(comp, e.yxx, smoothstep(sh, sh+0.01, height));
          comp = mix(comp, e.xxy, smoothstep(wh, wh-0.002, height));
    return comp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0, 0, -3);
    vec3 rd = vec3(vec3(uv, 1));

    vec3 col = vec3(0);

    float d = RayMarch(ro, rd);

    if (d < MAX_DIST) {
        vec3 p = ro+d*rd;
        vec3 n = GetNormal(p);
        vec3 m = materialComponents(p,n);

        vec3 l = .95*vec3(0.5, 0.7, 0.7)*GetLight(rd, p, n, m);
        vec3 grass = vec3(0.5, 0.9, 0.2);
        vec3 stone = 1.2*vec3(0.8, 0.3, 0.1);
        vec3 water = vec3(0.2, 0.2, 0.9);

        col = m.x*stone + m.y*grass + m.z*water;

        col *= l;
    } else {
        col = vec3(smoothstep(0.998, 1., N31(vec3(uv, 0.))));
    }

    col = pow(col, vec3(0.4545));

    fragColor = vec4(col, 1.0);
}

// A failed attempt to re-create https://www.reddit.com/r/generative/comments/iv9sue/tangled_cube/

#define time iTime*.1+1.3

// from iq - https://www.shadertoy.com/view/XdXBRH
vec2 hash(in vec2 x)// replace this by something better
{
    const vec2 k=vec2(.3183099,.3678794);
    x=x*k+k.yx;
    return-1.+2.*fract(16.*k*fract(x.x*x.y*(x.x+x.y)));
}

// from iq - https://www.shadertoy.com/view/XdXBRH
vec3 noised(in vec2 p)
{
    vec2 i=floor(p);
    vec2 f=fract(p);

    vec2 u=f*f*f*(f*(f*6.-15.)+10.);
    vec2 du=30.*f*f*(f*(f-2.)+1.);

    vec2 ga=hash(i+vec2(0.,0.));
    vec2 gb=hash(i+vec2(1.,0.));
    vec2 gc=hash(i+vec2(0.,1.));
    vec2 gd=hash(i+vec2(1.,1.));

    float va=dot(ga,f-vec2(0.,0.));
    float vb=dot(gb,f-vec2(1.,0.));
    float vc=dot(gc,f-vec2(0.,1.));
    float vd=dot(gd,f-vec2(1.,1.));

    return vec3(va+u.x*(vb-va)+u.y*(vc-va)+u.x*u.y*(va-vb-vc+vd),// value
    ga+u.x*(gb-ga)+u.y*(gc-ga)+u.x*u.y*(ga-gb-gc+gd)+// derivatives
    du*(u.yx*(va-vb-vc+vd)+vec2(vb,vc)-va));
}
// from iq - https://www.shadertoy.com/view/ll2GD3
vec3 pal(in float t)
{
    vec3 a=vec3(.5,.5,.5),
    b=vec3(.5,.5,.5),
    c=vec3(1.,1.,1.),
    d=vec3(0.,.33,.67);
    return a+b*cos(6.28318*(c*t+d));
}
// from iq - https://www.shadertoy.com/view/3tdSDj
float sdSegment(in vec2 p,in vec2 a,in vec2 b)
{
    vec2 pa=p-a,ba=b-a;
    float h=clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
    return length(pa-ba*h);
}
mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
}
vec2 cube(vec3 scale,vec3 cam,vec3 rot,vec2 uv){
    vec2 c=vec2(0);
    for(float x=0.;x<=1.;x++){
        for(float y=0.;y<=1.;y++){
            for(float z=0.;z<=1.;z++){
                vec3 p3d=vec3(x,y,z);
                p3d-=.5;
                p3d*=scale;
                p3d.xy*=rotate2d(rot.z);
                p3d.yz*=rotate2d(rot.y);
                p3d.xz*=rotate2d(rot.x);
                p3d+=cam;

                p3d.xy/=p3d.z;
                // lines
                float ss = .007;
				c.x += (1.0 - smoothstep(0., 1., sdSegment(uv,p3d.xy,normalize(noised(uv+time).yz)) / ss))
       				* (-sdSegment(uv,p3d.xy,normalize(noised(uv+time).yz))+.2)*2.;
                // dots
                c.y += (smoothstep(1., 0.5, length(uv-p3d.xy) / 1.2 / ss));

            }
        }
    }
    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv=vec2(fragCoord.x/iResolution.x,fragCoord.y/iResolution.y);
    uv-=.5;
    uv/=vec2(iResolution.y/iResolution.x,1);
    uv*=2.;

    vec3 cam=vec3(0,0,2);
    vec3 rot=vec3(time,time*4.5,time*11.);

    vec2 c=vec2(0);
    vec3 scale=vec3(1,.1,1);

    for(int i=0;i<=2;i++){
        scale=vec3(1);
        for(float s=.1;s<=1.;s+=.2){
            scale[i]=s;
            c+=cube(scale,cam,rot,uv);
        }
    }
    vec3 col=vec3(0);
    if(abs(uv.x)<1.){
        col=mix(vec3(1),vec3(0.984,0.933,0.796),clamp(length(uv*.5),0.,1.));
        col=mix(col,pal(c.x/6.),clamp(c.x,0.,1.));
        col=mix(col,vec3(0.),clamp(c.y,0.,1.));
    }
    fragColor=vec4(col,1.);
}

float hash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*43758.5453123);
}

float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

float noiseOctae(vec2 uv, float choppy) {
    uv += noise(uv);        
    vec2 wv = 1.0-abs(sin(uv));
    vec2 swv = abs(cos(uv));    
    wv = mix(wv,swv,wv);
    return pow(1.0-pow(wv.x * wv.y,0.9),choppy);
}

vec3 calcRainbow(vec2 uv, float offset) {
    return 0.5 + 0.5*cos(offset+uv.xyx+vec3(0,2,4));
}

vec3 calcMap(vec2 uv) {
    vec3 color = vec3(0.0);
    const float count = 16.0;
    for(float i = 1.0; i <= count; i += 1.0) {
    	vec3 rainbow = calcRainbow(uv, iTime + i) * noiseOctae(uv * 0.5 + i, 3.5);
        color += rainbow;
    }
    return color * .25;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = calcMap(uv);

    fragColor = vec4(col,1.0);
}

float DrawLine(vec2 pp, float yTarget) {
	float res = smoothstep(yTarget-0.01,yTarget+0.1,pp.y)+smoothstep(yTarget+0.01, yTarget-0.01, pp.y);
    return res;
}   
float DE( vec2 pp, out bool blood, float t )
{
	pp.y += sin(pp.x)/*(
		.4 * sin(.5*2.3*pp.x+pp.y) +
		.2 * sin(.5*5.5*pp.x+pp.y) +
		0.1*sin(.5*13.7*pp.x)+
		0.06*sin(.5*23.*pp.x))*/;

	pp += vec2(0.,0.4)*t;

	float thresh = 5.3;

	blood = pp.y > thresh;

	float d = abs(pp.y - thresh);
