#define ITERS 200
#define pi 3.14159

//from https://www.shadertoy.com/view/XtlGD8
vec2 cartToPol(vec2 z){
	return vec2(length(z),atan(z.y,z.x));
}

vec2 polToCart(vec2 p){
	return vec2(p.r*cos(p.g),p.r*sin(p.g));
}
vec2 powz(vec2 p , float power){
    return vec2(pow(p.r,power),power*mod(p.g,2.*pi));
}
// from https://iquilezles.org/www/articles/palettes/palettes.htm
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}
vec3 mandel(vec2 c) {
    vec2 p = vec2(0.);
    for (int i=0; i<ITERS; i++) {
        p = polToCart(powz(cartToPol(p),sin(-iTime / 10. * pi) + 2.));
        p += c;
        if (length(p) > 1000.) {
            return pal(float(i) * -.06248,vec3(0.5),vec3(0.5),vec3(2.0,1.0,0.0),vec3(0.5,0.20,0.2));
        }
    }
    return vec3(0.);
}
