    Ray ray = GetRay(uv, vec3(0.,0.,-10), vec3(0.,0.,1.), 1.);
    float t = iTime;
    float X = 7.;
    float Y = 7.;

    for(float i = -X; i <= X; i+= .25){
        float v = float(RandomNumber(i))/2. + 2.;
        vec4 Surface = GetPlane(vec3(.5,-.0,-1.), vec3(0.,0.,0.));
        bool isFade = RandomFloat(sin(i)) > 0.5;
        float minY = RandomFloat(cos(i)) * Y ;
        if(RandomFloat(sin(i*i)) > .5) minY*= -1.;
        for(float j = -Y; j < Y; j += .5){
            float y =j-v*t;
            if(y < -Y){
                y = ceil(abs(y + Y)/(Y*2.))*(Y*2.) + y;
            }
            vec3 color;
            float k = t * 2.;
            bool isLight = RandomFloat(tan(i - j) + (k - fract(k))) > 0.95;
            color = isLight ? vec3(0.,1.,0.) : hackGreen ;
            color *= (y - minY)/Y + hackGreen;
            col += SetNumber(ray, RandomNumber(sin(i + j) - cos(i - j)), Surface, vec2(i,y), 1.) * color;
        }
    }

    //col = texture(iChannel0, uv).xyz;
    //vec2 pos = vec2(iMouse.x, iMouse.y)/iResolution.xy;
    //if(length(pos-uv) <.01) col += vec3(1.,0.,0.);
    fragColor = vec4(col,1.0);
}

float distanceBetweenPointAndRay(vec3 point, vec3 ro, vec3 rd){
	return length(cross(point - ro, rd))/length(rd);
}

float drawPoint(vec3 point, vec3 ro, vec3 rd){
     float d = distanceBetweenPointAndRay(point, ro, rd);

    d = smoothstep(0.05, 0.045, d);
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    //Ray Origin
    vec3 ro = vec3(cos(iTime),sin(iTime) * -9., -4.);

    //Look At
    vec3 lookat = vec3(0., 0., 0.);

    //Forward
    vec3 forward = normalize(lookat - ro);

    vec3 Yaxis = vec3(0.,1.,0.);

   	//Right
    vec3 right = cross(Yaxis, forward);

    //Up
    vec3 up = cross(forward, right);

    //Zoom
    float zoom = 1.;

    //Center
    vec3 center = ro + (forward * zoom);

    // i
    vec3 i = center + (uv.x * right) + (uv.y * up);

    //Ray Distance
    vec3 rd = i - ro;

 	float d = 0.;

    d += drawPoint(vec3(-.5,.5,-.5), ro, rd);
    d += drawPoint(vec3(.5,-.5,-.5), ro, rd);
    d += drawPoint(vec3(-.5,-.5,.5), ro, rd);
    d += drawPoint(vec3(-.5,-.5,-.5), ro, rd);
    d += drawPoint(vec3(.5,.5,-0.5), ro, rd);
    d += drawPoint(vec3(.5,-.5,.5), ro, rd);
    d += drawPoint(vec3(-.5,.5,.5), ro, rd);
    d += drawPoint(vec3(.5,.5,.5), ro, rd);

    vec3 col = vec3(d,1.,1.);


    // Output to screen
    fragColor = vec4(col,1.0);
}

#define trajectoryR 0.4
#define xShift 3.0
#define yShift 3.0
#define numBlobs 16.0
#define threshold 9.0

float ballThreshold(vec2 origin, float r, vec2 coord) {
    return r / length(coord - origin);
}

vec3 blob(vec2 uv){

	float total = 0.0;

    vec3 c = vec3(1.0);
    for(float i = 0.0; i < numBlobs; i++) {
        // Lissajous
        //vec2 o = vec2(
        //    cos(iTime + i * xShift * (iTime / 10.0)), 
        //    sin(iTime - i * yShift)
        //) * trajectoryR; 

        // Round
        vec2 o = vec2(
            cos((iTime * i) / 8. + i * xShift), 
            sin((iTime * i) / 8. + i * yShift)
        ) * trajectoryR; 

        float r = i / 57.0;
        total += ballThreshold(o, r, uv);
        c -= vec3(fract(total / 160.0));
    }

    // White on black
    //vec3 color = vec3(1.0) - vec3(step(total, threshold));

    // Black on white (gradient)
    //vec3 color = vec3(step(total, threshold)) + c;

    // Black on white (no gradient)
    vec3 color = vec3(step(total, threshold));

    return color *= vec3(1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    fragColor = vec4(blob(uv),1.0);
}

/* Normal Map Stuff */
/* By: Flyguy */
/* With help from http://stackoverflow.com/q/5281261 */

// fancified a bit by psonice



#define PI 3.141592

 vec2 mouse;

vec3 heightmap(vec2 position)
{
	float row = floor((position.y-15.)/100.);
	if (mod(row, 2.0) < 1.0)
		position.x += 50.;

	float height = 0.0;

	height = sin(position.x*0.0625);
	height = clamp(height,0.0,0.5);
	height += clamp(sin(position.y*0.0625),0.0,1.0);
	height = clamp(height,0.0,0.5);
	height = 1.0 - height;

	height += height > 0.75 ? (max(acos(sin(position.x * .125 - PI * .5)) + asin(cos(position.y * .125 + PI)), 0.25) - 0.75) * .25 : 0.;

	height -= height > 0.0 && height < 0.55 ? (max(sin(position.x * .5 + PI * .5) + cos(position.y * .5 - PI *2.), 1.25) - 2.0) * 0.25 : 0.;

	return vec3(position,height);
}

vec3 n1,n2,n3,n4;
vec2 size = vec2(-0.4,0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mouse.x=0.5+0.25*cos(iTime);
    mouse.y=0.5+0.25*sin(iTime);

	vec2 pos = fragCoord.xy;

	vec3 color = vec3(0.0);

	vec2 off = vec2(pos.x,pos.y-1.0);

	n1 = heightmap(off);

	off = vec2(pos.x-1.0,pos.y);

	n2 = heightmap(off);

	off = vec2(pos.x+1.0,pos.y);

	n3 = heightmap(off);

	off = vec2(pos.x,pos.y+1.0);

	n4 = heightmap(off);

	vec3 va = normalize(vec3(size.xy,n2.z-n3.z));
	vec3 vb = normalize(vec3(size.yx,n1.z-n4.z));

	vec3 p2m = vec3(-((pos/iResolution.xy)-mouse.xy)*iResolution.xy,64.0);	

	vec3 normal = vec3(cross(va,vb));

	color = vec3(dot(normal.xyz, p2m)/96.0);

	color *= vec3(clamp(1.0-distance(mouse.xy*iResolution.xy,pos)/iResolution.x*2.0,0.0,1.0));

	fragColor = vec4( vec3( color ), 1.0 );

}

/* Simplest Possible Raymarcher */
/* By Giraugh */

#define MAX_STEPS 300
#define MAX_DIST 1000.
#define SURF_DIST 0.0005
#define PI 3.14159
#define ATMO_COL_DARK vec3(.05, .05, .3)
#define ATMO_COL_BRIGHT vec3(.6, .9, .95)
#define SEED 370.

vec2 hash( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

float fbm(vec2 uv) {
    uv *= .02;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    float f  = 0.5000*noise( uv ); uv = m*uv;
    f += 0.2500*noise( uv ); uv = m*uv;
    f += 0.1250*noise( uv ); uv = m*uv;
    f += 0.0625*noise( uv ); uv = m*uv;
    f += 0.01125*noise( uv ); uv = m*uv;
    f += 0.005*noise( uv ); uv = m*uv;
    return f;
}

mat2 Rot(float a) {
	float s = sin(a);
	float c = cos(a);
	return mat2(
    	c, -s, s, c
    );
}

// Return the distance to the nearest point in the scene
// from (point)
float GetDist(vec3 point) {
    // Plane SDF at y=0
    float planeD = point.y;

    // Sphere SDF at (0, 1, 4) w/ rad 1
    float amp = .08;
    float freq = 31.;
    float n1 = ((2. * fbm(freq * point.xy + SEED))-1.);
    float n2 = ((2. * fbm(freq * point.zx + SEED))-1.);
    float r = 1. + (n1 + n2) * amp;
    float sphereD = length(point - vec3(0, 0, 0)) - r;
    float oceanD = (length(point - vec3(0, 0, 0)) - .87);

    return min(sphereD, oceanD) / 2.;
}

// March a ray forwards into the scene determined by (GetDist)
// Returns the distance the ray travelled before getting
// below (SURF_DIST) distance from a surface or too far away 
float RayMarch(vec3 rayOrigin, vec3 rayDirection) {
    float d = 0.;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = rayOrigin + rayDirection * d;
        float d_delta = GetDist(p);
        d += d_delta;
        if (d > MAX_DIST || abs(d_delta) < SURF_DIST) break; 
    }
    return d;
}

// Calculate the surface normal at (point)
// can reduce (off) to improve accuracy
vec3 GetNormal(vec3 point) {
    float d = GetDist(point);
    float off = .01;
    vec3 n = vec3(
    	d - GetDist(point - vec3(off,0,0)),
        d - GetDist(point - vec3(0,off,0)),
        d - GetDist(point - vec3(0,0,off))
    );
    return normalize(n);
}

// Determine degree of lighting (0 to 1) at (pos) by (lightPos)
float GetLighting(vec3 point, vec3 lightPos, vec3 n) {
    vec3 l = normalize(lightPos - point);
    float diff = clamp(dot(l, n), 0., 1.);
    return diff;
}

// Get specular highlight
float GetLightingPhong(vec3 point, vec3 view, vec3 lightPos, float shininess) {
	vec3 l = normalize(lightPos - point);
    vec3 n = GetNormal(point);
    vec3 r = 2. * n * dot(n, l) - l;

    if (dot(n, l) < 0.0) {
        return 0.;
    } else {
    	return .3 * pow(max(0., dot(r, normalize(-view))), shininess);
    }
}

// Returns 1. for intersect 0. for no intersect
float RaySphereIntersectLength(vec3 ro, vec3 rd, vec3 so, float r) {
    // calculate nearest point on ray to sphere (p)
    float t = dot(so - ro, rd);
   	vec3 p = ro + t * rd;

    // y is dist from sphere to nearest point
    float y = length(so - p);

    // x is dist from intersection point to p
    float x = sqrt(pow(r, 2.) - pow(y, 2.));
    float w = 2. * x;
    return w;
}

float RaySphereIntersectDist(vec3 ro, vec3 rd, vec3 so, float r) {
    // calculate nearest point on ray to sphere (p)
    float t = dot(so - ro, rd);
   	vec3 p = ro + t * rd;

    // y is dist from sphere to nearest point
    float y = length(so - p);

    // x is dist from intersection point to p
    float x = sqrt(pow(r, 2.) - pow(y, 2.));

    // Get point of intersection
    vec3 i = ro + (t - x) * rd;

    return length(ro - i);
}

vec3 Atmosphere(vec3 ro, vec3 rd, vec3 so, float r, vec3 lightPos, float depth) {    
    // calculate nearest point on ray to sphere (p)
    float t = dot(so - ro, rd);
   	vec3 p = ro + t * rd;

    // y is dist from sphere to nearest point
    float y = length(so - p);

    // x is dist from intersection point to p
    float x = sqrt(pow(r, 2.) - pow(y, 2.));

    // If it doesn't intersect then no atmosphere here
    if (y > r) {
    	return vec3(0);
    }

    // Calculate intersection width
    float t1 = max(t - x, 0.);
    float t2 = min(t + x, depth);
    float w = max(0., t2 - t1) / (r / 2.);

    // Get point of intersection
    vec3 atmoI = ro + (t - x) * rd;

    // Find point to sample light
    // this is some hacks to spread the light out more over the sphere
    vec3 samplePoint = ro + min(t, t2) * rd;
    vec3 normal = normalize(samplePoint - so);
    vec3 lightDir = normalize(lightPos - samplePoint);
    float luminance = max(dot(lightDir, normal), 0.);

    vec3 atmoCol = mix(ATMO_COL_DARK, ATMO_COL_BRIGHT, luminance);

    return vec3(atmoCol * min(2., w * luminance));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (square) (from 0 to 1)
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    // Declare camera position in terms of ray origin and direction
    vec3 rayOrigin = vec3(0, 0, 0);
    vec3 rayDirection = normalize(vec3(uv.x, uv.y, 1));

	// rotate the view
    //rayOrigin.xz *= Rot(iTime);
    float ang = 2. + iTime / 4.;
    float viewDist = 4.;
    rayOrigin.x += cos(ang) * viewDist;
    rayOrigin.z += sin(ang) * viewDist;

    // look at center
    rayDirection.xz *= Rot(ang + PI/2.);

    // RayMarch to find point
    float dist = RayMarch(rayOrigin, rayDirection);
    vec3 hitPoint = rayOrigin + dist * rayDirection;

    // Determine lighting
    vec3 lightPos = vec3(-12, 6, 1);
    vec3 normal = GetNormal(hitPoint);
    float lighting = GetLighting(hitPoint, lightPos, normal);
    if (dist < 50.) lighting += .01;
	vec3 col = vec3(0);

    // Colour Terrain
    if (dist < 100.) {
     	float pd = length(hitPoint);

        // Grass
        col = vec3(.2, .7, .3) * (1. - ((smoothstep(.9, .92, pd)) * .3));

        // Mountains
        if (pd > 0.913) {
            //col = vec3(.2, .8, .3) * .6;
        }

        // Sand
        if (pd <= .887) {
            col = mix(vec3(.7, .8, .5), vec3(.2, .7, .3), smoothstep(.872, .887, pd));
        }

        // Water
        if (pd <= .874) {
        	col = vec3(.1, .15, 1) * mix(.3, .8, (pd - .7) / .05);
            col += 2. * GetLightingPhong(hitPoint, rayDirection, lightPos, 30.);
        }
    }

    col = col * lighting;

    // Atmosphere
    vec3 atmoOrigin = vec3(0, 0, 0);
    float atmoRadius = 1.1;
    vec3 atmosphere = Atmosphere(
        rayOrigin,
        rayDirection,
        atmoOrigin,
        atmoRadius,
        lightPos,
    	dist
    );

    col += atmosphere;


    // Stars
    if (dist > 100.) {
        float noiseX = fbm(rayDirection.xy * 500.);
        float noiseY = fbm(rayDirection.yz * 500.);
        vec2 samp = 25. * rayDirection.xy + vec2(noiseX, noiseY);
        if (mod(samp.x, 2.) < 0.08 && mod(samp.y, 2.) < 0.05) {
            col += vec3(.7) * max(0., sin(iTime + fbm(samp.xy) * 100.));
        }
    }

	// A sun
    if (dist > 100.) {
        float sun = max(0., dot(rayDirection, normalize(lightPos - rayOrigin)) - 0.97);
        col += (pow(sun, .75) * 35.) * vec3(1, .8, .6);
        col += max(0., dot(rayDirection, normalize(lightPos - rayOrigin)) - .8) * vec3(1, .8, .6);
    }

    // Ouput colour at full transparency
    fragColor = vec4(col, 1);
}

