#define PI 3.14

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Plasma is the sum of three sinusoids
    float w1 = sin(10.0*uv.x+iTime);
    float w2 = sin(10.0*(uv.x*sin(iTime/2.0)+uv.y*sin(iTime/3.0))+iTime);
    vec2 c = vec2(uv.x+sin(iTime*0.5), uv.y+cos(iTime*0.33));
    float w3 = sin(sqrt(100.0*(c.x*c.x+c.y*c.y)+1.0)+iTime);

	float w = (w1+w2+w3)/2.0;

    vec3 col = vec3(sin(PI*w), sin(PI*w+2.0*PI/(3.0+sin(iTime))), cos(PI*w+4.0*PI/(4.0+sin(iTime))));
    fragColor = vec4(col*0.5 + 0.5,1.0);
}

float Ei(float x){		//Exponential Integral
    float gamma = 0.57721566490153286060;
    float result = gamma+log(abs(x));

    float tmp = 1.;
    for(float k=1.;k<1000.;k++){
        tmp = tmp*x/k;
        result += tmp/k;
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Constants to vary
    float C = sin(iTime);
    float scale = 5.;

    //Coordinate transformation from pixels
    vec2 uv = scale*(2.*fragCoord-iResolution.xy)/iResolution.y;

    //Implicit function
    float t = uv.x;
    float y = uv.y;
    float f = Ei(log(abs(y+1.))+C)-t*1.-0.;

    //Function plot
    fragColor = vec4(abs(f)/fwidth(f))*vec4(0,1,1,1)+vec4(1,0,0,0);

    //Coordinate system
    if ( min( abs(uv.x), abs(uv.y) ) < 8./iResolution.y ) fragColor *= 0.;
    fragColor *= smoothstep( 0., 1.5, length(uv-vec2(1,0))*iResolution.y/8.- 4.);
    fragColor *= smoothstep( 0., 1.5, length(uv-vec2(0,1))*iResolution.y/8.- 4.);
}

