// Demonstration of the circle inversion on a square
// Blue square: square in real plane
// Blue circle: unitary circle that makes the plane inversion
// Red square: square in the inverted plane

#define TH 0.01
#define COS_PI4 0.70710678118

const mat2 rot45 = mat2(COS_PI4, COS_PI4, -COS_PI4, COS_PI4);

float square(vec2 uv, float s)
{
    uv *= rot45;
    uv.y = abs(uv.y);
    uv *= rot45;
    uv.y = abs(uv.y);
    uv *= rot45;
    uv.y = abs(uv.y);
    uv *= rot45;
    uv = uv.yx;

    uv.y = abs(uv.y);
    uv.y -= s;
    return smoothstep(TH, TH*2., abs(uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 UV = (fragCoord*2. - iResolution.xy)/iResolution.y;

    float v1, v2, c;

    {   // real plane
        vec2 uv = UV;

        uv*=2.;

        v1 = smoothstep(TH, TH*2., abs(length(uv)-1.));

        uv.x +=(sin(iTime)*1.+1.);
        v1 *= square(uv, 0.6);
    }

    {   // inverted plane
        vec2 uv = UV;

        uv*=2.;
        uv /= dot(uv, uv);

        uv.x +=(sin(iTime)*1.+1.);
        v2 = square(uv, 0.6);
    }

    vec3 col = mix(vec3(v1, v1, 1.), vec3(1., v2, v2), .5);

    fragColor = vec4(col,1.0);
}

