        o += smoothstep((20.+i*1.)/iResolution.y,0.,l);
        o *= 1.2 * vec3((0.5+(0.5*rand(vec2(1.,id)))),
                        (0.5+(0.5*rand(vec2(id+i/2.0,id)))),
                        (0.5+(0.5*rand(vec2(id-i,10.5)))));
    }


    c.rgb = (o+pow(o.b,2.0)*.2);
}

/*

	Fiery Spikeball
	---------------

	Making some modifications to Duke's "Cloudy Spikeball" port to produce a fiery version.

	I trimmed the original shader down a bit, changed the weighting slightly, made a couple of 
	sacrifices to the spike shape to tighten up the distance equation, etc.


	Cloudy Spikeball - Duke
	https://www.shadertoy.com/view/MljXDw

    // Port from http://glslsandbox.com/e#1802.0, with some modifications.
    //--------------
    // Posted by Las
    // http://www.pouet.net/topic.php?which=7920&page=29&x=14&y=9
	// By the way, the demo is really good. Definitely worth watching.


*/


mat2 rM; // Rotation matrix.
vec2 a;

// IQ's noise
float pn(in vec3 p){

    vec3 ip = floor(p);
    p = fract(p);
    p *= p*(3.0 - 2.0*p);
    vec2 uv = (ip.xy + vec2(37.0, 17.0)*ip.z) + p.xy;
    uv = texture(iChannel0, (uv+ 0.5)/256.0, -100.0).yx;
    return mix(uv.x, uv.y, p.z);
}

// FBM
float fpn(vec3 p){
    return pn(p*.6125)*0.1*max(1.,cos(iTime)) + pn(p*.125)*0.3*sin(iTime) + pn(p*.25)*.65*sin(iTime);
}

// Spikeball. Using 16 hardcoded points, reflected to give 32 spikes in all.
float spikeball(vec3 p) {

    // Ball
    float d = length(p) - 0.6;
	float ao = (0.5 * (cos(iTime) + 1.));
    //    float o = (0.035 * ao * ao);
    float o = 0.0;
    // Spikes
    p = normalize(p);
    vec4 b = max(max(max(
        abs(vec4(dot(p,vec3(0.526 + o,0.000,0.851)), dot(p,vec3(-0.526,0.000,0.851)),dot(p, vec3(0.851,0.526,0.000)), dot(p,vec3(-0.851,0.526,0.000)))),
        abs(vec4(dot(p,vec3(0.357+o,0.934,0.000)), dot(p,vec3(-0.357+o,0.934,0.000)), dot(p, vec3(0.000,0.851,0.526)), dot(p,vec3(0.000,-0.851,0.526))))),
                     abs(vec4(dot(p,vec3(0.000,0.357 + o,0.934)), dot(p,vec3(0.000,-0.357 - o,0.934)), dot(p, vec3(0.934,0.000,0.357)), dot(p,vec3(-0.934,0.000,0.357))))),
                 abs(vec4(dot(p,vec3(0.577 + o,0.577 + o,0.577 + o)), dot(p,vec3(-0.577,0.577,0.577)), dot(p, vec3(0.577,-0.577,0.577)), dot(p,vec3(0.577,0.577,-0.577)))));
    b.xy = max(b.xy, b.zw);
    b.x = pow(max(b.x, b.y), 64.);


    return d - exp2(b.x*(sin(iTime+1.)*0.25 + 0.75));
}


// Distance function.
float map(vec3 p) {


    //float c = cos(iTime), s = sin(iTime);
    //p.xy=c*p.xy+s*vec2(p.y, -p.x); // Rotate p.xy
    //p.xz=c*p.xz+s*vec2(p.z, -p.x); // Rotate p.xz

    // Performs the same as above. "rM" is produced just once, before the raymarching loop.
    // I think it'd be faster, but GPUs are strange, so who knows.
    // Duke tells me that "r *= rM" can break in some older browsers. Hence, the longhand.
    p.xy = p.xy*rM;
    p.xz = p.xz*rM;

    return spikeball(p) +  fpn(p*50. + iTime*15.)*0.8;
}

// See "Combustible Voronoi"
// https://www.shadertoy.com/view/4tlSzl
vec3 firePalette(float i){

    float T = 1400. + 1000.*i + 200. * (0.5 * (1. + sin(iTime))); // Temperature range (in Kelvin).
    vec3 L = vec3(7.4 * (0.5 * (sin(iTime) + 1.)), 7.6, 7.4* (0.5 * (cos(iTime) + 1.)) ); // Red, green, blue wavelengths (in hundreds of nanometers).
    L = pow(L,vec3(5.0)) * (exp(1.43876719683e5/(T*L))-1.0);
    return 1.0 - exp(-2e8/L); // Exposure level. Set to "50." For "70," change the "5" to a "7," etc.
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){

   // p: position on the ray
   // rd: direction of the ray
   //
   // Trimmed "rd" down. Plus, moved "ro" back 6 units to get rid of the "p.z += 6" line
   // in the distance equation. Also, reversed the Z-coordinates, because it's a personal preference.
   vec3 rd = normalize(vec3((fragCoord.xy - 0.5*iResolution.xy)/iResolution.y, 1.));
   vec3 ro = vec3(0., 0., -8.);

   // ld, td: local, total density 
   // w: weighting factor
   float ld = 0., td = 0., w;

   // t: length of the ray
   // d: distance function
   float d = 1., t = 0.;

   // Distance threshold.
   const float h = .01;

   // total color
   vec3 tc = vec3(0.);


   // Setting up the matrix outside of the loop, which might save a few cycles.
   float cs = cos(iTime*0.4), si = sin(iTime*0.4);
   rM = mat2(cs, si, -si, cs);
    //p.xy=c*p.xy+s*vec2(p.y, -p.x); // Rotate p.xy
    //p.xz=c*p.xz+s*vec2(p.z, -p.x); // Rotate p.xz

   // Tidied the raymarcher up a bit. Plus, got rid some redundancies... I think.

   // rm loop
   for (int i=0; i<64*64*64; i++) {

      // Loop break conditions. Seems to work, but let me know if I've 
      // overlooked something. The middle break isn't really used here, but
      // it can help in certain situations.
      if(td>(1. - 1./200.) || d<0.001*t || t>1200.)break;

      // evaluate distance function
      // Took away the "0.5" factor, and put it below. 
      d = map(ro + t*rd); 

      // check whether we are close enough (step)
      // compute local density and weighting factor 
      //const float h = .1;
      ld = (h - d) * step(d, h);
      w = (1. - td) * ld;   

      // accumulate color and density
      tc += w*w + 1./100.;  // Different weight distribution.
      td += w + 1./200.;


      // enforce minimum stepsize
      d = max(d, 0.04); // Increased the minimum, just a little.

      // step forward
      t += d*0.5;

   }

   // Fire palette.
   tc = firePalette(tc.x);

   // No gamma correction. It was a style choice, but usually, you should have it.   
   fragColor = vec4(tc, 1.0); //vec4(tc.x+td*2., ld*3., 0, tc.x);
}

/*

    Irreptile Triangle Zoom
    -----------------------

    Recursively tiling this hexadrafter irreptile from George Sicherman
    https://userpages.monmouth.com/~colonel/drirrep/index.html

	Forked from https://shadertoy.com/view/tddyWl

*/

// Polygon distance, iq https://www.shadertoy.com/view/wdBXRW

float dot2( in vec2 v ) { return dot(v,v); }
float cross2d( in vec2 v0, in vec2 v1) { return v0.x*v1.y - v0.y*v1.x; }

const int N = 6;

float sdPoly( in vec2[N] v, in vec2 p )
{
    const int num = v.length();
    float d = dot(p-v[0],p-v[0]);
    float s = 1.0;
    for( int i=0, j=num-1; i<num; j=i, i++ )
    {
        // distance
        vec2 e = v[j] - v[i];
        vec2 w =    p - v[i];
        vec2 b = w - e*clamp( dot(w,e)/dot(e,e), 0.0, 1.0 );
        d = min( d, dot(b,b) );

        // winding number from http://geomalgorithms.com/a03-_inclusion.html
        bvec3 cond = bvec3( p.y>=v[i].y, p.y<v[j].y, e.x*w.y>e.y*w.x );
        if( all(cond) || all(not(cond)) ) s*=-1.0;  
    }

    return s*sqrt(d);
}


// Spectrum palette, iq https://www.shadertoy.com/view/ll2GD3

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 spectrum(float n) {
    return 1. - pal( n, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
}



// MATRIX

mat3 trs(vec2 t) {
	return mat3(1, 0, t.x, 0, 1, t.y, 0, 0, 1);
}

mat3 rot(float a) {
	return mat3(cos(a), -sin(a), 0, sin(a), cos(a), 0, 0, 0, 1);
}

mat3 scl(vec2 s) {
	return mat3(s.x, 0, 0, 0, s.y, 0, 0, 0, 1);
}

vec2 mul(vec2 p, mat3 m) {
   return (vec3(p, 1) * m).xy;
}

vec2 mul(mat3 m, vec2 p) {
   return (m * vec3(p, 1)).xy;
}

