	vec2 result = vec2(-1.0);
	float t = MIN_DIST;
	for (int i =0; i < MAX_STEPS && t < MAX_DIST; i++) {
		vec2 h = map(ro + t*rd);
		if (abs(h.x) < (EPSILON*t)) {
			result = vec2(t, h.y);
			break;
		}
		t += h.x;
	}

	return result;
}

// get direction of ray with just device coordinates
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}


// calculate the normal via finite differences
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}


float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (0.8-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=0; i<24; i++ )
    {
		float h = map( ro + rd*t ).x;
        float s = clamp(8.0*h/t,0.0,1.0);
        res = min( res, s*s*(3.0-2.0*s) );
        t += clamp( h, 0.02, 0.2 );
        if( res<0.004 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}




float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

vec3 light = normalize( vec3(-0.5, 0.4, 0.6) );


vec3 render(vec3 ro, vec3 rd, vec3 rdx, vec3 rdy)
{
	// background
	vec3 col = background(rd);

	vec2 tmat = raycast(ro, rd);
	float t = tmat.x;
	float m = tmat.y;
	if (m > -1.) {


		col = 0.2 + 0.2*sin( m*2.0 + vec3(0.0,1.0,2.0) );
        float ks = 1.0;

		vec3 pos = ro + tmat.x*rd;
		vec3  hal = normalize(light - rd );
		vec3 norm = (m < 1.5) ? vec3(0.0,1.0,0.0) : calcNormal( pos );
		vec3 ref = reflect( rd, norm );

		if( m<1.5 )
        {
            // project pixel footprint into the plane
            //vec3 dpdx = ro.y*(rd/rd.y-rdx/rdx.y);
            //vec3 dpdy = ro.y*(rd/rd.y-rdy/rdy.y);

            col = vec3(0.30);
            ks = 0.1;
        }

		float occ = calcAO(pos, norm); 
		vec3 lin = vec3(0.0);

		// sun
		{
            vec3  hal = normalize( light-rd );
            float dif = clamp( dot( norm, light ), 0.0, 1.0 );
        	      dif *= calcSoftshadow( pos, light, 0.02, 2.5 );
			float spe = pow( clamp( dot( norm, hal ), 0.0, 1.0 ),16.0);
                  spe *= dif;
                  spe *= 0.04+0.16*pow(clamp(1.0-dot(hal,light),0.0,1.0),2.0);
            lin += col*2.20*dif*sunColor;
            lin +=     5.00*spe*sunColor*ks;
        }

        // sky
        {
            float dif = sqrt(clamp( 0.5+1.5*norm.y, 0.0, 1.0 ));
            dif *= occ;
            float spe = smoothstep( -0.2, 0.2, ref.y );
            spe *= dif;
            spe *= 0.04+0.96*pow(clamp(1.0+dot(norm,rd),0.0,1.0), 5.0 );
            spe *= calcSoftshadow( pos, ref, 0.02, 4.2 );
            lin += col*0.70*dif*background(rd);
            lin +=     1.30*spe*background(rd)*ks;
        }
        col = lin;

		// falloff (fakes a depth blur)
        col = mix( col, vec3(0.7,0.7,0.9), 1.0-exp( -0.00002*t*t*t ) );
	}

	return vec3( clamp(col,0.0,1.0) );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec3 tot = vec3(0.);
    for (int m =0; m < AA; m++)
    for (int n =0; n < AA; n++)
    {
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord + o);
    	vec3 eye = vec3(-2.0, 3.0, 6.0);

        mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    	vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

  		// ray derivatives
    	vec2 px = (2.0*(fragCoord+vec2(1.0,0.0))-iResolution.xy)/iResolution.y;
    	vec2 py = (2.0*(fragCoord+vec2(0.0,1.0))-iResolution.xy)/iResolution.y;
    	vec3 rdx = (viewToWorld * normalize( vec4(px,2.5, 1.0) )).xyz;
    	vec3 rdy = (viewToWorld * normalize( vec4(py,2.5, 1.0) )).xyz;

    	vec3 col = render(eye, worldDir, rdx, rdy);
        tot += col;

    }

    tot /= float(AA*AA);

	fragColor = vec4(tot, 1.0);
}

vec3 colorX(float x)
{
    vec3 a = vec3(0.5, 0.5, 0.5);    
    vec3 b = vec3(0.5, 0.5, 0.5);    
    vec3 c = vec3(1.0, 1.0, 0.5);    
    vec3 d = vec3(0.00, 0.33, 0.67);    

    float t = x;
    vec3 color = a + b * cos( 2. * 3.1415 * (c * t + d));
    return color;
}

vec3 colorY(float x)
{
    vec3 a = vec3(0.8, 0.5, 0.4 );    
    vec3 b = vec3(0.2, 0.4, 0.2);    
    vec3 c = vec3(2.0, 1.0, 1.0);    
    vec3 d = vec3(0.00, 0.25, 0.25);    

    float t = x;
    vec3 color = a + b * cos( 2. * 3.1415 * (c * t + d));
    return color;
}

vec3 calc(vec2 uv)
{
    //vec2 uv = -1. + 2. * inData.v_texcoord;
    float time = iTime;
    vec3 colorx = colorX(uv.x + time*0.321);
    vec3 colory = colorY(uv.y + time*0.35);
    vec3 color = mix(colorx, colory, length(uv)*2.);
    color = clamp(color, vec3(0.3,0.3,0.2), vec3(0.9,0.5,0.3));
    color *= 1.5 - 0.2*length(uv); // vignetting
    color *= smoothstep(0.3, 0., abs(length(uv)-0.5));
    //fragColor = vec4(color, 1.0);
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;

    // Time varying pixel color
    vec3 col = calc(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(0.330,0.340)),
              dot(st,vec2(0.520,0.730)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    vec2 u = f*f*f*f;

    return mix( mix( dot( random2(i + vec2(.9,0.710) ), f - vec2(-0.440,0.380) ),
                     dot( random2(i + vec2(-0.020,-0.740) ), f - vec2(-0.300,0.210) ), u.x),
                mix( dot( random2(i + vec2(-0.820,-0.990) ), f - vec2(-0.870,-0.910) ),
                     dot( random2(i + vec2(-0.100,-0.350) ), f - vec2(0.350,0.940) ), u.x), u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;

    vec3 color = vec3(0.0);

    float t = 1.0;
    t = cos(iTime/5.)*10.;
    st += noise(st)*t; 
    color = vec3(.4) * smoothstep(.2,.1,noise(st*1000.));
    color += vec3(1.) * smoothstep(1.784,0.376,noise(st));
    fragColor = vec4(1.-color,1.);
}

// some usefull info related to implicit surfaces:
// http://paulbourke.net/geometry/implicitsurf/
// http://paulbourke.net/geometry/polygonise/
// https://developer.nvidia.com/gpugems/gpugems3/part-i-geometry/chapter-1-generating-complex-procedural-terrains-using-gpu
// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm

float sphere(vec3 c, float r, vec3 v)
{
    return length(v - c) - r;
}

float cyl_x(vec3 c, float r, vec3 v)
{
    return length(v.yz - c.yz) - r;
}

float cyl_y(vec3 c, float r, vec3 v)
{
    return length(v.xz - c.xz) - r;
}

mat3 rotate_x(float phi)
{
    return mat3(
        vec3(1.0, 0.0, 0.0),
        vec3(0.0, cos(phi), sin(phi)),
        vec3(0.0,-sin(phi), cos(phi)));
}

mat3 rotate_y(float phi)
{
    return mat3(
        vec3(cos(phi), 0.0, sin(phi)),
        vec3(0.0, 1.0, 0.0),
        vec3(-sin(phi), 0.0, cos(phi)));
}

mat3 rotate_z(float phi)
{
    return mat3(
        vec3(cos(phi), sin(phi), 0.0),
        vec3(-sin(phi), cos(phi), 0.0),
        vec3(0.0, 0.0, 1.0));
}

vec3 transform(vec3 v)
{
    v += vec3(0.0, 0.0,-2.0); // camera-offset
    return rotate_x(iTime) * rotate_y(iTime) * rotate_z(iTime) * v;
}

vec3 tex(vec3 v)
{
    v = transform(v);
    //return fract(v);
    return vec3(step(0.75, length(2.0 * fract(2.0 * v) - 1.0)));
}

// primitives can be combined via min, max, negation
// (https://en.wikipedia.org/wiki/Constructive_solid_geometry)
float scene(vec3 v)
{
    v = transform(v);

    //return sphere(vec3(0.5, 0.0, 2.0), 1.0, v);
    //return min(sphere(vec3(0.5, 0.0, 2.0), 1.0, v), sphere(vec3(-0.5, 0.0, 2.0), 1.0, v));
    //return max(sphere(vec3(0.5, 0.0, 2.0), 1.0, v), sphere(vec3(-0.5, 0.0, 2.0), 1.0, v));
    //return max(sphere(vec3(0.75, 0.0, 2.0), 1.0, v), -sphere(vec3(-0.25, 0.0, 2.0), 1.0, v));
    return max(max(
        min(cyl_x(vec3(0.0, 0.0, 0.0), 0.5, v), cyl_y(vec3(0.0, 0.0, 0.0), 0.5, v)),
        sphere(vec3(0.0, 0.0, 0.0), 1.0, v)), -sphere(vec3(0.0, 0.0, 0.0), 0.75, v));
}

#define EPS 0.001

vec3 gradient(vec3 p)
{
    return vec3(
        scene(p + vec3(+1.0, 0.0, 0.0) * EPS) - scene(p + vec3(-1.0, 0.0, 0.0) * EPS),
        scene(p + vec3(0.0, +1.0, 0.0) * EPS) - scene(p + vec3(0.0, -1.0, 0.0) * EPS),
        scene(p + vec3(0.0, 0.0, +1.0) * EPS) - scene(p + vec3(0.0, 0.0, -1.0) * EPS));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 delta = vec3(2.0 * fragCoord/iResolution.xy - 1.0, 1.0);
    delta.x *= iResolution.x / iResolution.y; // fix aspect

    fragColor = vec4(0.0);
    float s = 0.01;
    int iter = 0;
    int itermax = 280;
    //for(vec3 p = vec3(0.0); iter < itermax; p += delta * scene(p), iter++) // buggy
    for(vec3 p = vec3(0.0); iter < itermax; p += delta * s, iter++)
    {
        float dist = scene(p);
        if(dist <= 0.0)
        {
            //fragColor = vec4(1.0);
            //fragColor = vec4(fract(p.z));
            vec3 n = normalize(gradient(p));
            fragColor = vec4(0.5 + 0.5 * n, 1.0);
            //fragColor = vec4(tex(p), 1.0); // apply 3d texture
            float brightness = dot(n, normalize(vec3(1.0, 0.0, 0.0)));
            //fragColor *= brightness;
            //fragColor = vec4(brightness);
            break;
        }
    }
}

const float pi=3.14159265358;
const float rr=5.;
float surface (vec2 st) {

    return fract(sin(dot(st.xy-vec2(mod(st.x,rr/iResolution.x),mod(st.y,rr/iResolution.y)),
                         vec2(12.9898,78.233)))*
        43758.5453123);
}
float prod(vec2 x){
    return x.x*x.y;
}

float interpsurface(vec2 st) {
    vec2  u=rr/iResolution.xy;

    vec2 x = mod(st,u);
    //if (x.x<u.x/4. && x.y<u.y/4.) return surface(st-x);
    vec2 u00 = st-x;
    vec2 u01 = u00 + u*vec2(0,1);
    vec2 u10 = u00 + u*vec2(1,0);
    vec2 u11 = u00 + u;
    float w00=(prod(abs(u00-st)/u));
    float w01=(prod(abs(u01-st)/u));
    float w10=(prod(abs(u10-st)/u));
    float w11=(prod(abs(u11-st)/u));
	return surface(u11) * w00 //x.x * x.y/u.x/u.y
          +surface(u10) * w01 //x.x/u.x * (1.-x.y/u.y)
          +surface(u01) * w10 //(1.-x.x/u.x)*(x.y/u.y)
          +surface(u00) * w11 ; //(1.-x.x/u.x)*(1.-x.y/u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fg = vec2(fragCoord.x/3.14159*iResolution.x,(fragCoord.y/3.14159*iResolution.y));
    fg.y+=iTime*200.;
	fragColor = vec4(interpsurface((fg)/(iResolution.xy)));
    fragColor.r *= sin(fragCoord.x/3.14159/10.+iTime)*0.5+0.5;
    fragColor.g *= cos(fragCoord.y/3.14159/10.-iTime)*0.5+0.5;
    float d = length(fragCoord - iResolution.xy/2.);
    fragColor.a = cos(d/15.-iTime*2.4)*0.1; //+(1.-d/iResolution.x);
}

