#define PI acos(-1.)
#define TAU 6.283185

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define lrep(p,c,l) p=p-c*clamp(round(p/c),-l,l)
#define moda(p,rep) float a=mod(atan(p.y,p.x),TAU/rep)-(TAU/rep)*0.5;p=vec2(cos(a),sin(a))*length(p) 

#define dt(speed) fract(iTime*speed)
#define switanim(speed) floor(sin(dt(speed)*TAU)+1.)

struct obj
{
    float d;
    vec3 color;
};

obj minobj (obj a, obj b)
{
    if (a.d < b.d) return a;
    else return b;
}

float box (vec3 p, vec3 c)
{return length(max(abs(p)-c,0.));}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r, abs(p.z)-h);}

obj ground (vec3 p)
{
    vec2 id = round(p.xz/.5)-.5;
    lrep(p.xz, 0.5,5.);
    float d = box(p,vec3(.2));

    return obj(d,clamp(vec3(length(id*0.4)*0.8,1.,length(id*0.1)),0.,1.));
}

float line (vec2 uv, vec2 a, vec2 b, float r, bool s)
{
    vec2 pa = uv - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    if (s) return length(pa - ba*h) - r;
    else return length(pa - ba*h) - r*h*0.5; 
}

float face (vec2 uv)
{
    vec2 uu = uv;
    uv.x = abs(uv.x) -0.5;
    uv.y -= 0.3; 
    float m = smoothstep(0.2,0.22,length(uv))+(1.-smoothstep(0.05,0.06, length(uv+vec2(0.,0.1))));

    uv = uu;
    uv.y -= uv.x*uv.x*0.5;
    float b = smoothstep(0.06,0.08,line(uv,vec2(-.5,-0.5),vec2(.5,-0.5),0.3,true));
    m *= b;

    uv.y += 0.5;
    uv.y = abs(uv.y)+0.05;
    uv.x = abs(abs(abs(uv.x)-0.4)-.2)-0.1;
    float d =  smoothstep(0.01,0.011, line(uv,vec2(0.,0.1),vec2(0.,0.42), 0.15,false));
    m += (1.-d);

    return clamp(m,0.,1.);
}

obj flowey (vec3 p)
{
    float sy = 1.;
    float sxz = 1./sy;
    vec3 new_size = vec3(sxz,sy,sxz);
    p *= new_size;  
    p.y -= 0.9;

    vec3 pp = p;

    p.x += sin(p.y*3.)*0.1;
    float sd = cyl(p.xzy, 0.08-p.y*0.05, 0.8);
    obj stem = obj(sd,vec3(0.1,0.8,0.2)); 

    p = pp;
    p.y -= 1.;
    p.x += 0.3;
    p.xy *= rot(PI/4.);
    vec2 puv = p.xz*rot(-PI/2.); 
    float ps = cyl(p.xzy, 0.3,length(p.xz*0.3)*0.2);
    vec3 pcol = (switanim(1.) <= 0.) ? vec3(1.)*face(puv*3.5):vec3(1.);
    obj pistil = obj(ps, pcol);

    moda(p.xz,6.);
    p.x -= 0.49;
    float pes = box(p,vec3(0.2,0.03-p.x*0.25,0.1-sin(p.x*TAU*2.)*0.05));
    obj petal = obj(pes,vec3(0.9,0.7,0.));

    obj flowey = minobj(stem,pistil);
    flowey = minobj(flowey,petal);

    flowey.d /= length(new_size);

    return flowey;
}  

obj SDF (vec3 p)
{
    p.yz *= rot(-atan(1./sqrt(2.)));
    p.xz *= rot(PI/4.);

    obj scene = ground(p);
    scene = minobj(scene, flowey(p));
    return scene;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float detail = sin(dt(1.)*TAU)*70.-75.;
    uv.y = floor(uv.y*detail)/detail;

    vec3 ro = vec3(uv*2.8,-50.), rd = vec3(0.,0.005,1.), p = ro, col = vec3(0.);
    bool hit = false;
    float shad; obj O;
    for (float i=0.; i<64.;i++)
    {
        O = SDF(p);
        if (O.d<0.001)
        {
            hit = true; shad = i/64.; break;
        }
        p += O.d*rd;
    }

    if (hit)
    {
        col = O.color;
        col *= (1.-shad);
    }

    fragColor = vec4(sqrt(col),1.0);
}

#define PI acos(-1.)
#define pmod(p,n) (length(p)*sin(vec2(0,PI*.5)+mod(atan(p.x,p.y),PI*2./n)-PI/n))

mat3 lookat(vec3 eye, vec3 target, vec3 up){
    vec3 w=normalize(target-eye), u=normalize(cross(w,up));
    return mat3(u,cross(u,w),w);
}

void rot(inout vec3 p,vec3 a,float t){
	a=normalize(a);
	vec3 u=cross(a,p),v=cross(u,a);
	p=u*cos(t)+v*sin(t)+a*dot(p,a);   
}

float lpNorm(vec2 p, float n){
	p = pow(abs(p), vec2(n));
	return pow(p.x+p.y, 1./n);
}

float map(vec3 p){
	rot(p,vec3(1,6,-3),iTime*.3);
	rot(p,vec3(5,2,3),iTime*.5);
    p=abs(p);
    if(p.x<p.z)p.xz=p.zx;
	if(p.y<p.z)p.yz=p.zy;
 	if(p.x<p.y)p.xy=p.yx;
 	vec3 nor=normalize(vec3(int(iTime*2.0)%3+3,int(iTime*1.5)%2+2,int(iTime*2.5)%3));
 	float x=dot(p,nor)-.1*fract(sin(iTime+.3*sin(.5*iTime))*2.-1.3);
 	float y=length(cross(p,nor));
 	return min(
        length(vec2(x-clamp(x,.85,2.),y))-.1*mix(1.5,-.5,pow(max(0.,x-.8),2.)),
 		lpNorm(vec2(x-1.,y-.16),5.)-.07
    );
}

vec3 calcNormal(vec3 p){
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p=(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 bg=vec3(1,1.5,1.8)*.7;
    vec3 ro0=vec3(0,0,3.5);
    vec3 ta=vec3(.1,.05,0);
    vec3 rd0=normalize(vec3(p,2.));
    vec3 sv=normalize(cross(ta-ro0,vec3(0,1,0)))*.04;
    float b=6.;  
	for (float j=0.; j<b; j++){
        vec3 ro = ro0+sv;
        vec3 rd = lookat(ro,ta,vec3(0,1,0)) * rd0;
		float z = 0., d, i, ITR=70.;
 		for( i = 0.; i < ITR; i++){
    		z += d = map(ro + rd * z);
    		if(d < .001 || z > 30.) break;
  		}
    	if(d < .001){
	  		vec3 p = ro + rd * z;
	 		vec3 nor = calcNormal(p);
    		vec3 li = normalize(vec3(1));
        	vec3 col = vec3(.7,.9,.8)*.9;
        	col *= pow(1.-i/ITR,2.); 
       	 	col *= clamp(dot(nor,li),.3,.8);
        	col *= max(.5+.5*nor.y,0.);
            float rimd = pow(clamp(1.-dot(reflect(-li,nor),-rd),0.,1.),2.5);
		    float frn = rimd+2.2*(1.0-rimd);
    	    col *= frn*.8;
        	col += pow(clamp(dot(reflect(normalize(p-ro),nor),li),0.,1.),30.);
        	col *= exp(-z*z*0.08);
         	col = min(vec3(1),col*2.);
        	bg += col;
    	}
        rot(sv, normalize(ta-ro0), PI*2./b);
	}
    bg = clamp(bg/b,0.,1.);
    fragColor.xyz = pow(bg,vec3(1.5));
}

