float getCloudNoiseWaves(in vec2 uv, float scale, float speed) 
{
    float waveColor;
    vec2 uvVar1 = uv * scale / 8.;
    vec2 uvVar2 = uv * scale / 0.25;

    float waves1;
    waves1 += cos(uvVar1.x * 30. + sin(iTime * speed + uvVar1.y * 10. + cos(uvVar1.x * 60.0 + iTime * speed)));
    waves1 *= waves1;
    waves1 *= 0.3;

    float waves2;
    waves2 += cos(uvVar2.x / 6. * 5.0 + sin(iTime * speed + uvVar2.y + cos(uvVar2.x + iTime * 2. * speed)));
    waves2 *= waves2 * 0.2;

    waveColor = waves1 + waves2 * 2.;

    return waveColor;
}

float getClouds(in vec2 uv, float cloudOpacity, float cloudHeight, float cloudScale, float cloudAmount) 
{
    cloudAmount -= 1.;
    float fog;
    vec2 st = uv;
    uv.y *= 4.;
    uv.x *= 0.4;
    uv.x += iTime / 1270. / cloudScale;
    uv.y += sin((uv.x + iTime / 100.) * 30.) * 0.02;
    fog += getCloudNoiseWaves(uv * cloudScale, 3.4, 0.1);
    fog -= getCloudNoiseWaves(uv * cloudScale + vec2(2.), 2.4, 0.2);
    fog = clamp(fog, 0., 1.);
    float mask;
    mask = 1. - distance(st.y - (0.35 + cloudHeight), 0.5 + cloudHeight) * 2.;
    fog *= mask;
    fog = smoothstep(0.15 - cloudAmount, 0.6 - cloudAmount, fog);
    return mix(0., clamp(fog, 0., 1.), cloudOpacity);
}

float getWave(in vec2 uv, float waveHeight, float waveSpeed, float waveFrequency, float waveAmplitude, float depth) 
{
    uv.y -= sin(iTime + depth * 12.) / 60. * waveAmplitude;
    waveSpeed = iTime / 4. * waveSpeed;
	float wave = smoothstep(0.0 + waveHeight, 0.003 + waveHeight * (depth + 1.), uv.y + sin((uv.x + waveSpeed) * waveFrequency * 10. + sin(uv.x * 3.)) * 0.03 * waveAmplitude);
    float result = 1. - wave;
    return result;
}

vec4 getOcean(in vec2 uv, float waveHeight, float waveSpeed, float waveFrequency, float waveAmplitude) 
{
    vec3 waves;
    waveHeight -= 1.;
    waveFrequency -= 1.;
    waves += getWave(uv, 0.325 + waveHeight, 0.025 * waveSpeed, 8. + waveFrequency, 0.06 * waveAmplitude, 0.0) * vec3(0.2);
    waves += getWave(uv, 0.3 + waveHeight, 0.05 * waveSpeed, 6. + waveFrequency, 0.1 * waveAmplitude, 0.0005) * vec3(0.2);
    waves += getWave(uv, 0.26 + waveHeight, 0.1 * waveSpeed, 4. + waveFrequency, 0.2 * waveAmplitude, 0.01) * vec3(0.2);
    waves += getWave(uv, 0.20 + waveHeight, 0.2 * waveSpeed, 2. + waveFrequency, 0.4 * waveAmplitude, 0.05) * vec3(0.2);
    waves += getWave(uv, 0.11 + waveHeight, 0.35 * waveSpeed, 1.5 + waveFrequency, 0.77 * waveAmplitude, 0.2) * vec3(0.2);
    waves = pow(waves, vec3(1.)) / 1.6;

    float oceanOpacity = 1. - getWave(uv, 0.325 + waveHeight, 0.025 * waveSpeed, 8. + waveFrequency, 0.06 * waveAmplitude, 0.0);

    return vec4(waves, oceanOpacity);
}

vec3 getSun(in vec2 uv, float oceanOpacity) 
{
    vec2 st = uv;
    uv -= .5;
 	uv.x *= iResolution.x / iResolution.y;

    uv.y += 0.05;

    float radius = 0.1;
    float uvLength = 1. - length(uv);
    vec3 sun;
    float sunOpacity = 1.;

    for(int i = 0; i < 10; i++) 
    {
        sunOpacity *= 0.6;
    	sun += vec3(smoothstep(0.95 - radius, 0.953 - radius, uvLength) * sunOpacity) * vec3(1., 0.6, 0.);
        sun = clamp(sun, 0., 1.);
        radius *= 1.3 + sin(iTime) / 100.;
    }

    sun *= oceanOpacity;
    sun.g *= 0.9;

    vec3 sunGlow = clamp(1. - vec3(length(uv * 2.)), 0., 1.) * oceanOpacity;
    sunGlow *= smoothstep(0.1 - sin(iTime) / 10., 1. - sin(iTime) / 10., sunGlow);

    vec3 flare = 1. - vec3(distance(vec2(st.x * 1.5 - 0.25, st.y * 2. - 0.5), vec2(0.5))) * 2. + sin(iTime) / 40.;
    flare = clamp(flare * vec3(1., 0.8, 0.), 0., 1.);

    return sun + (sunGlow + flare) / 4.;
}

vec3 getSkyGradient(in vec2 uv) 
{
    float mid = smoothstep(0.2, 0.5, uv.y);

    vec3 midColLit = vec3(1., 0.341 * 2., 0.09 * 2.2);
    vec3 midColUnlit = vec3(1., 0.341, 0.09);
    vec3 midCol = mix(midColUnlit, midColLit, 1. - clamp(distance(uv.x, 0.5) * 1.5, 0., 1.));
    vec3 upperCol = vec3(0.5, 0.156, 0.225);

	vec3 skyColorTop = mix(midCol, upperCol, uv.y);
    vec3 skyColorBottom = mix(vec3(1., 0.6, 0.1), vec3(1., 0.7, 0.1), 1. - clamp(distance(uv.x, 0.5) * 3., 0., 1.));

    vec3 skyColor = mix(skyColorBottom, skyColorTop, mid);

    return skyColor;
}

float luma(vec3 color)
{
    return dot(color, vec3(0.2126, 0.7152, 0.0722));
}

vec3 changeLuminance(vec3 colorIn, float lumaOut)
{
    float lumaIn = luma(colorIn);
    return colorIn * (lumaOut / lumaIn);
}

vec3 reinhardExtended(vec3 color, float maxWhiteLuma)
{
    float lumaOld = luma(color);
    float numerator = lumaOld * (1.0 + (lumaOld / (maxWhiteLuma * maxWhiteLuma)));
    float lumaNew = numerator / (1.0 + lumaOld);
    return changeLuminance(color, lumaNew);
}

vec3 tonemapping(in vec3 color)
{
    color -= 0.06;
    color *= 1.62;
    color = reinhardExtended(color, 2.);
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color;

    vec3 waveColor = vec3(0.19, 0.21, 0.45); 
    vec3 waves = getOcean(uv, 1.05, 1.2, 1.1, 1.0).rgb;
    waves *= waveColor;

    vec3 sun = vec3(getSun(uv, getOcean(uv, 1.05, 1.2, 1.1, 1.0).a));

    vec3 skyColor = getSkyGradient(uv);
    vec3 sky = pow(1. - getOcean(uv, 1.05, 1.2, 1.1, 1.0).rgb, vec3(1.5)) * skyColor;

    float clouds = getClouds(uv, 0.5, 0.01, 1., 0.8);
    clouds += getClouds(uv, 0.4, 0.015, 2., 0.75);
    clouds += getClouds(uv, 0.3, 0.02, 2.3, 0.75);

    color = waves + sky + sun + clouds;

    color = tonemapping(color);

    // Output to screen
    fragColor = vec4(color, 1.0);
}

// Configuration.
const bool  bAnimateRelativeLuminancePlane = true;  // Set to true automatically modulate relative luminance input.
const float AnimationAngularFrequency      = 0.3;   // Angular frequency for rl modulation in 1/s.
const float StaticRelativeLuminancePlane   = 0.707; // If bAnimatePlane is not set, use this relative lumianance.
const float BorderFraction                 = 0.379; // Amount of combined top-bottom or left-right fraction.

// Piecewise gamma transfer functions for sRGB colorspace.
vec3 GammaCompress_sRGB( vec3 Color )
{
	vec3 Step = step(vec3(0.0031308),Color);
	return (12.92*Color)*(vec3(1.0)-Step) + (1.055*pow(Color,vec3(0.41666))-vec3(0.055))*Step;
}


const vec3 Rec709RelativeLuminanceWeights = vec3( 0.2126, 0.7152, 0.0722 );

void mainImage( out vec4 FragColor, in vec2 FragCoord )
{
    float RelativeLuminancePlane;

    if ( bAnimateRelativeLuminancePlane )
    {
        // Use the square of the modulation, to make it look a little more brightness uniform over time.
    	RelativeLuminancePlane = pow( 0.5+0.5*cos(AnimationAngularFrequency*iTime), 2.0 );
    }
    else // !bAnimateRelativeLuminancePlane
    {
    	RelativeLuminancePlane = StaticRelativeLuminancePlane;
    }

    vec3 LinearFragColor;

    if ( RelativeLuminancePlane<0.0 )
    {
        LinearFragColor = vec3(0.0,0.0,1.0); // Blue.
    }    
    //else if ( RelativeLuminancePlane<0.001 )
    //{
    //    LinearFragColor = vec3(0.0,0.0,0.0); // Black.
    //}
    else if ( RelativeLuminancePlane>1.0 )
    {
        LinearFragColor = vec3(1.0,0.0,0.0); // Red.
    }
    //else if ( RelativeLuminancePlane>0.999 )
    //{
    //    LinearFragColor = vec3(1.0,1.0,1.0); // White.
    //}
    else // RelativeLuminancePlane in (0,1).
    {
        // Start with coloring everything with border/out of gamut color.
        //LinearFragColor = vec3(RelativeLuminancePlane);

        // Turns out, the above is really annoying, and that black seems to be preferable.
        LinearFragColor = vec3(0.0);

        //vec2 NormalizedFragCoord = 2.0*FragCoord/iResolution.xy-vec2(1.0);
        vec2 NormalizedFragCoord = 2.0*FragCoord/min(iResolution.x,iResolution.y)-vec2(max(iResolution.x/iResolution.y,1.0),max(iResolution.y/iResolution.x,1.0));

        //

        vec2 SquareFragCoords = (NormalizedFragCoord)/(1.0-BorderFraction);

        //SquareFragCoords = NormalizedFragCoord;

        if ( SquareFragCoords.x>=-1.0 && SquareFragCoords.x<=1.0 && SquareFragCoords.y>=-1.0 && SquareFragCoords.y<=1.0 )
        {
            float Red   = (1.0+SquareFragCoords.y)/2.0;
            float Blue  = (1.0+SquareFragCoords.x)/2.0;

            float Green = (RelativeLuminancePlane-Red*Rec709RelativeLuminanceWeights.r-Blue*Rec709RelativeLuminanceWeights.b)/Rec709RelativeLuminanceWeights.g;

            // Color is in gamut and we can just display it, otherwise gray background remains.
            if ( Green>=0.0 && Green<=1.0 )
            {
                LinearFragColor = vec3(Red,Green,Blue);
            }
        }
    }

    // Output to screen
    FragColor.rgb = GammaCompress_sRGB( LinearFragColor );
    FragColor.a   = 1.0;
}

#define PI 3.14159265359
#define TWO_PI 2.*PI
#define FBM_OCTAVES 4
#define DX 1./iResolution.x
#define DY 1./iResolution.y

//1d pseudo-random for shaders
float random(vec2 p) {
    p = mod(p, 10000.);

 	return fract(sin(dot(p, vec2(12.9898,78.233))) * 43758.5453); 
}

//2d pseudo-random
vec2 random2(vec2 p) {
    p = mod(p, 10000.);

 	return fract(
        sin( 
            vec2( 
                dot(p,vec2(127.1,311.7)), 
                dot(p,vec2(269.5,183.3)) 
            ) 
        ) 
    	* 43758.5453
    ); 
}

float noise(vec2 p) {
	vec2 i = floor(p);
    vec2 f = fract(p);

    float bottom_left = random(i);
    float bottom_right = random(i + vec2(1.0, 0.0));
    float top_left = random(i + vec2(0.0, 1.0));
    float top_right = random(i + vec2(1.0, 1.0));

    // Quintic interpolation
    f = f*f*f*(f*(f*6.-15.)+10.);

    // Cubic interpolationn
    //f = f*f*(3.0 - 2.0*f);

    float bottom = mix(bottom_left, bottom_right, f.x); 
    float top = mix(top_left, top_right, f.x); 

    return mix(bottom, top, f.y);
}

float perlinNoise(vec2 p) {
 	vec2 i = floor(p);
    vec2 f = fract(p);

    vec2 a = i;
    vec2 b = i + vec2(1., 0.);
    vec2 c = i + vec2(0., 1.);
    vec2 d = i + vec2(1., 1.);

    //random angles
    float bl_angle = random(a) * TWO_PI;
    float br_angle = random(b) * TWO_PI;
    float tl_angle = random(c) * TWO_PI;
    float tr_angle = random(d) * TWO_PI;

    // corner vectors 
    vec2 bl_vec = vec2(-sin(bl_angle), cos(bl_angle));
    vec2 br_vec = vec2(-sin(br_angle), cos(br_angle));
    vec2 tl_vec = vec2(-sin(tl_angle), cos(tl_angle));
    vec2 tr_vec = vec2(-sin(tr_angle), cos(tr_angle));

    float bl = dot(bl_vec, p - a);
    float br = dot(br_vec, p - b);
    float tl = dot(tl_vec, p - c);
    float tr = dot(tr_vec, p - d);


     // Quintic interpolation
    f = f*f*f*(f*(f*6.-15.)+10.);

    // Cubic interpolationn
    //f = f*f*(3.0 - 2.0*f);

    float bottom = mix(bl, br, f.x); 
    float top = mix(tl, tr, f.x); 

    return clamp(mix(bottom, top, f.y) + 0.5, 0., 1.);  
}

float celularNoise(vec2 p) {
	vec2 i = floor(p);
    vec2 f = fract(p);

    float minDist = 100.;

    for(float x=-1.; x < 2.; x++) {
        for(float y=-1.; y < 2.; y++) {
            vec2 coord = vec2(x, y);
         	vec2 point = random2(i + coord) + coord;

            float dist = distance(f, point);

            minDist = min(minDist, dist); 
        }
    }

    return minDist;
}

float fbm(vec2 p, float shift) {
   float amplitude = 1.0;
   float freq = 4.;
   float normalizeFactor = 0.0;
   float value = 0.0;


   for(int i = 0; i <= FBM_OCTAVES; i++) {
      float angle = float(i) * PI;
      //avoid axis bias
      mat2 rotate = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

      value += perlinNoise(rotate * p * freq + shift) * amplitude;
      normalizeFactor += amplitude;
      amplitude *= 0.5;
      freq *= 2.1;
   }

   return value/normalizeFactor;
}

vec2 fbmGradient(vec2 p, float shift) {
 	float value = fbm(p, shift);

    float valueX = (fbm(vec2(p.x + DX, p.y), shift) - value)/DX;
    float valueY = (fbm(vec2(p.x, p.y + DY), shift) - value)/DY;

    vec2 result = vec2(valueX, valueY);

    return normalize(result);
}

