vec3 blueYonder = vec3(88./255., 105./255., 148./255.);
vec3 laurelGreen = vec3(180./255., 196./255., 174./255.);
vec3 paleSpringBud = vec3(229./255., 232./255., 182./255.);
vec3 romanSilver = vec3(125./255., 134./255., 125./255.);//125, 134, 156  // 125, 1, 1


float hash21(vec2 co) {
    return fract(sin(dot(co.xy ,vec2(13.9098,78.2143))) * 43658.5553);
}

vec4 hash24(vec2 co) {
 	   return vec4(fract(sin(dot(co.yx ,vec2(13.90128,78.2143))) * 43658.5453),
                   fract(sin(dot(co.xy ,vec2(83.97128,68.2146))) * 48.5453),
                   fract(sin(dot(co.yx ,vec2(53.9128,68.3346))) * 46.6453),
                   fract(sin(dot(co.xy ,vec2(59.9158,68.1))) * 86.324));
}

vec2 rotate(vec2 uv, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * uv;
}

float Rect(vec2 uv, vec2 bottomLeft, vec2 topRight) {
    vec2 step1 = step(bottomLeft, uv);
    vec2 step2 = step(1.0-topRight, 1.0-uv);
    return step1.x*step1.y*step2.x*step2.y;
}

vec3 chooseColor(float r) {
    if (r > 0.75) {
     	return blueYonder;
    } else if (r > 0.50) {
        return laurelGreen;
    } else if (r > 0.25) {
        return paleSpringBud;
    } else {
        return romanSilver;
    }
}

vec3 drawTile(vec2 uv, float frot, vec4 colr) {
    uv = rotate(uv, frot*M_PI/2.);

    float rtopleft = Rect(uv, vec2(-0.5, -0.2), vec2(-0.2, 0.5));
    float rbottomleft = Rect(uv, vec2(-0.5, -0.5), vec2(-0.2, -0.2));
    float rtopright = Rect(uv, vec2(-0.2, -0.2), vec2(0.5, 0.5));
    float rbottomright = Rect(uv, vec2(-0.2, -0.5), vec2(0.5, -0.2));

    vec3 col = vec3(0.3);
    vec3 square1 = chooseColor(colr.x);
    vec3 square2 = chooseColor(colr.y);
    vec3 square3 = chooseColor(colr.z);
    vec3 square4 = chooseColor(colr.t);
    col = mix(col, square1, rtopleft);
    col = mix(col, square2, rbottomleft);
    col = mix(col, square3, rtopright);
    col = mix(col, square4, rbottomright);

    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
	uv.x *= iResolution.x/iResolution.y;

    // Move to the right
    uv.x += iTime/32.;

    // Scale for more tiles
    uv *= 30.0;

    // Tiling
    vec2 gv = fract(uv)-0.5;
    vec2 id = floor(uv);

    // Scale grid uv in order to add more chaos
    gv.x /= sin(4.*iTime)+2.0;
    gv.y /= cos(8.*iTime)+2.0;

    // Rotate the tile randomly
    // choose 4 random colors for the rectangles in the tile
    float frot = floor(hash21(id)*10.);//*iTime/5.;
    vec4 colr = hash24(id+3.);

   	// finaly draw the tile.
    vec3 col = drawTile(gv, frot, colr);

    fragColor = vec4(col,1.0);
}

