    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos((iTime+uv.xyx+vec3(0,2,4))*1.0);

    vec3 vb[3] = vec3[3](
    	vec3(-0.5,-0.5,0.0),
        vec3(0.5,-0.5,0.0),
        vec3(0.0,0.5,0.0)
    );

    if(inTri(vec3(xy,0.0), mat3(vb[0], vb[1], vb[2])))
       col = vec3(1,1,1);

    // Output to screen
    fragColor = vec4(col,1.0);
}

