#define MAX_STEPS 100
#define SURFACE_DIST 0.001
#define MAX_DIST 100.0


float t;

mat2 Rot2d(float a) {
  float s = sin(a);
  float c = cos(a);
  return mat2(c, - s, s, c);
}
float differenceSdf(float a, float b){
  return max(a,-b);
}
float sdLorrenz(vec3 p, float sigma, float rho, float beta, float scale){
  float dx = sigma * (p.y-p.x);
  float dy = p.x * (rho - p.z) - p.y;
  float dz = p.x * p.y - beta * p.z;
  return length(vec3(dx*scale, dy*scale, dz*scale));
  //return length(vec3(dx, dy, dz));
}
float GetDist(vec3 p){
  // replace with your scene
  float d = sdLorrenz(p, 10., 28., 8./4.,.004);
  float d2 = sdLorrenz(vec3(p.x-.01, p.y+.01, p.z), 10., 28., 8./4., .005);
  //float d = sdBox(p, vec3(.5,.5,.5));
  //float d2 = sdBox(p, vec3(.2,.2,1.5));
  //return d2;
  return differenceSdf(d*1.4, d2*.4);
}

vec3 GetNormal(vec3 p) {
  vec2 e = vec2(0.02, 0);
  float d = GetDist(p);
  vec3 n = d-vec3(GetDist(p - e.xyy), GetDist(p - e.yxy), GetDist(p - e.yyx));
  return normalize(n);
}
vec3 Bg(vec3 rd){
  float k = rd.y*.5+.5;
  float d = sin(t)*.5+.5;
  float s = cos(t)*.5+.5;
  float dark = .2;
  vec3 col = mix(vec3(d,s,0.),vec3(s,d,1.),k);
  return col*dark;
}
vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
  vec3 f = normalize(l - p),
  r = normalize(cross(vec3(0, 1, 0), f)),
  u = cross(f, r),
  c = p+f * z,
  i = c+uv.x * r+uv.y * u,
  d = normalize(i - p);
  return d;
}

float RayMarch(vec3 ro, vec3 rd) {
  float dO = 0.0;
  for(int i = 0; i < MAX_STEPS; i ++ ) {
    vec3 p = ro + dO * rd;
    float ds = GetDist(p);
    dO += ds;
    if (dO < SURFACE_DIST||dO > MAX_DIST) {
      break;
    }
  }
  return dO;
}
vec3 render(vec2 uv){
  vec3 color = vec3(0);

  // camera

  vec3 camO = vec3(0, 0, -50.);
  camO.yx *= Rot2d(t);
  camO.xz *= Rot2d(t);
  vec3 lookAt = vec3(0, 0, 0);
  vec3 rd = GetRayDir(uv, camO, lookAt, 1.0);
  color += Bg(rd);
  // trace scene
  float d = RayMarch(camO, rd);

  // material
  if (d < MAX_DIST) {
    vec3 p = camO + rd * d;
    vec3 n = GetNormal(p);
    vec3 reflect = reflect(rd, n);
    float height = p.y;
    float dif = dot(n, normalize(vec3(1.,.5,.0)))*.5+.5;
    float spec = pow(reflect.y*.5+.5, 10.);
    color = n*.5+.5;
    color *= pow(1./d,0.3);
    color += mix(Bg(reflect),vec3(dif),.4);
    color += vec3(spec);
  }
  return color;
}
