//Awesome function that uses uses how much a value changes between one 
//pixel and the next to determine how sharp the edge should be
float SDF_HardShape(float shape, float _AntialiasingValue)
{
	float distanceChange = fwidth(shape) * _AntialiasingValue;
	float antialiasedCutoff = smoothstep(distanceChange, -distanceChange, shape);
	antialiasedCutoff;
	return antialiasedCutoff;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Offset the UV so that it sits in the middle of the screen
    uv -= 0.5;
    //Adjust the X value of the UV based on the resolution of the screen
    uv.x *= (iResolution.x / iResolution.y);

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    //Make the first SDF Circle
	float circle1 = Circle(uv, .4);

    //Make an offset that will be used on the second circle
    vec2 offset = vec2(.7 * sin(iTime), 0.);

    //Make the second SDF Circle
    float circle2 = Circle(uv + offset, .15);
    //circle2 = opOnion(circle2, .005);

    //Blend the circles together
    float dist = opSmoothUnion(circle1, circle2, .1);

    //Sample the SDF to generate a hard edge
    float hardEdge = SDF_HardShape(dist, 1.);

    //Color
	vec4 blue = vec4(0.1, 0.8, 1, 1);

    // Output to screen
    fragColor = vec4(blue.xyz,1.0) * hardEdge;
}

