mat3 rotZ(float d)
{
    float s = sin(d);
    float c = cos(d);

    return mat3(c,-s,0,
                s,c,0,
                0,0,1);
}

mat3 rotY(float d)
{
    float s = sin(d);
    float c = cos(d);

    return mat3(c,0,-s,
                0,1,0,
                s,0,c);
}

mat3 rotX(float d)
{
