mat3 cameraSetup(vec3 forward,vec3 up)
{
    vec3 right = normalize(cross(up,forward));
    up = normalize(cross(forward,right));

    return mat3(right,up,forward);
}

float drawSphere(vec3 p,vec3 o,float r)
{
    return length(p-o) - r;
}

float drawCapsule(vec3 p,vec3 a,vec3 b,float r)
{
    vec3 ap = p - a;
    vec3 ab = b - a;

    vec3 c = a + ab * clamp(dot(ap,ab) / dot(ab,ab),0.0,1.0);
    return length(c - p) - r;
}

vec2 opU(vec2 a,vec2 b)
{
    return a.x < b.x ? a : b; 
}

vec2 map(vec3 pos)
{
    vec4 sphere = sphere_info;

    pos = rot(iTime) * pos;

    vec2 d1 = vec2(drawSphere(pos,sphere.xyz,sphere.w),0.1);

    vec3 posA = vec3(0.0,0.9,0.0);
    vec3 posB = vec3(0.0,-0.9,0.0);

    vec2 d2 = vec2(drawCapsule(pos,posA,posB,0.05),1.1);

    return opU(d1,d2);
}

float geodesic(vec3 p1,vec3 p2,vec3 wp)
{
    if(length(p1-p2) < 0.0001) return 0.0;

    //world to object
    vec3 op1 = rot(iTime) * p1;
    vec3 op2 = rot(iTime) * p2;

    vec3 op = vec3(0.0, (dot(op1,op1) - dot(op2,op2)) / (2.0 * (op1.y - op2.y)) ,0.0);

    vec3 p = invRot(iTime) * op;

    vec3 up = normalize(cross(p-p1,p-p2));

    up = length(up) < 0.5 ? invRot(iTime) * vec3(0.0,1.0,0.0) : up;

    vec3 wpd = normalize(wp - p);

    float x = abs(dot(up,wpd));

    vec3 forward = normalize((p1 + p2) * 0.5 - p);

    forward = length(forward) < 0.5 ? invRot(iTime) * vec3(0.0,0.0,-1.0) : forward;

    float y = dot(forward,wpd);
    float y0 = dot(forward,normalize(p1 - p));

    if(x < 0.01 && y > y0) return 1.0;

    return 0.0;
}

float softShadow(vec3 pos,vec3 nor,vec3 dir)
{
    float minValue = 0.0001;
    float maxValue = 10.0;
    float t = 0.0;

    pos = pos + nor * 0.1;

    for(int i = 0; i < 10; ++ i)
    {
        vec2 hit = map(pos + dir * t);
        if(hit.x < minValue) break;

        t += hit.x;
        if(t > maxValue) break;
    }

    return clamp(t / maxValue,0.0,1.0);
}

vec3 calNormal(vec3 p)
{
    float h = 0.000001;
    vec2 k = vec2(1.0,-1.0);

    return normalize(k.xxx * map(p + k.xxx * h).x 
                   + k.xyy * map(p + k.xyy * h).x
                   + k.yxy * map(p + k.yxy * h).x
                   + k.yyx * map(p + k.yyx * h).x);
}

vec2 raymarching(vec3 pos,vec3 dir)
{
    float near = 0.001;
    float minValue = 0.0001;
    float maxValue = 100.0;
    float t = near;
    float material = -1.0;

    for(int i = 0; i < 100; ++ i)
    {
        vec2 hit = map(pos + dir * t);
        material = hit.y;
        if(hit.x < minValue) break;

        t += hit.x;
        if(t > maxValue) break;
    }

    if(t > maxValue) return vec2(-1.0,-1.0);
    return vec2(t,material);
}

vec2 calSphereUV(vec3 pos)
{
    pos = rot(iTime) * pos;

    vec4 sph = sphere_info;

    vec3 dir = normalize(pos - sph.xyz);
    vec2 dir2 = normalize(dir.xz); 

    return acos(vec2(dir2.y,dir.y)) / 3.1415926;
}

float calCapsuleUV(vec3 pos)
{
    pos = rot(iTime) * pos;

    vec4 sph = sphere_info;

    vec3 dir = normalize(pos - sph.xyz);
    vec2 dir2 = normalize(dir.xz); 

    float d = acos(dir2.y) / 3.1415926;

    if(dir2.x < 0.0)
    {
        d = 2.0 - d;
    }

    return d * 0.5;
}

float renderTouchPoint(bool isHit,vec2 hit2,vec3 hitPos,vec3 pos,vec3 dir,out vec3 touchPos)
{
    if(isHit)
    {
        float dist = hit2.x;
        float materialId = hit2.y;
        if(dist >= 0.0 && materialId < 1.0)
        {
            vec3 objPos = pos + dir * dist;

            float t = 1.0 - smoothstep(0.03,0.031,length(objPos - hitPos));

            float t2 = 1.0 - smoothstep(0.0,0.01,abs(length(objPos - hitPos) - 0.06));

            touchPos = objPos;

            return max(t,t2);
        }
    }

    touchPos = vec3(0.0,0.0,0.0);
    return 0.0;
}

vec3 render(vec3 pos,vec3 dir, out vec2 hit)
{
    hit = raymarching(pos,dir);

    float dist = hit.x;
    vec3 skyCol = vec3(0.1,0.5,1.8);

    if(dist < 0.0)
    {
        //not hit anything

        return skyCol;
    }

    float materialId = hit.y;
    vec3 objPos = pos + dir * dist;

    vec3 baseCol = vec3(1.0);
    if(materialId < 1.0)
    {
        baseCol = vec3(1.0,1.0,1.0);

        vec2 uv = calSphereUV(objPos);

        float u = smoothstep(0.07,0.12,abs(fract(uv.x * 10.0) * 2.0 - 1.0));
        float v = smoothstep(0.07,0.12,abs(fract(uv.y * 10.0) * 2.0 - 1.0));

        baseCol *= u;
        baseCol *= v;
    }
    else if(materialId < 2.0)
    {
        float u = calCapsuleUV(objPos);

        if(u < 1.0 / 3.0)
        {
            baseCol = vec3(1.0,0.0,0.0);
        }
        else if(u < 2.0 / 3.0)
        {
            baseCol = vec3(0.0,1.0,0.0);
        }
        else
        {
            baseCol = vec3(0.0,0.0,1.0);
        }
    }


    vec3 litCol = vec3(1.0,1.0,1.0);
    vec3 lit = normalize(vec3(-0.5,1.0,-0.1));

    vec3 nor = calNormal(objPos);

    float shadow = softShadow(objPos,nor,lit);

    float ndl = max(0.0,dot(lit,nor));

    return baseCol * litCol * ndl * shadow + skyCol * baseCol * 0.3;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2  mouseCoord = (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;
    vec2  mouseCoord2 = (vec2(iMouse.z,abs(iMouse.w)) * 2.0 - iResolution.xy) / iResolution.y;


    float z = -10.0;
    vec3  cameraPos = vec3(0.0,0.0,z);
    vec3  mousePos = vec3(mouseCoord.xy,0.0);
    vec3  mousePos2 = vec3(mouseCoord2.xy,0.0);

    vec3  finalColor = vec3(0.0);
    float n = 0.0;

    vec3  cameraViewDir = vec3(0.0,0.0,1.0);
    mat3  viewToWorld = cameraSetup(cameraViewDir,vec3(0.0,1.0,0.0));

    vec3 mouseDir1 = viewToWorld * normalize(mousePos - cameraPos);
    vec3 mouseDir2 = viewToWorld * normalize(mousePos2 - cameraPos);


    vec2 mouseHit1 = raymarching(cameraPos,mouseDir1);
    vec2 mouseHit2 = raymarching(cameraPos,mouseDir2);

    for(int y = -AA; y <= AA; ++ y)
    {
    	for(int x = -AA; x <= AA; ++ x)
        {
            vec2  screenCoord = ((fragCoord + vec2(x,y)*0.25) * 2.0 - iResolution.xy) / iResolution.y;

            vec3  screenPos = vec3(screenCoord.xy,0.0);

            vec3  cameraRay = normalize(screenPos - cameraPos);

            vec3  dir = viewToWorld * cameraRay;

            vec2  hit;

            vec3  sceneColor = render(cameraPos,dir,hit);

            bool  isHit = hit.x >= 0.0 ? true : false;

            vec3  tarPos = isHit ? cameraPos + dir * hit.x : vec3(0.0);

            vec3  touchPos1;
            vec3  touchPos2;

            float alpha = renderTouchPoint(isHit,mouseHit1,tarPos,cameraPos,mouseDir1,touchPos1);
            float alpha2 = renderTouchPoint(isHit,mouseHit2,tarPos,cameraPos,mouseDir2,touchPos2);

            sceneColor = mix(sceneColor,vec3(1.0,0.0,0.0),alpha);
            sceneColor = mix(sceneColor,vec3(1.0,0.0,0.0),alpha2);

            if(mouseHit1.x > 0.0 && mouseHit1.y < 1.0 && mouseHit2.x > 0.0 && mouseHit2.y < 1.0 && iMouse.z > 0.0)
            {
                float alpha3 = geodesic(touchPos1,touchPos2,tarPos);
            	sceneColor = mix(sceneColor,vec3(1.0,0.0,0.0),alpha3);
            }

            //gamma correct
            sceneColor = pow(sceneColor,vec3(0.4545));

            finalColor += sceneColor;
            n += 1.0;
        }
    }

    finalColor = finalColor / n;

    // Output to screen
    fragColor = vec4(finalColor,1.0);
}

